INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-04-08-approval-ratings-drop (Multiple Choice - 17 questions)
('2025-04-08-approval-ratings-drop', 1, 'multiple_choice', 'Government', 'What are presidential approval ratings designed to measure?', 'Congressional performance', 'Public opinion about presidential job performance', 'Economic indicators only', 'Foreign policy success exclusively', 'Public opinion about presidential job performance', 'These polls track how citizens evaluate the president\'s overall performance.', 'Presidential approval ratings measure public opinion about how well the president is performing their job overall. These polls reflect citizen satisfaction with presidential leadership, policy decisions, and response to national challenges, serving as a key indicator of political support.', '["approval ratings", "public opinion", "presidential performance"]', '[{"name": "Gallup - Presidential Job Approval", "url": "https://www.gallup.com/poll/topics/presidential-job-approval.aspx"}, {"name": "Pew Research - Presidential Approval", "url": "https://www.pewresearch.org/politics/"}, {"name": "FiveThirtyEight - Presidential Approval Tracking", "url": "https://projects.fivethirtyeight.com/biden-approval-rating/"}]'),

('2025-04-08-approval-ratings-drop', 2, 'multiple_choice', 'Government', 'What factors typically influence presidential approval ratings?', 'Only foreign policy decisions', 'Economic conditions, major events, and policy responses', 'Congressional legislation exclusively', 'State government actions only', 'Economic conditions, major events, and policy responses', 'Multiple factors affect how citizens evaluate presidential performance.', 'Presidential approval ratings are influenced by various factors including economic conditions, response to national crises, major policy initiatives, foreign policy events, and how effectively the president communicates with the public about national challenges.', '["economic conditions", "crisis response", "policy effectiveness"]', '[{"name": "American Political Science Association - Presidential Studies", "url": "https://www.apsanet.org/"}, {"name": "Brookings Institution - Presidential Approval Analysis", "url": "https://www.brookings.edu/research/politics-and-policy/"}, {"name": "Congressional Research Service - Presidential Approval", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 3, 'multiple_choice', 'Civic Participation', 'How do approval ratings affect democratic accountability?', 'They have no effect on accountability', 'They provide feedback for presidents and influence electoral prospects', 'They only affect media coverage', 'They determine congressional votes', 'They provide feedback for presidents and influence electoral prospects', 'Approval ratings serve as democratic feedback mechanisms.', 'Approval ratings affect democratic accountability by providing continuous feedback to presidents about public satisfaction, influencing their political standing, affecting their ability to advance policy agendas, and potentially impacting future electoral success for themselves and their party.', '["democratic accountability", "electoral feedback", "political consequences"]', '[{"name": "American Democracy Project", "url": "https://www.aascu.org/programs/adp/"}, {"name": "Center for Democracy and Civil Society", "url": "https://www.georgetown.edu/research/democracy-and-governance/"}, {"name": "Congressional Research Service - Democratic Accountability", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 4, 'multiple_choice', 'Government', 'What role do polling organizations play in measuring approval ratings?', 'They have no role in approval ratings', 'They conduct scientific surveys to measure public opinion', 'They only report government statistics', 'They advocate for specific policies', 'They conduct scientific surveys to measure public opinion', 'Professional polling organizations use scientific methods to measure public opinion.', 'Polling organizations play a crucial role by conducting scientific surveys using statistical sampling methods to measure public opinion about presidential performance. These organizations follow professional standards to ensure accurate representation of public sentiment across demographic groups.', '["polling organizations", "scientific surveys", "statistical sampling"]', '[{"name": "American Association for Public Opinion Research", "url": "https://www.aapor.org/"}, {"name": "Pew Research Center - Polling Methods", "url": "https://www.pewresearch.org/methods/"}, {"name": "Gallup - Methodology", "url": "https://www.gallup.com/methodology/"}]'),

('2025-04-08-approval-ratings-drop', 5, 'multiple_choice', 'Media Literacy', 'How should citizens evaluate the reliability of approval rating polls?', 'All polls are equally reliable', 'Consider methodology, sample size, and polling organization reputation', 'Only believe polls that confirm personal views', 'Ignore all polling data completely', 'Consider methodology, sample size, and polling organization reputation', 'Critical evaluation requires understanding polling methodology and quality indicators.', 'Citizens should evaluate poll reliability by examining methodology (random sampling vs. convenience sampling), sample sizes, margins of error, question wording, and the reputation and track record of polling organizations for accuracy and professional standards.', '["poll reliability", "methodology evaluation", "critical thinking"]', '[{"name": "American Association for Public Opinion Research - Standards", "url": "https://www.aapor.org/Standards-Ethics.aspx"}, {"name": "Pew Research - How to Read Polls", "url": "https://www.pewresearch.org/fact-tank/2016/09/08/understanding-the-margin-of-error-in-election-polls/"}, {"name": "FiveThirtyEight - Pollster Ratings", "url": "https://projects.fivethirtyeight.com/pollster-ratings/"}]'),

('2025-04-08-approval-ratings-drop', 6, 'multiple_choice', 'Government', 'How do approval ratings affect presidential political capital?', 'They have no effect on political capital', 'Higher ratings increase ability to advance policy agenda', 'Only Congress determines political capital', 'Ratings only affect international relations', 'Higher ratings increase ability to advance policy agenda', 'Political capital refers to a president\'s ability to influence and persuade.', 'Approval ratings significantly affect presidential political capital by influencing the president\'s ability to persuade Congress, rally public support for policies, and maintain influence with other political actors. Higher ratings provide more leverage for advancing policy agendas.', '["political capital", "policy influence", "congressional relations"]', '[{"name": "Brookings Institution - Presidential Power", "url": "https://www.brookings.edu/research/governance-studies/"}, {"name": "American Political Science Association", "url": "https://www.apsanet.org/"}, {"name": "Congressional Research Service - Presidential Influence", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 7, 'multiple_choice', 'Civic Participation', 'What can cause significant drops in presidential approval ratings?', 'Routine policy announcements', 'Economic recessions, scandals, or crisis mismanagement', 'Regular congressional sessions', 'Diplomatic meetings', 'Economic recessions, scandals, or crisis mismanagement', 'Major negative events typically cause substantial approval rating changes.', 'Significant drops in approval ratings typically result from economic recessions, major scandals, poor crisis management, unpopular wars, or policy failures that affect large numbers of citizens or undermine public trust in presidential leadership.', '["approval rating factors", "crisis management", "economic impact"]', '[{"name": "Gallup - Historical Presidential Approval", "url": "https://www.gallup.com/poll/topics/presidential-job-approval.aspx"}, {"name": "American Presidency Project - Approval Ratings", "url": "https://www.presidency.ucsb.edu/"}, {"name": "Pew Research - Presidential Approval Trends", "url": "https://www.pewresearch.org/politics/"}]'),

('2025-04-08-approval-ratings-drop', 8, 'multiple_choice', 'Government', 'How do approval ratings vary across different demographic groups?', 'All groups always have identical ratings', 'Ratings often differ by party, age, race, education, and region', 'Only party affiliation matters', 'Demographics have no effect on approval', 'Ratings often differ by party, age, race, education, and region', 'Public opinion varies significantly across different population groups.', 'Approval ratings vary considerably across demographic groups including party affiliation, age, race, education level, income, and geographic region. These differences reflect varying policy priorities, life experiences, and political perspectives among different communities.', '["demographic differences", "partisan divides", "group variations"]', '[{"name": "Pew Research - Demographic Analysis", "url": "https://www.pewresearch.org/politics/"}, {"name": "Gallup - Demographics and Politics", "url": "https://www.gallup.com/poll/topics/politics.aspx"}, {"name": "American National Election Studies", "url": "https://electionstudies.org/"}]'),

('2025-04-08-approval-ratings-drop', 9, 'multiple_choice', 'Media Literacy', 'How do media coverage patterns affect public perception of approval ratings?', 'Media has no effect on perception', 'Media framing and emphasis can influence how citizens interpret rating changes', 'Media only reports objective facts', 'Coverage patterns are identical across all outlets', 'Media framing and emphasis can influence how citizens interpret rating changes', 'Media presentation affects public understanding of political information.', 'Media coverage patterns significantly affect public perception by choosing which approval rating changes to emphasize, how to frame the significance of changes, what context to provide, and which expert interpretations to include, influencing citizen understanding.', '["media framing", "public perception", "information interpretation"]', '[{"name": "Pew Research - Media and Politics", "url": "https://www.pewresearch.org/journalism/"}, {"name": "Knight Foundation - Media Trust", "url": "https://knightfoundation.org/"}, {"name": "Poynter Institute - Media Literacy", "url": "https://www.poynter.org/"}]'),

('2025-04-08-approval-ratings-drop', 10, 'multiple_choice', 'Government', 'What historical precedents exist for significant approval rating changes?', 'Approval ratings never change significantly', 'Rally-around-the-flag effects during crises and major event responses', 'Only gradual changes over time', 'Random fluctuations without patterns', 'Rally-around-the-flag effects during crises and major event responses', 'Historical patterns show how major events affect presidential approval.', 'Historical precedents include rally-around-the-flag effects during national crises (like 9/11), significant drops during economic recessions, approval surges during successful military operations, and rating collapses during major scandals like Watergate.', '["historical precedents", "rally effects", "crisis responses"]', '[{"name": "American Presidency Project - Historical Data", "url": "https://www.presidency.ucsb.edu/"}, {"name": "Gallup - Historical Trends", "url": "https://www.gallup.com/poll/topics/presidential-job-approval.aspx"}, {"name": "Congressional Research Service - Presidential History", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 11, 'multiple_choice', 'Civic Participation', 'How do approval ratings relate to voter turnout in elections?', 'No relationship exists', 'Low approval may increase opposition turnout while discouraging supporter turnout', 'High approval always reduces turnout', 'Approval ratings only affect primary elections', 'Low approval may increase opposition turnout while discouraging supporter turnout', 'Approval ratings can influence voter motivation and participation.', 'Approval ratings relate to voter turnout by potentially energizing opposition voters when ratings are low while possibly discouraging supporters, or conversely, motivating supporters when ratings are high while reducing opposition urgency to vote.', '["voter turnout", "electoral motivation", "political mobilization"]', '[{"name": "American Political Science Association - Voting Behavior", "url": "https://www.apsanet.org/"}, {"name": "Center for Election Innovation and Research", "url": "https://electioninnovation.org/"}, {"name": "Congressional Research Service - Voter Behavior", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 12, 'multiple_choice', 'Government', 'What constitutional mechanisms exist for addressing presidential performance concerns?', 'No constitutional mechanisms exist', 'Elections, impeachment, and congressional oversight', 'Only judicial review', 'Automatic removal procedures', 'Elections, impeachment, and congressional oversight', 'The Constitution provides several accountability mechanisms.', 'Constitutional mechanisms for addressing presidential performance include regular elections, impeachment for high crimes and misdemeanors, congressional oversight through investigations and hearings, and the political process of checks and balances between branches.', '["constitutional mechanisms", "impeachment", "elections"]', '[{"name": "Constitution - Impeachment Provisions", "url": "https://constitution.congress.gov/browse/essay/artII-S4-1/"}, {"name": "Congressional Research Service - Presidential Accountability", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}, {"name": "National Archives - Constitutional Processes", "url": "https://www.archives.gov/founding-docs/constitution"}]'),

('2025-04-08-approval-ratings-drop', 13, 'multiple_choice', 'Media Literacy', 'How should citizens interpret margin of error in approval rating polls?', 'Ignore margin of error completely', 'Understand that results could vary within the stated range', 'Margin of error only applies to phone polls', 'Larger margins always mean better accuracy', 'Understand that results could vary within the stated range', 'Margin of error indicates the precision of poll results.', 'Citizens should interpret margin of error as indicating that the true approval rating could be anywhere within the stated range (e.g., 45% Â± 3% means the actual rating could be between 42% and 48%), reflecting the uncertainty inherent in statistical sampling.', '["margin of error", "statistical uncertainty", "poll interpretation"]', '[{"name": "American Association for Public Opinion Research - Margin of Error", "url": "https://www.aapor.org/Education-Resources/For-Researchers/Poll-Survey-FAQ.aspx"}, {"name": "Pew Research - Understanding Polls", "url": "https://www.pewresearch.org/methods/"}, {"name": "Statistical Literacy Guide", "url": "https://www.amstat.org/"}]'),

('2025-04-08-approval-ratings-drop', 14, 'multiple_choice', 'Government', 'How do international events affect domestic approval ratings?', 'International events never affect domestic ratings', 'Foreign policy successes and failures can significantly impact approval', 'Only domestic policy matters for approval', 'International events only affect foreign leaders', 'Foreign policy successes and failures can significantly impact approval', 'Foreign policy performance affects presidential approval ratings.', 'International events significantly affect domestic approval ratings through foreign policy successes or failures, international crises requiring presidential response, diplomatic achievements or setbacks, and how well presidents handle America\'s role in global affairs.', '["foreign policy impact", "international crises", "diplomatic performance"]', '[{"name": "Council on Foreign Relations - Presidential Foreign Policy", "url": "https://www.cfr.org/"}, {"name": "Brookings Institution - Foreign Policy and Approval", "url": "https://www.brookings.edu/research/foreign-policy/"}, {"name": "Congressional Research Service - Foreign Policy Politics", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 15, 'multiple_choice', 'Civic Participation', 'What role do approval ratings play in congressional elections?', 'No role in congressional elections', 'Presidential approval affects down-ballot races and party performance', 'Only affects presidential races', 'Determines congressional leadership automatically', 'Presidential approval affects down-ballot races and party performance', 'Presidential performance influences elections throughout the political system.', 'Approval ratings significantly affect congressional elections by influencing voter attitudes toward the president\'s party, affecting fundraising and candidate recruitment, and serving as a factor in voter decisions about supporting or opposing the president\'s agenda through congressional votes.', '["down-ballot effects", "party performance", "congressional elections"]', '[{"name": "American Political Science Association - Congressional Elections", "url": "https://www.apsanet.org/"}, {"name": "Cook Political Report", "url": "https://www.cookpolitical.com/"}, {"name": "Congressional Research Service - Electoral Politics", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 16, 'multiple_choice', 'Government', 'How do economic indicators correlate with approval ratings?', 'No correlation exists', 'Economic performance strongly influences presidential approval', 'Only foreign policy affects approval', 'Economic data is ignored by voters', 'Economic performance strongly influences presidential approval', 'Economic conditions significantly affect how citizens evaluate presidential performance.', 'Economic indicators strongly correlate with approval ratings because citizens often hold presidents responsible for economic conditions including unemployment, inflation, economic growth, and overall economic security, viewing economic performance as a key measure of presidential effectiveness.', '["economic correlation", "unemployment", "economic responsibility"]', '[{"name": "Bureau of Economic Analysis", "url": "https://www.bea.gov/"}, {"name": "Federal Reserve Economic Data", "url": "https://fred.stlouisfed.org/"}, {"name": "Congressional Research Service - Economic Politics", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 17, 'multiple_choice', 'Media Literacy', 'What questions should citizens ask when evaluating approval rating news coverage?', 'Accept all coverage without question', 'Who conducted the poll, what was the methodology, and what\'s the context?', 'Only consider polls from preferred sources', 'Focus only on the headline number', 'Who conducted the poll, what was the methodology, and what\'s the context?', 'Critical evaluation requires understanding polling details and context.', 'Citizens should ask critical questions including who conducted the poll, what methodology was used, what was the sample size and margin of error, how does this compare to historical trends, and what context explains any changes in approval ratings.', '["critical evaluation", "source credibility", "contextual analysis"]', '[{"name": "News Literacy Project", "url": "https://newslit.org/"}, {"name": "Poynter Institute - Fact Checking", "url": "https://www.poynter.org/"}, {"name": "American Press Institute - News Evaluation", "url": "https://www.americanpressinstitute.org/"}]'),

-- True/False Questions (5 questions)
('2025-04-08-approval-ratings-drop', 18, 'true_false', 'Government', 'Presidential approval ratings directly determine policy outcomes.', '', '', '', '', 'False', 'Approval ratings influence but don\'t directly determine policy outcomes.', 'False. While approval ratings influence presidential political capital and ability to advance agendas, they don\'t directly determine policy outcomes. Policy requires legislative processes, constitutional procedures, and institutional mechanisms beyond public opinion polling.', '["approval influence", "policy process", "political capital"]', '[{"name": "Brookings Institution - Presidential Power", "url": "https://www.brookings.edu/research/governance-studies/"}, {"name": "American Political Science Association", "url": "https://www.apsanet.org/"}, {"name": "Congressional Research Service - Presidential Influence", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 19, 'true_false', 'Media Literacy', 'All polling organizations use identical methodologies for measuring approval ratings.', '', '', '', '', 'False', 'Different organizations use varying methodologies and standards.', 'False. Polling organizations use different methodologies including various sampling techniques, different question wording, different survey modes (phone, online, mail), and different weighting procedures, which can lead to somewhat different results even when measuring the same phenomenon.', '["polling methodology", "methodological differences", "survey techniques"]', '[{"name": "American Association for Public Opinion Research", "url": "https://www.aapor.org/"}, {"name": "Pew Research - Polling Methods", "url": "https://www.pewresearch.org/methods/"}, {"name": "FiveThirtyEight - Polling Analysis", "url": "https://fivethirtyeight.com/"}]'),

('2025-04-08-approval-ratings-drop', 20, 'true_false', 'Civic Participation', 'Approval ratings provide important feedback for democratic accountability.', '', '', '', '', 'True', 'Approval ratings serve as a democratic feedback mechanism.', 'True. Approval ratings provide important democratic feedback by giving citizens a way to express satisfaction or dissatisfaction with presidential performance, influencing political behavior, and serving as a continuous measure of democratic accountability between elections.', '["democratic feedback", "accountability mechanism", "citizen input"]', '[{"name": "American Democracy Project", "url": "https://www.aascu.org/programs/adp/"}, {"name": "Brookings Institution - Democratic Accountability", "url": "https://www.brookings.edu/research/governance-studies/"}, {"name": "Congressional Research Service - Democratic Processes", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 21, 'true_false', 'Government', 'Economic conditions have no relationship to presidential approval ratings.', '', '', '', '', 'False', 'Economic performance strongly correlates with presidential approval.', 'False. Economic conditions have a strong relationship to presidential approval ratings. Citizens often hold presidents responsible for economic performance, and approval ratings typically rise with good economic conditions and fall during recessions or economic difficulties.', '["economic correlation", "presidential responsibility", "economic performance"]', '[{"name": "Federal Reserve Economic Data", "url": "https://fred.stlouisfed.org/"}, {"name": "Gallup - Economic Indicators and Approval", "url": "https://www.gallup.com/poll/topics/economy.aspx"}, {"name": "Congressional Research Service - Economic Politics", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]'),

('2025-04-08-approval-ratings-drop', 22, 'true_false', 'Media Literacy', 'Citizens should consider margin of error when interpreting poll results.', '', '', '', '', 'True', 'Margin of error indicates the precision and uncertainty of poll results.', 'True. Citizens should always consider margin of error when interpreting poll results because it indicates the range of uncertainty in the poll findings and helps determine whether apparent changes in approval ratings are statistically significant or within normal sampling variation.', '["margin of error", "statistical significance", "poll interpretation"]', '[{"name": "American Association for Public Opinion Research", "url": "https://www.aapor.org/"}, {"name": "Pew Research - Understanding Margins of Error", "url": "https://www.pewresearch.org/methods/"}, {"name": "Statistics Education Resources", "url": "https://www.amstat.org/"}]'),

-- Short Answer Questions (3 questions)
('2025-04-08-approval-ratings-drop', 23, 'short_answer', 'Government', 'What do presidential approval ratings measure?', '', '', '', '', 'Public opinion about presidential job performance', 'These polls track citizen satisfaction with how the president is doing their job.', 'Presidential approval ratings measure public opinion about how well the president is performing their job overall. These polls reflect citizen satisfaction with presidential leadership, policy decisions, and response to national challenges.', '["approval ratings", "job performance", "public opinion"]', '[{"name": "Gallup - Presidential Approval", "url": "https://www.gallup.com/poll/topics/presidential-job-approval.aspx"}, {"name": "Pew Research - Presidential Polling", "url": "https://www.pewresearch.org/politics/"}, {"name": "American Presidency Project", "url": "https://www.presidency.ucsb.edu/"}]'),

('2025-04-08-approval-ratings-drop', 24, 'short_answer', 'Media Literacy', 'What should citizens consider when evaluating the reliability of approval rating polls?', '', '', '', '', 'Methodology and polling organization reputation', 'Critical evaluation requires understanding how polls are conducted and by whom.', 'Citizens should consider polling methodology, sample size, margin of error, question wording, and the reputation and track record of the polling organization when evaluating the reliability of approval rating polls.', '["poll reliability", "methodology", "critical evaluation"]', '[{"name": "American Association for Public Opinion Research", "url": "https://www.aapor.org/"}, {"name": "Pew Research - Polling Standards", "url": "https://www.pewresearch.org/methods/"}, {"name": "FiveThirtyEight - Pollster Ratings", "url": "https://projects.fivethirtyeight.com/pollster-ratings/"}]'),

('2025-04-08-approval-ratings-drop', 25, 'short_answer', 'Civic Participation', 'How do approval ratings affect democratic accountability?', '', '', '', '', 'They provide feedback and influence electoral prospects', 'Approval ratings serve as continuous democratic feedback between elections.', 'Approval ratings affect democratic accountability by providing continuous feedback to presidents about public satisfaction, influencing their political standing and ability to advance policy agendas, and potentially impacting future electoral success.', '["democratic accountability", "political feedback", "electoral impact"]', '[{"name": "American Democracy Project", "url": "https://www.aascu.org/programs/adp/"}, {"name": "Brookings Institution - Democratic Accountability", "url": "https://www.brookings.edu/research/governance-studies/"}, {"name": "Congressional Research Service - Democratic Processes", "url": "https://crsreports.congress.gov/product/pdf/R/R44483"}]');