INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-05-02-climate-policy-rollback (Multiple Choice - 17 questions)
('2025-05-02-climate-policy-rollback', 1, 'multiple_choice', 'Environmental Policy', 'What federal agency primarily regulates greenhouse gas emissions?', 'Department of Energy only', 'Environmental Protection Agency', 'Department of Agriculture', 'State environmental agencies', 'Environmental Protection Agency', 'EPA has primary authority for environmental regulation under federal law.', 'The Environmental Protection Agency has primary federal authority for regulating greenhouse gas emissions under the Clean Air Act, setting standards for vehicles, power plants, and industrial facilities to address climate change and air quality concerns.', '["EPA", "greenhouse gas regulation", "Clean Air Act"]', '[{"name": "Environmental Protection Agency", "url": "https://www.epa.gov/"}, {"name": "Clean Air Act", "url": "https://www.epa.gov/clean-air-act-overview"}, {"name": "Congressional Research Service - Climate Regulation", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 2, 'multiple_choice', 'Constitutional Law', 'What constitutional authority supports federal climate regulation?', 'No constitutional authority', 'Commerce Clause and general welfare provisions', 'Only state police powers', 'Second Amendment', 'Commerce Clause and general welfare provisions', 'Federal environmental authority derives from constitutional commerce and welfare powers.', 'Federal climate regulation is supported by constitutional authority including the Commerce Clause (regulating interstate commerce affecting climate), general welfare provisions, and federal authority over activities that have substantial effects on interstate and international environmental concerns.', '["Commerce Clause", "general welfare", "federal environmental authority"]', '[{"name": "Constitution - Commerce Clause", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Environmental Law Cases", "url": "https://supreme.justia.com/cases/federal/environmental-law/"}, {"name": "Congressional Research Service - Constitutional Environmental Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 3, 'multiple_choice', 'Environmental Policy', 'How do climate policy rollbacks affect international climate commitments?', 'No effect on international commitments', 'May undermine U.S. credibility and global climate cooperation', 'Automatically improve international relations', 'Only affect domestic policy', 'May undermine U.S. credibility and global climate cooperation', 'International climate agreements depend on national policy implementation.', 'Climate policy rollbacks can undermine U.S. credibility in international climate commitments, reduce global cooperation on climate action, potentially violate international agreement obligations, and weaken U.S. leadership in global environmental governance.', '["international credibility", "global cooperation", "climate diplomacy"]', '[{"name": "United Nations Framework Convention on Climate Change", "url": "https://unfccc.int/"}, {"name": "Paris Agreement", "url": "https://unfccc.int/process-and-meetings/the-paris-agreement"}, {"name": "Congressional Research Service - International Climate Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 4, 'multiple_choice', 'Constitutional Law', 'How does federalism affect climate policy implementation?', 'Only federal government can act on climate', 'Both federal and state governments have roles, with state innovation important', 'States have exclusive authority', 'No government authority over climate', 'Both federal and state governments have roles, with state innovation important', 'Environmental federalism involves cooperation and sometimes tension between levels.', 'Climate policy federalism involves both federal standards and state innovation, with states often leading on climate action, federal minimum standards providing baselines, and tensions arising when federal and state policies conflict or when federal rollbacks affect state programs.', '["environmental federalism", "state leadership", "multi-level governance"]', '[{"name": "Congressional Research Service - Environmental Federalism", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}, {"name": "Environmental Council of the States", "url": "https://www.ecos.org/"}, {"name": "National Association of Clean Air Agencies", "url": "https://www.4cleanair.org/"}]'),

('2025-05-02-climate-policy-rollback', 5, 'multiple_choice', 'Environmental Policy', 'What economic impacts can result from climate policy rollbacks?', 'Only positive economic effects', 'Mixed effects including short-term industry benefits and long-term climate costs', 'No economic impact', 'Automatic economic improvement', 'Mixed effects including short-term industry benefits and long-term climate costs', 'Climate policy changes have complex economic implications.', 'Climate policy rollbacks create mixed economic impacts including potential short-term benefits for certain industries, reduced compliance costs, but also long-term economic risks from climate change, lost clean energy investment, and potential competitive disadvantages in emerging markets.', '["economic tradeoffs", "industry impacts", "long-term costs"]', '[{"name": "Congressional Budget Office - Climate Economics", "url": "https://www.cbo.gov/"}, {"name": "Bureau of Economic Analysis", "url": "https://www.bea.gov/"}, {"name": "Congressional Research Service - Climate Economics", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 6, 'multiple_choice', 'Government', 'What role does Congress play in climate policy?', 'No congressional role in environmental policy', 'Legislative authority over environmental laws and funding', 'Only oversight functions', 'Complete delegation to executive agencies', 'Legislative authority over environmental laws and funding', 'Congress has primary authority for environmental legislation and appropriations.', 'Congress plays crucial roles in climate policy through legislative authority over environmental laws like the Clean Air Act, appropriations for environmental programs, oversight of agency implementation, and authorization of climate research and international cooperation.', '["legislative authority", "environmental laws", "congressional oversight"]', '[{"name": "House Energy and Commerce Committee", "url": "https://energycommerce.house.gov/"}, {"name": "Senate Environment and Public Works Committee", "url": "https://www.epw.senate.gov/"}, {"name": "Congressional Research Service - Congressional Environmental Role", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 7, 'multiple_choice', 'Environmental Policy', 'How do climate policy rollbacks affect public health?', 'No public health effects', 'Increased air pollution and health risks from reduced environmental protections', 'Only positive health effects', 'Health impacts are purely speculative', 'Increased air pollution and health risks from reduced environmental protections', 'Environmental policies have significant public health implications.', 'Climate policy rollbacks can negatively affect public health by increasing air pollution from reduced emissions standards, higher rates of respiratory disease, heat-related illness from climate change, and reduced protection of vulnerable populations from environmental health hazards.', '["public health", "air pollution", "environmental health"]', '[{"name": "Centers for Disease Control and Prevention - Climate Health", "url": "https://www.cdc.gov/climateandhealth/"}, {"name": "American Public Health Association", "url": "https://www.apha.org/"}, {"name": "Congressional Research Service - Environmental Health", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 8, 'multiple_choice', 'Constitutional Law', 'What due process considerations apply to environmental regulation changes?', 'No due process requirements', 'Notice, comment, and fair procedures for regulatory changes', 'Immediate implementation without process', 'Only judicial approval required', 'Notice, comment, and fair procedures for regulatory changes', 'Administrative law requires due process for regulatory changes.', 'Due process in environmental regulation changes requires notice to affected parties, public comment opportunities, reasoned decision-making, consideration of scientific evidence, and fair procedures that allow input from stakeholders before implementing policy rollbacks.', '["administrative due process", "public participation", "regulatory procedure"]', '[{"name": "Administrative Procedure Act", "url": "https://www.law.cornell.edu/uscode/text/5/part-I/chapter-5"}, {"name": "Federal Register - Rulemaking", "url": "https://www.federalregister.gov/"}, {"name": "Congressional Research Service - Administrative Law", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 9, 'multiple_choice', 'Environmental Policy', 'How do climate policy rollbacks affect renewable energy development?', 'No effect on renewable energy', 'Reduced incentives and support for clean energy development', 'Automatic increase in renewable energy', 'Only positive effects for renewables', 'Reduced incentives and support for clean energy development', 'Climate policies often include renewable energy incentives and support.', 'Climate policy rollbacks can reduce renewable energy development by eliminating tax incentives, reducing research funding, weakening renewable energy standards, and decreasing government support for clean energy innovation and deployment.', '["renewable energy", "clean energy incentives", "energy policy"]', '[{"name": "Department of Energy - Renewable Energy", "url": "https://www.energy.gov/eere"}, {"name": "National Renewable Energy Laboratory", "url": "https://www.nrel.gov/"}, {"name": "Congressional Research Service - Energy Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 10, 'multiple_choice', 'Government', 'What oversight mechanisms exist for environmental policy changes?', 'No oversight of environmental policy', 'Congressional hearings, judicial review, and public accountability', 'Only internal agency oversight', 'Automatic approval processes', 'Congressional hearings, judicial review, and public accountability', 'Environmental policies are subject to multiple oversight mechanisms.', 'Oversight of environmental policy changes includes congressional hearings and investigations, judicial review of regulatory decisions, inspector general reviews, Government Accountability Office studies, and public participation in the rulemaking process.', '["congressional oversight", "judicial review", "public accountability"]', '[{"name": "Government Accountability Office - Environment", "url": "https://www.gao.gov/environmental-protection"}, {"name": "EPA Inspector General", "url": "https://www.epa.gov/office-inspector-general"}, {"name": "Congressional Research Service - Environmental Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 11, 'multiple_choice', 'Environmental Policy', 'How do climate policy rollbacks affect environmental justice?', 'No environmental justice effects', 'Disproportionately harm low-income and minority communities', 'Equal effects on all communities', 'Only benefit disadvantaged communities', 'Disproportionately harm low-income and minority communities', 'Environmental policies often address historical inequities in environmental protection.', 'Climate policy rollbacks can disproportionately harm low-income and minority communities that face higher environmental health risks, have less access to clean air and water, and are more vulnerable to climate change impacts and pollution.', '["environmental justice", "vulnerable communities", "health equity"]', '[{"name": "EPA Office of Environmental Justice", "url": "https://www.epa.gov/environmentaljustice"}, {"name": "Environmental Justice Network", "url": "https://www.ejnet.org/"}, {"name": "Congressional Research Service - Environmental Justice", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 12, 'multiple_choice', 'Constitutional Law', 'What scientific evidence standards apply to environmental regulation?', 'No scientific standards required', 'Best available science and peer review requirements', 'Only political considerations', 'Arbitrary decision-making allowed', 'Best available science and peer review requirements', 'Environmental law typically requires scientific basis for regulatory decisions.', 'Environmental regulation must be based on best available science, peer-reviewed research, transparent methodology, and consideration of scientific uncertainty, ensuring that policy decisions are grounded in credible evidence and rigorous scientific analysis.', '["scientific evidence", "peer review", "evidence-based policy"]', '[{"name": "National Academy of Sciences", "url": "https://www.nationalacademies.org/"}, {"name": "EPA Science Advisory Board", "url": "https://www.epa.gov/sab"}, {"name": "Congressional Research Service - Science and Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 13, 'multiple_choice', 'Environmental Policy', 'How do climate policy rollbacks affect technology innovation?', 'No effect on innovation', 'Reduced incentives for clean technology development and research', 'Automatic increase in innovation', 'Only benefit technology development', 'Reduced incentives for clean technology development and research', 'Climate policies often drive technological innovation through requirements and incentives.', 'Climate policy rollbacks can reduce technology innovation by eliminating research funding, removing regulatory drivers for clean technology, reducing market incentives for innovation, and decreasing government support for emerging environmental technologies.', '["technology innovation", "clean tech", "research funding"]', '[{"name": "Department of Energy - Technology Innovation", "url": "https://www.energy.gov/science-innovation"}, {"name": "National Science Foundation", "url": "https://www.nsf.gov/"}, {"name": "Congressional Research Service - Innovation Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 14, 'multiple_choice', 'Government', 'What role do states play when federal climate policies are rolled back?', 'States become powerless', 'States can maintain or strengthen their own climate policies', 'All state policies are automatically invalidated', 'Only federal policy matters', 'States can maintain or strengthen their own climate policies', 'Federalism allows states to exceed federal minimum standards.', 'When federal climate policies are rolled back, states can maintain or strengthen their own climate policies, form interstate coalitions, continue renewable energy programs, and provide leadership on climate action within their constitutional authority.', '["state leadership", "federalism", "policy innovation"]', '[{"name": "U.S. Climate Alliance", "url": "https://www.usclimatealliance.org/"}, {"name": "National Governors Association", "url": "https://www.nga.org/"}, {"name": "Congressional Research Service - State Climate Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 15, 'multiple_choice', 'Civic Participation', 'How can citizens influence climate policy decisions?', 'Citizens have no role in environmental policy', 'Public comment, advocacy, voting, and civic engagement', 'Only scientists can influence policy', 'Policy is completely insulated from public input', 'Public comment, advocacy, voting, and civic engagement', 'Democratic processes include citizen participation in environmental policy.', 'Citizens can influence climate policy through public comment on regulations, advocacy and organizing, voting for representatives who support climate action, participating in civic engagement, and supporting environmental organizations and initiatives.', '["public participation", "civic engagement", "environmental advocacy"]', '[{"name": "Federal Register - Public Comments", "url": "https://www.federalregister.gov/"}, {"name": "EPA Public Participation", "url": "https://www.epa.gov/international-cooperation/public-participation"}, {"name": "Environmental advocacy organizations", "url": "https://www.nrdc.org/"}]'),

('2025-05-02-climate-policy-rollback', 16, 'multiple_choice', 'Constitutional Law', 'How do property rights interact with environmental regulation?', 'Environmental regulation eliminates all property rights', 'Constitutional balance between regulatory authority and property protection', 'Unlimited property rights trump all regulation', 'No interaction between property and environment', 'Constitutional balance between regulatory authority and property protection', 'The Constitution requires balancing property rights with legitimate government regulation.', 'Property rights and environmental regulation require constitutional balance through the Takings Clause, ensuring that environmental protections serve legitimate public purposes while providing just compensation when regulations constitute a taking of private property.', '["Takings Clause", "property rights", "regulatory balance"]', '[{"name": "Constitution - Fifth Amendment", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Supreme Court - Takings Cases", "url": "https://supreme.justia.com/cases/federal/takings/"}, {"name": "Congressional Research Service - Property and Environment", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 17, 'multiple_choice', 'Environmental Policy', 'What international competitiveness issues arise from climate policy rollbacks?', 'No competitiveness effects', 'May reduce U.S. competitiveness in clean energy markets and green technology', 'Automatic improvement in all competitiveness', 'Only positive economic effects', 'May reduce U.S. competitiveness in clean energy markets and green technology', 'Climate policies can affect economic competitiveness in emerging industries.', 'Climate policy rollbacks may reduce U.S. competitiveness by decreasing investment in clean energy industries, losing leadership in green technology markets, reducing export opportunities in renewable energy, and falling behind other countries in climate innovation.', '["economic competitiveness", "clean energy markets", "green technology"]', '[{"name": "International Energy Agency", "url": "https://www.iea.org/"}, {"name": "Department of Commerce - Trade", "url": "https://www.commerce.gov/"}, {"name": "Congressional Research Service - Trade and Environment", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

-- True/False Questions (5 questions)
('2025-05-02-climate-policy-rollback', 18, 'true_false', 'Constitutional Law', 'The federal government has constitutional authority to regulate greenhouse gas emissions under the Commerce Clause.', '', '', '', '', 'True', 'The Commerce Clause provides federal authority over activities affecting interstate commerce.', 'True. The federal government has constitutional authority to regulate greenhouse gas emissions under the Commerce Clause because climate change and emissions have substantial effects on interstate and international commerce and environmental quality.', '["Commerce Clause", "federal authority", "environmental regulation"]', '[{"name": "Constitution - Commerce Clause", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Environmental Commerce Cases", "url": "https://supreme.justia.com/cases/federal/environmental-law/"}, {"name": "Congressional Research Service - Constitutional Environmental Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 19, 'true_false', 'Environmental Policy', 'States can maintain their own climate policies even when federal policies are rolled back.', '', '', '', '', 'True', 'Federalism allows states to exceed federal minimum environmental standards.', 'True. States can maintain their own climate policies when federal policies are rolled back, as federalism allows states to set standards that exceed federal minimums and pursue environmental leadership within their constitutional authority.', '["state authority", "environmental federalism", "policy leadership"]', '[{"name": "Environmental Federalism", "url": "https://www.ecos.org/"}, {"name": "U.S. Climate Alliance", "url": "https://www.usclimatealliance.org/"}, {"name": "Congressional Research Service - State Environmental Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 20, 'true_false', 'Government', 'Environmental policy changes must follow Administrative Procedure Act requirements for public notice and comment.', '', '', '', '', 'True', 'The APA requires fair procedures for regulatory changes.', 'True. Environmental policy changes must follow Administrative Procedure Act requirements including public notice, comment periods, consideration of input, and reasoned decision-making to ensure fair and transparent regulatory processes.', '["Administrative Procedure Act", "public participation", "regulatory process"]', '[{"name": "Administrative Procedure Act", "url": "https://www.law.cornell.edu/uscode/text/5/part-I/chapter-5"}, {"name": "Federal Register", "url": "https://www.federalregister.gov/"}, {"name": "Congressional Research Service - Administrative Law", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 21, 'true_false', 'Environmental Policy', 'Climate policy rollbacks have no effect on public health outcomes.', '', '', '', '', 'False', 'Environmental policies significantly affect public health through air quality and pollution control.', 'False. Climate policy rollbacks can significantly affect public health outcomes by increasing air pollution, reducing environmental protections, and potentially increasing rates of respiratory disease and other health problems.', '["public health", "environmental health", "air quality"]', '[{"name": "CDC Climate and Health", "url": "https://www.cdc.gov/climateandhealth/"}, {"name": "American Public Health Association", "url": "https://www.apha.org/"}, {"name": "Congressional Research Service - Environmental Health", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 22, 'true_false', 'Constitutional Law', 'Environmental regulations must be based on scientific evidence and peer-reviewed research.', '', '', '', '', 'True', 'Environmental law requires scientific basis for regulatory decisions.', 'True. Environmental regulations must be based on scientific evidence and peer-reviewed research to ensure that policy decisions are grounded in credible evidence, sound methodology, and rigorous scientific analysis.', '["scientific evidence", "peer review", "evidence-based regulation"]', '[{"name": "EPA Science and Technology", "url": "https://www.epa.gov/science-and-technology"}, {"name": "National Academy of Sciences", "url": "https://www.nationalacademies.org/"}, {"name": "Congressional Research Service - Science and Environmental Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

-- Short Answer Questions (3 questions)
('2025-05-02-climate-policy-rollback', 23, 'short_answer', 'Environmental Policy', 'What federal agency has primary authority for regulating greenhouse gas emissions?', '', '', '', '', 'Environmental Protection Agency', 'This agency administers major environmental laws including the Clean Air Act.', 'The Environmental Protection Agency (EPA) has primary federal authority for regulating greenhouse gas emissions under the Clean Air Act and other environmental statutes, setting standards for vehicles, power plants, and industrial sources.', '["EPA", "greenhouse gas regulation", "environmental authority"]', '[{"name": "Environmental Protection Agency", "url": "https://www.epa.gov/"}, {"name": "Clean Air Act", "url": "https://www.epa.gov/clean-air-act-overview"}, {"name": "Congressional Research Service - EPA Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 24, 'short_answer', 'Constitutional Law', 'What constitutional provision primarily supports federal authority to regulate environmental issues?', '', '', '', '', 'Commerce Clause', 'This Article I power covers activities affecting interstate commerce.', 'The Commerce Clause primarily supports federal authority to regulate environmental issues because pollution, climate change, and environmental degradation have substantial effects on interstate and international commerce and environmental quality.', '["Commerce Clause", "federal environmental authority", "constitutional basis"]', '[{"name": "Constitution - Commerce Clause", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Environmental Commerce Cases", "url": "https://supreme.justia.com/cases/federal/environmental-law/"}, {"name": "Congressional Research Service - Constitutional Environmental Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]'),

('2025-05-02-climate-policy-rollback', 25, 'short_answer', 'Government', 'What federal law requires public notice and comment for environmental regulatory changes?', '', '', '', '', 'Administrative Procedure Act', 'This law establishes fair procedures for federal rulemaking.', 'The Administrative Procedure Act requires public notice and comment for environmental regulatory changes, ensuring transparent and fair procedures that allow public participation in the rulemaking process.', '["Administrative Procedure Act", "public participation", "rulemaking procedures"]', '[{"name": "Administrative Procedure Act", "url": "https://www.law.cornell.edu/uscode/text/5/part-I/chapter-5"}, {"name": "Federal Register - Rulemaking", "url": "https://www.federalregister.gov/"}, {"name": "Congressional Research Service - Administrative Procedure", "url": "https://crsreports.congress.gov/product/pdf/R/R46204"}]');