INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-04-23-deportation-expansion (Multiple Choice - 17 questions)
('2025-04-23-deportation-expansion', 1, 'multiple_choice', 'Immigration', 'What constitutional protections apply to immigrants facing deportation?', 'No constitutional protections', 'Due process rights under the Fifth and Fourteenth Amendments', 'Only constitutional protections for citizens', 'State constitutional protections only', 'Due process rights under the Fifth and Fourteenth Amendments', 'Constitutional due process protections extend beyond citizenship.', 'Immigrants facing deportation have constitutional protections under the Due Process Clauses of the Fifth and Fourteenth Amendments, including rights to notice, hearing, legal representation, and protection against arbitrary government action, regardless of their immigration status.', '["due process rights", "constitutional protection", "immigrant rights"]', '[{"name": "Supreme Court - Zadvydas v. Davis", "url": "https://supreme.justia.com/cases/federal/us/533/678/"}, {"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Congressional Research Service - Immigration Due Process", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 2, 'multiple_choice', 'Immigration', 'What federal agency primarily handles deportation proceedings?', 'Federal Bureau of Investigation', 'Immigration and Customs Enforcement and Immigration Courts', 'Department of Homeland Security only', 'State immigration departments', 'Immigration and Customs Enforcement and Immigration Courts', 'ICE enforces immigration law while immigration courts adjudicate cases.', 'Immigration and Customs Enforcement (ICE) primarily handles deportation enforcement, while Immigration Courts under the Department of Justice adjudicate removal proceedings. These agencies work together to implement federal immigration law and deportation policies.', '["ICE", "Immigration Courts", "deportation enforcement"]', '[{"name": "Immigration and Customs Enforcement", "url": "https://www.ice.gov/"}, {"name": "Executive Office for Immigration Review", "url": "https://www.justice.gov/eoir"}, {"name": "Congressional Research Service - Immigration Enforcement", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 3, 'multiple_choice', 'Constitutional Law', 'How does federalism affect immigration enforcement?', 'States control all immigration policy', 'Federal government has primary authority, but states can affect implementation', 'No federal role in immigration', 'Complete state sovereignty over immigration', 'Federal government has primary authority, but states can affect implementation', 'Immigration is primarily federal, but state cooperation affects enforcement.', 'Federalism affects immigration enforcement because the federal government has primary constitutional authority over immigration, but state and local cooperation significantly affects implementation of deportation policies, creating tension between federal mandates and state choices.', '["federalism", "federal supremacy", "state cooperation"]', '[{"name": "Supreme Court - Arizona v. United States", "url": "https://supreme.justia.com/cases/federal/us/567/387/"}, {"name": "Congressional Research Service - Immigration Federalism", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}, {"name": "Constitution - Federal Immigration Power", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}]'),

('2025-04-23-deportation-expansion', 4, 'multiple_choice', 'Immigration', 'What legal processes must occur before deportation?', 'Immediate deportation without process', 'Notice of proceedings, hearing, and opportunity for legal representation', 'Only administrative review', 'Automatic deportation for violations', 'Notice of proceedings, hearing, and opportunity for legal representation', 'Due process requires procedural protections before deportation.', 'Legal processes required before deportation include notice of removal proceedings, immigration court hearings, opportunity for legal representation, rights to present evidence and witnesses, and appeals processes, ensuring due process protections for individuals facing removal.', '["removal proceedings", "immigration hearings", "legal representation"]', '[{"name": "Immigration Court Procedures", "url": "https://www.justice.gov/eoir/immigration-court-practice-manual"}, {"name": "American Immigration Lawyers Association", "url": "https://www.aila.org/"}, {"name": "Congressional Research Service - Immigration Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 5, 'multiple_choice', 'Constitutional Law', 'What constitutional provision gives Congress authority over immigration?', 'Second Amendment', 'Commerce Clause and naturalization power', 'Tenth Amendment', 'First Amendment', 'Commerce Clause and naturalization power', 'Article I grants Congress power over naturalization and interstate commerce.', 'Congress derives immigration authority from the Constitution\'s Commerce Clause and the power to establish uniform rules of naturalization in Article I, Section 8, giving the federal government primary responsibility for immigration law and policy.', '["Commerce Clause", "naturalization power", "congressional authority"]', '[{"name": "Constitution - Article I Powers", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Immigration Powers Cases", "url": "https://supreme.justia.com/cases/federal/immigration/"}, {"name": "Congressional Research Service - Immigration Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 6, 'multiple_choice', 'Immigration', 'What rights do immigrants have during deportation proceedings?', 'No rights during deportation', 'Right to legal counsel, interpreter services, and appeals', 'Only the right to remain silent', 'Rights determined by immigration status', 'Right to legal counsel, interpreter services, and appeals', 'Procedural rights ensure fair treatment in immigration proceedings.', 'Immigrants have important rights during deportation proceedings including the right to legal counsel (at their own expense), interpreter services, the right to examine evidence, present witnesses, and appeal negative decisions to higher courts and administrative bodies.', '["legal counsel", "interpreter services", "appeal rights"]', '[{"name": "Executive Office for Immigration Review - Rights", "url": "https://www.justice.gov/eoir/list-pro-bono-legal-service-providers"}, {"name": "American Bar Association - Immigration Rights", "url": "https://www.americanbar.org/groups/public_interest/immigration/"}, {"name": "Congressional Research Service - Immigration Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 7, 'multiple_choice', 'Immigration', 'How do deportation policies affect family separation?', 'No effect on families', 'Can separate family members with different immigration statuses', 'Always keep families together', 'Only affect single individuals', 'Can separate family members with different immigration statuses', 'Mixed-status families face particular challenges in deportation enforcement.', 'Deportation policies can result in family separation when family members have different immigration statuses, including separation of parents from U.S. citizen children, creating humanitarian concerns and affecting mixed-status families throughout communities.', '["family separation", "mixed-status families", "humanitarian impact"]', '[{"name": "Department of Homeland Security - Family Case Management", "url": "https://www.dhs.gov/"}, {"name": "American Civil Liberties Union - Family Separation", "url": "https://www.aclu.org/issues/immigrants-rights"}, {"name": "Congressional Research Service - Family Immigration", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 8, 'multiple_choice', 'Constitutional Law', 'What Equal Protection concerns arise from deportation enforcement?', 'No Equal Protection issues', 'Discriminatory enforcement based on race, ethnicity, or national origin', 'Equal Protection only applies to citizens', 'No constitutional protections in immigration', 'Discriminatory enforcement based on race, ethnicity, or national origin', 'Equal Protection prevents discriminatory law enforcement practices.', 'Equal Protection concerns in deportation enforcement include discriminatory targeting based on race, ethnicity, or national origin, requiring that immigration enforcement be applied fairly and not selectively target individuals based on protected characteristics rather than legal violations.', '["Equal Protection", "discriminatory enforcement", "racial profiling"]', '[{"name": "Supreme Court - Equal Protection Cases", "url": "https://supreme.justia.com/cases/federal/equal-protection/"}, {"name": "American Civil Liberties Union - Immigration Rights", "url": "https://www.aclu.org/issues/immigrants-rights"}, {"name": "Congressional Research Service - Immigration Equal Protection", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 9, 'multiple_choice', 'Immigration', 'What role do sanctuary cities play in deportation policy?', 'Sanctuary cities control federal immigration law', 'They limit local cooperation with federal immigration enforcement', 'They have no effect on deportation', 'They automatically prevent all deportations', 'They limit local cooperation with federal immigration enforcement', 'Local policies can affect federal immigration enforcement effectiveness.', 'Sanctuary cities limit local cooperation with federal immigration enforcement by restricting information sharing, limiting detention holds, and refusing to assist in immigration raids, creating tension between federal deportation policies and local community policing priorities.', '["sanctuary cities", "local cooperation", "federal-local tension"]', '[{"name": "International City/County Management Association", "url": "https://icma.org/"}, {"name": "National League of Cities", "url": "https://www.nlc.org/"}, {"name": "Congressional Research Service - Sanctuary Policies", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 10, 'multiple_choice', 'Immigration', 'What international obligations affect U.S. deportation practices?', 'No international obligations apply', 'Non-refoulement and human rights treaties', 'Only domestic law applies', 'International law overrides all domestic policy', 'Non-refoulement and human rights treaties', 'International law provides important protections against deportation to persecution.', 'U.S. deportation practices are affected by international obligations including the principle of non-refoulement (not returning people to persecution), refugee law, and human rights treaties that provide protections against deportation to countries where individuals face torture or persecution.', '["non-refoulement", "international law", "human rights treaties"]', '[{"name": "United Nations High Commissioner for Refugees", "url": "https://www.unhcr.org/"}, {"name": "Convention Against Torture", "url": "https://www.ohchr.org/en/professionalinterest/pages/cat.aspx"}, {"name": "Congressional Research Service - International Immigration Law", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 11, 'multiple_choice', 'Immigration', 'How do expedited removal procedures affect due process?', 'Expedited removal provides more due process', 'Reduces procedural protections and hearing opportunities', 'Has no effect on due process', 'Eliminates all constitutional protections', 'Reduces procedural protections and hearing opportunities', 'Expedited procedures can limit traditional due process protections.', 'Expedited removal procedures reduce due process protections by limiting hearing opportunities, shortening review periods, and allowing quicker deportations with fewer procedural safeguards, raising constitutional concerns about adequate protection of individual rights.', '["expedited removal", "reduced due process", "procedural limitations"]', '[{"name": "Department of Homeland Security - Expedited Removal", "url": "https://www.dhs.gov/"}, {"name": "American Immigration Council", "url": "https://www.americanimmigrationcouncil.org/"}, {"name": "Congressional Research Service - Expedited Removal", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 12, 'multiple_choice', 'Constitutional Law', 'What oversight mechanisms exist for deportation enforcement?', 'No oversight mechanisms exist', 'Congressional oversight, judicial review, and inspector general investigations', 'Only executive branch oversight', 'State government oversight exclusively', 'Congressional oversight, judicial review, and inspector general investigations', 'Multiple oversight mechanisms ensure accountability in immigration enforcement.', 'Oversight of deportation enforcement includes congressional hearings and investigations, federal court judicial review of individual cases and policies, inspector general investigations of agency practices, and civil rights monitoring to ensure constitutional compliance.', '["congressional oversight", "judicial review", "inspector general"]', '[{"name": "House Judiciary Committee", "url": "https://judiciary.house.gov/"}, {"name": "Department of Homeland Security Inspector General", "url": "https://www.oig.dhs.gov/"}, {"name": "Congressional Research Service - Immigration Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 13, 'multiple_choice', 'Immigration', 'What factors can prevent deportation?', 'No factors can prevent deportation', 'Asylum, withholding of removal, and protection under Convention Against Torture', 'Only citizenship prevents deportation', 'Deportation is always mandatory', 'Asylum, withholding of removal, and protection under Convention Against Torture', 'Legal protections exist for individuals facing persecution or torture.', 'Factors that can prevent deportation include asylum grants for persecution, withholding of removal for credible fear of persecution, protection under the Convention Against Torture, and other forms of relief available through immigration law and international obligations.', '["asylum protection", "withholding of removal", "torture protection"]', '[{"name": "Executive Office for Immigration Review - Forms of Relief", "url": "https://www.justice.gov/eoir"}, {"name": "United Nations High Commissioner for Refugees", "url": "https://www.unhcr.org/"}, {"name": "Congressional Research Service - Immigration Relief", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 14, 'multiple_choice', 'Civic Participation', 'How can citizens monitor deportation enforcement practices?', 'Citizens cannot monitor immigration enforcement', 'FOIA requests, advocacy organizations, and congressional contact', 'Only government officials can monitor enforcement', 'State governments monitor federal immigration', 'FOIA requests, advocacy organizations, and congressional contact', 'Citizens have tools for monitoring government immigration practices.', 'Citizens can monitor deportation enforcement through Freedom of Information Act requests, supporting advocacy organizations that track enforcement data, contacting congressional representatives about policies, and participating in oversight and accountability efforts.', '["FOIA monitoring", "advocacy organizations", "citizen oversight"]', '[{"name": "Freedom of Information Act", "url": "https://www.foia.gov/"}, {"name": "American Civil Liberties Union", "url": "https://www.aclu.org/"}, {"name": "National Immigration Forum", "url": "https://immigrationforum.org/"}]'),

('2025-04-23-deportation-expansion', 15, 'multiple_choice', 'Immigration', 'What role do immigration courts play in deportation?', 'No role in deportation decisions', 'Adjudicate removal proceedings and determine deportation orders', 'Only provide advisory opinions', 'Automatically approve all deportations', 'Adjudicate removal proceedings and determine deportation orders', 'Immigration courts provide judicial review of deportation cases.', 'Immigration courts play a crucial role by adjudicating removal proceedings, determining whether individuals should be deported, reviewing evidence, hearing testimony, and making legal determinations about immigration violations and available relief from removal.', '["immigration courts", "removal proceedings", "judicial determination"]', '[{"name": "Executive Office for Immigration Review", "url": "https://www.justice.gov/eoir"}, {"name": "National Association of Immigration Judges", "url": "https://www.naij-usa.org/"}, {"name": "Congressional Research Service - Immigration Courts", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 16, 'multiple_choice', 'Constitutional Law', 'How do deportation policies affect Fourth Amendment rights?', 'Fourth Amendment doesn\'t apply to immigration', 'Immigration enforcement must comply with search and seizure protections', 'No constitutional protections in immigration enforcement', 'Fourth Amendment only applies to citizens', 'Immigration enforcement must comply with search and seizure protections', 'Constitutional protections apply to all government law enforcement.', 'Deportation policies must comply with Fourth Amendment protections against unreasonable searches and seizures, requiring proper warrants or legal justification for immigration raids, arrests, and searches, ensuring constitutional compliance in enforcement activities.', '["Fourth Amendment", "search and seizure", "immigration enforcement"]', '[{"name": "Supreme Court - Fourth Amendment Cases", "url": "https://supreme.justia.com/cases/federal/fourth-amendment/"}, {"name": "American Civil Liberties Union - Fourth Amendment Rights", "url": "https://www.aclu.org/issues/criminal-law-reform/reforming-police/fourth-amendment"}, {"name": "Congressional Research Service - Immigration Fourth Amendment", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 17, 'multiple_choice', 'Immigration', 'What economic impacts result from expanded deportation policies?', 'Only positive economic effects', 'Complex effects including labor shortages, family disruption, and enforcement costs', 'No economic impact', 'Automatic economic improvement', 'Complex effects including labor shortages, family disruption, and enforcement costs', 'Deportation policies have multifaceted economic consequences.', 'Expanded deportation policies create complex economic impacts including potential labor shortages in affected industries, family economic disruption, high enforcement costs, community economic effects, and varying impacts across different economic sectors and regions.', '["economic impact", "labor effects", "enforcement costs"]', '[{"name": "Congressional Budget Office - Immigration Economics", "url": "https://www.cbo.gov/"}, {"name": "Bureau of Labor Statistics", "url": "https://www.bls.gov/"}, {"name": "Congressional Research Service - Immigration Economics", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

-- True/False Questions (5 questions)
('2025-04-23-deportation-expansion', 18, 'true_false', 'Constitutional Law', 'Constitutional due process protections only apply to U.S. citizens.', '', '', '', '', 'False', 'Due process protections extend to all persons within U.S. jurisdiction.', 'False. Constitutional due process protections apply to all persons within U.S. jurisdiction, regardless of citizenship status. The Constitution uses "persons" rather than "citizens" in the Due Process Clauses, extending these protections to immigrants facing deportation.', '["due process scope", "constitutional protection", "person vs citizen"]', '[{"name": "Supreme Court - Zadvydas v. Davis", "url": "https://supreme.justia.com/cases/federal/us/533/678/"}, {"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Congressional Research Service - Constitutional Immigration Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 19, 'true_false', 'Immigration', 'Immigrants have the right to legal representation in deportation proceedings, but must provide it at their own expense.', '', '', '', '', 'True', 'The right to counsel exists but is not government-funded in immigration court.', 'True. Immigrants have the right to legal representation in deportation proceedings, but unlike in criminal cases, the government is not required to provide free legal counsel. Immigrants must secure and pay for their own legal representation in immigration court.', '["right to counsel", "self-funded representation", "immigration court rights"]', '[{"name": "Executive Office for Immigration Review - Legal Representation", "url": "https://www.justice.gov/eoir/list-pro-bono-legal-service-providers"}, {"name": "American Bar Association - Immigration Counsel", "url": "https://www.americanbar.org/groups/public_interest/immigration/"}, {"name": "Congressional Research Service - Immigration Legal Representation", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 20, 'true_false', 'Immigration', 'States can nullify federal immigration law within their borders.', '', '', '', '', 'False', 'Federal immigration law is supreme and states cannot nullify it.', 'False. States cannot nullify federal immigration law due to the Supremacy Clause of the Constitution. While states can choose not to cooperate with federal enforcement, they cannot override or nullify federal immigration statutes within their borders.', '["federal supremacy", "state nullification", "immigration federalism"]', '[{"name": "Supreme Court - Arizona v. United States", "url": "https://supreme.justia.com/cases/federal/us/567/387/"}, {"name": "Constitution - Supremacy Clause", "url": "https://constitution.congress.gov/browse/article-6/"}, {"name": "Congressional Research Service - Immigration Federalism", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 21, 'true_false', 'Immigration', 'International law prohibits deporting individuals to countries where they face torture or persecution.', '', '', '', '', 'True', 'The principle of non-refoulement protects against return to persecution.', 'True. International law, including the principle of non-refoulement and treaties like the Convention Against Torture, prohibits deporting individuals to countries where they face torture, persecution, or other serious human rights violations.', '["non-refoulement", "international protection", "torture protection"]', '[{"name": "Convention Against Torture", "url": "https://www.ohchr.org/en/professionalinterest/pages/cat.aspx"}, {"name": "United Nations High Commissioner for Refugees", "url": "https://www.unhcr.org/"}, {"name": "Congressional Research Service - International Immigration Obligations", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 22, 'true_false', 'Constitutional Law', 'Immigration enforcement must comply with Fourth Amendment protections against unreasonable searches.', '', '', '', '', 'True', 'Constitutional protections apply to all government law enforcement activities.', 'True. Immigration enforcement must comply with Fourth Amendment protections against unreasonable searches and seizures. Immigration agents must have proper legal justification for searches, arrests, and raids, just like other law enforcement activities.', '["Fourth Amendment compliance", "immigration searches", "constitutional enforcement"]', '[{"name": "Supreme Court - Fourth Amendment Cases", "url": "https://supreme.justia.com/cases/federal/fourth-amendment/"}, {"name": "American Civil Liberties Union - Immigration Rights", "url": "https://www.aclu.org/issues/immigrants-rights"}, {"name": "Congressional Research Service - Immigration Constitutional Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

-- Short Answer Questions (3 questions)
('2025-04-23-deportation-expansion', 23, 'short_answer', 'Constitutional Law', 'What constitutional amendments provide due process protections for immigrants facing deportation?', '', '', '', '', 'Fifth and Fourteenth Amendments', 'These amendments protect due process rights for all persons within U.S. jurisdiction.', 'The Fifth and Fourteenth Amendments provide due process protections for immigrants facing deportation. These amendments use "persons" rather than "citizens," extending constitutional protections to all individuals within U.S. jurisdiction regardless of immigration status.', '["Fifth Amendment", "Fourteenth Amendment", "due process protection"]', '[{"name": "Constitution - Fifth Amendment", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Constitution - Fourteenth Amendment", "url": "https://constitution.congress.gov/browse/amendment-14/"}, {"name": "Congressional Research Service - Immigration Due Process", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 24, 'short_answer', 'Immigration', 'What federal agencies are primarily responsible for deportation enforcement?', '', '', '', '', 'ICE and Immigration Courts', 'ICE handles enforcement while immigration courts adjudicate removal proceedings.', 'Immigration and Customs Enforcement (ICE) and Immigration Courts are primarily responsible for deportation enforcement. ICE handles investigation, arrest, and detention, while Immigration Courts under the Department of Justice adjudicate removal proceedings.', '["ICE", "Immigration Courts", "deportation agencies"]', '[{"name": "Immigration and Customs Enforcement", "url": "https://www.ice.gov/"}, {"name": "Executive Office for Immigration Review", "url": "https://www.justice.gov/eoir"}, {"name": "Congressional Research Service - Immigration Agencies", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]'),

('2025-04-23-deportation-expansion', 25, 'short_answer', 'Constitutional Law', 'What international legal principle prohibits returning people to countries where they face persecution?', '', '', '', '', 'Non-refoulement', 'This principle provides protection against return to persecution or torture.', 'Non-refoulement is the international legal principle that prohibits returning people to countries where they face persecution, torture, or other serious human rights violations. This principle is established in refugee law and human rights treaties.', '["non-refoulement", "international protection", "refugee law"]', '[{"name": "United Nations High Commissioner for Refugees", "url": "https://www.unhcr.org/"}, {"name": "Convention Against Torture", "url": "https://www.ohchr.org/en/professionalinterest/pages/cat.aspx"}, {"name": "Congressional Research Service - International Refugee Law", "url": "https://crsreports.congress.gov/product/pdf/R/R45314"}]');