INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-04-17-emergency-powers-expansion (Multiple Choice - 17 questions)
('2025-04-17-emergency-powers-expansion', 1, 'multiple_choice', 'Constitutional Law', 'What constitutional principles limit presidential emergency powers?', 'No constitutional limits exist', 'Separation of powers, due process, and constitutional rights', 'Only congressional approval required', 'State government authorization needed', 'Separation of powers, due process, and constitutional rights', 'Constitutional principles constrain all government power, including emergency authority.', 'Presidential emergency powers are limited by constitutional principles including separation of powers that prevents concentration of authority in one branch, due process requirements that protect individual rights, and constitutional protections that cannot be suspended without proper justification and procedures.', '["separation of powers", "due process", "constitutional limits"]', '[{"name": "Constitution - Separation of Powers", "url": "https://constitution.congress.gov/browse/essay/intro-7/"}, {"name": "Supreme Court - Youngstown Co. v. Sawyer", "url": "https://supreme.justia.com/cases/federal/us/343/579/"}, {"name": "Congressional Research Service - Emergency Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 2, 'multiple_choice', 'Constitutional Law', 'What Supreme Court case established limits on presidential emergency powers?', 'Brown v. Board of Education', 'Youngstown Co. v. Sawyer', 'Miranda v. Arizona', 'Marbury v. Madison', 'Youngstown Co. v. Sawyer', 'This 1952 case limited presidential power during national emergencies.', 'Youngstown Co. v. Sawyer (1952) established important limits on presidential emergency powers by rejecting President Truman\'s seizure of steel mills during the Korean War, ruling that presidents cannot act beyond constitutional authority even during national emergencies without congressional authorization.', '["Youngstown case", "presidential limits", "emergency authority"]', '[{"name": "Supreme Court - Youngstown Co. v. Sawyer", "url": "https://supreme.justia.com/cases/federal/us/343/579/"}, {"name": "Constitutional Law - Presidential Power", "url": "https://www.law.cornell.edu/wex/executive_power"}, {"name": "Congressional Research Service - Youngstown Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 3, 'multiple_choice', 'Government', 'What federal law governs presidential emergency declarations?', 'No federal law governs emergencies', 'National Emergencies Act', 'Defense Production Act', 'Patriot Act', 'National Emergencies Act', 'This 1976 law established procedures for emergency declarations.', 'The National Emergencies Act of 1976 governs presidential emergency declarations by establishing procedures for declaring emergencies, requiring congressional notification, providing for automatic termination unless renewed, and ensuring congressional oversight of emergency powers.', '["National Emergencies Act", "emergency procedures", "congressional oversight"]', '[{"name": "National Emergencies Act", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Congressional Research Service - Emergency Powers Law", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federal Emergency Management Agency", "url": "https://www.fema.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 4, 'multiple_choice', 'Constitutional Law', 'How does habeas corpus relate to emergency powers?', 'Emergency powers always suspend habeas corpus', 'Habeas corpus can only be suspended by Congress in specific circumstances', 'Presidents can suspend habeas corpus at will', 'Habeas corpus never applies during emergencies', 'Habeas corpus can only be suspended by Congress in specific circumstances', 'Article I gives Congress, not the President, power to suspend habeas corpus.', 'The Constitution\'s Suspension Clause in Article I, Section 9 gives only Congress the power to suspend habeas corpus, and only "when in Cases of Rebellion or Invasion the public Safety may require it," limiting presidential emergency authority over this fundamental right.', '["habeas corpus", "Suspension Clause", "congressional authority"]', '[{"name": "Constitution - Suspension Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C2-1/"}, {"name": "Supreme Court - Ex parte Milligan", "url": "https://supreme.justia.com/cases/federal/us/71/2/"}, {"name": "Congressional Research Service - Habeas Corpus", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 5, 'multiple_choice', 'Government', 'What role does Congress play in overseeing emergency powers?', 'No congressional role in emergencies', 'Review declarations, terminate emergencies, and provide ongoing oversight', 'Only advisory opinions', 'Automatic approval of all declarations', 'Review declarations, terminate emergencies, and provide ongoing oversight', 'Congress has important oversight responsibilities for emergency powers.', 'Congress plays crucial roles in overseeing emergency powers including reviewing emergency declarations, voting to terminate emergencies, conducting oversight hearings, and ensuring that emergency authorities are used appropriately and within constitutional bounds.', '["congressional oversight", "emergency termination", "legislative review"]', '[{"name": "Congressional Research Service - Congressional Emergency Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "House Oversight Committee", "url": "https://oversight.house.gov/"}, {"name": "National Emergencies Act - Congressional Procedures", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}]'),

('2025-04-17-emergency-powers-expansion', 6, 'multiple_choice', 'Constitutional Law', 'What constitutional rights remain protected during emergencies?', 'No rights remain protected', 'Fundamental rights remain unless constitutionally suspended through proper procedures', 'Only property rights', 'Rights depend on presidential discretion', 'Fundamental rights remain unless constitutionally suspended through proper procedures', 'Constitutional rights generally continue to apply during emergencies.', 'Fundamental constitutional rights including free speech, due process, equal protection, and others generally remain protected during emergencies unless specifically and constitutionally suspended through proper procedures with compelling justification and appropriate safeguards.', '["fundamental rights", "constitutional protection", "emergency limitations"]', '[{"name": "Supreme Court - Constitutional Rights in Emergencies", "url": "https://supreme.justia.com/cases/federal/constitutional-law/"}, {"name": "Bill of Rights Institute", "url": "https://www.billofrightsinstitute.org/"}, {"name": "Congressional Research Service - Rights During Emergencies", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 7, 'multiple_choice', 'Government', 'How do emergency powers affect federalism?', 'Emergency powers eliminate federalism', 'Federal emergency authority can override some state powers but must respect constitutional limits', 'States control all emergency responses', 'No federal role in emergencies', 'Federal emergency authority can override some state powers but must respect constitutional limits', 'Federalism continues to apply with some modifications during emergencies.', 'Emergency powers affect federalism by allowing federal authority to override some state powers during genuine emergencies, but federal emergency actions must still respect constitutional limits, state sovereignty in areas of traditional state authority, and the federal structure of government.', '["federalism", "state authority", "federal preemption"]', '[{"name": "Congressional Research Service - Federalism and Emergencies", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Supreme Court - Federalism Cases", "url": "https://supreme.justia.com/cases/federal/federalism/"}, {"name": "Federal Emergency Management Agency - Federal-State Relations", "url": "https://www.fema.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 8, 'multiple_choice', 'Constitutional Law', 'What judicial review applies to emergency powers?', 'No judicial review during emergencies', 'Courts can review emergency actions for constitutional compliance', 'Only emergency courts have jurisdiction', 'Automatic approval of all emergency actions', 'Courts can review emergency actions for constitutional compliance', 'Judicial review continues to apply to emergency government actions.', 'Courts retain authority to review emergency powers for constitutional compliance, ensuring that emergency actions are within legal authority, serve legitimate purposes, are proportionate to the emergency, and respect constitutional rights and procedural requirements.', '["judicial review", "constitutional compliance", "court oversight"]', '[{"name": "Supreme Court - Emergency Powers Review", "url": "https://supreme.justia.com/cases/federal/executive-power/"}, {"name": "Congressional Research Service - Judicial Review of Emergencies", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federal Courts - Constitutional Review", "url": "https://www.uscourts.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 9, 'multiple_choice', 'Government', 'What transparency requirements apply to emergency declarations?', 'Complete secrecy required', 'Public notification and congressional reporting requirements', 'No transparency requirements', 'Only classified briefings', 'Public notification and congressional reporting requirements', 'Democratic accountability requires transparency in emergency powers.', 'Emergency declarations are subject to transparency requirements including public notification of the emergency and its scope, congressional reporting on authorities used, and ongoing disclosure of emergency actions, ensuring democratic accountability and oversight.', '["transparency requirements", "public notification", "congressional reporting"]', '[{"name": "National Emergencies Act - Reporting Requirements", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Government Accountability Office - Emergency Transparency", "url": "https://www.gao.gov/"}, {"name": "Congressional Research Service - Emergency Disclosure", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 10, 'multiple_choice', 'Constitutional Law', 'How do emergency powers interact with civil liberties?', 'Emergency powers eliminate all civil liberties', 'Emergency powers may restrict some liberties but must meet strict constitutional standards', 'Civil liberties are automatically suspended', 'No interaction between emergency powers and liberties', 'Emergency powers may restrict some liberties but must meet strict constitutional standards', 'Civil liberties restrictions require compelling justification and narrow tailoring.', 'Emergency powers may restrict some civil liberties, but such restrictions must meet strict constitutional standards including compelling government interest, narrow tailoring to achieve specific emergency objectives, and time limitations with appropriate safeguards for fundamental rights.', '["civil liberties", "strict scrutiny", "constitutional standards"]', '[{"name": "American Civil Liberties Union - Emergency Powers", "url": "https://www.aclu.org/"}, {"name": "Supreme Court - Civil Liberties in Emergencies", "url": "https://supreme.justia.com/cases/federal/civil-rights/"}, {"name": "Congressional Research Service - Emergency and Civil Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 11, 'multiple_choice', 'Government', 'What historical precedents exist for emergency power abuse?', 'No historical abuses occurred', 'Japanese internment, martial law declarations, and wartime restrictions', 'Only beneficial uses of emergency powers', 'Emergency powers have never been controversial', 'Japanese internment, martial law declarations, and wartime restrictions', 'History provides important lessons about emergency power limitations.', 'Historical precedents for emergency power abuse include Japanese American internment during World War II, excessive martial law declarations, wartime restrictions on civil liberties, and other examples that demonstrate the importance of constitutional limits and oversight of emergency authority.', '["historical precedents", "Japanese internment", "emergency abuse"]', '[{"name": "National Archives - Japanese American Internment", "url": "https://www.archives.gov/research/japanese-americans"}, {"name": "Supreme Court - Korematsu v. United States", "url": "https://supreme.justia.com/cases/federal/us/323/214/"}, {"name": "Congressional Research Service - Emergency Powers History", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 12, 'multiple_choice', 'Constitutional Law', 'What constitutional provisions address emergency situations?', 'No constitutional emergency provisions', 'Suspension Clause, martial law authority, and implied emergency powers', 'Only presidential discretion', 'State emergency powers exclusively', 'Suspension Clause, martial law authority, and implied emergency powers', 'The Constitution addresses emergencies through specific and implied provisions.', 'The Constitution addresses emergency situations through the Suspension Clause for habeas corpus, provisions for martial law and domestic violence, implied emergency powers for national defense, and the general framework of government powers that must operate within constitutional bounds.', '["Suspension Clause", "martial law", "constitutional emergency provisions"]', '[{"name": "Constitution - Emergency Provisions", "url": "https://constitution.congress.gov/"}, {"name": "Congressional Research Service - Constitutional Emergency Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Supreme Court - Constitutional Emergency Cases", "url": "https://supreme.justia.com/cases/federal/constitutional-law/"}]'),

('2025-04-17-emergency-powers-expansion', 13, 'multiple_choice', 'Government', 'How do international emergency standards affect U.S. practice?', 'International standards don\'t apply to the U.S.', 'International human rights law provides guidance for emergency limitations', 'U.S. emergency powers are unlimited', 'Only domestic law applies', 'International human rights law provides guidance for emergency limitations', 'International legal standards inform best practices for emergency governance.', 'International emergency standards, particularly international human rights law, provide guidance for U.S. emergency practices by establishing principles for legitimate emergency restrictions, protection of fundamental rights, and proportionate responses to genuine threats.', '["international standards", "human rights law", "emergency proportionality"]', '[{"name": "United Nations Human Rights Office", "url": "https://www.ohchr.org/"}, {"name": "International Covenant on Civil and Political Rights", "url": "https://www.ohchr.org/en/professionalinterest/pages/ccpr.aspx"}, {"name": "Congressional Research Service - International Emergency Law", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 14, 'multiple_choice', 'Civic Participation', 'How can citizens monitor and respond to emergency power use?', 'Citizens have no role during emergencies', 'Congressional contact, legal challenges, and civic organization involvement', 'Only government officials can monitor emergencies', 'Automatic acceptance required', 'Congressional contact, legal challenges, and civic organization involvement', 'Democratic participation continues during emergencies.', 'Citizens can monitor and respond to emergency power use by contacting congressional representatives, supporting legal challenges to unconstitutional emergency actions, participating in civic organizations that monitor government power, and maintaining democratic engagement and oversight.', '["citizen monitoring", "democratic participation", "legal challenges"]', '[{"name": "American Civil Liberties Union", "url": "https://www.aclu.org/"}, {"name": "Brennan Center for Justice", "url": "https://www.brennancenter.org/"}, {"name": "Congressional Research Service - Civic Engagement", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]'),

('2025-04-17-emergency-powers-expansion', 15, 'multiple_choice', 'Constitutional Law', 'What standards determine legitimate emergency declarations?', 'No standards exist', 'Genuine emergency, proportionate response, and constitutional compliance', 'Presidential discretion alone', 'Popular approval required', 'Genuine emergency, proportionate response, and constitutional compliance', 'Emergency declarations must meet legal and constitutional standards.', 'Legitimate emergency declarations must meet standards including existence of a genuine emergency or threat, proportionate response appropriate to the specific emergency, constitutional compliance with legal procedures and rights protections, and time limitations with appropriate oversight.', '["emergency standards", "proportionate response", "constitutional compliance"]', '[{"name": "National Emergencies Act - Standards", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Supreme Court - Emergency Standards", "url": "https://supreme.justia.com/cases/federal/executive-power/"}, {"name": "Congressional Research Service - Emergency Criteria", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 16, 'multiple_choice', 'Government', 'What oversight mechanisms exist for ongoing emergency powers?', 'No oversight during emergencies', 'Congressional review, judicial oversight, and periodic renewal requirements', 'Only executive branch oversight', 'Automatic indefinite authority', 'Congressional review, judicial oversight, and periodic renewal requirements', 'Multiple oversight mechanisms ensure accountability during emergencies.', 'Ongoing emergency powers are subject to oversight through congressional review and termination authority, judicial oversight of specific emergency actions, periodic renewal requirements that prevent indefinite emergency rule, and transparency and reporting requirements for accountability.', '["congressional review", "judicial oversight", "periodic renewal"]', '[{"name": "National Emergencies Act - Oversight Provisions", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Government Accountability Office - Emergency Oversight", "url": "https://www.gao.gov/"}, {"name": "Congressional Research Service - Emergency Accountability", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 17, 'multiple_choice', 'Constitutional Law', 'How do emergency powers affect the system of checks and balances?', 'Emergency powers eliminate checks and balances', 'Checks and balances continue to operate with some modifications during emergencies', 'Only the executive branch functions during emergencies', 'Checks and balances are automatically suspended', 'Checks and balances continue to operate with some modifications during emergencies', 'Constitutional structure remains important during emergency situations.', 'Emergency powers affect but do not eliminate checks and balances. The constitutional system continues to operate with some modifications, including enhanced executive authority balanced by continued congressional oversight, judicial review, and constitutional limits on emergency actions.', '["checks and balances", "constitutional structure", "emergency modifications"]', '[{"name": "Constitution - Separation of Powers", "url": "https://constitution.congress.gov/browse/essay/intro-7/"}, {"name": "Congressional Research Service - Emergency Powers and Separation", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federalist Papers - Checks and Balances", "url": "https://guides.loc.gov/federalist-papers/separation-of-powers"}]'),

-- True/False Questions (5 questions)
('2025-04-17-emergency-powers-expansion', 18, 'true_false', 'Constitutional Law', 'Presidents can suspend any constitutional right during declared emergencies.', '', '', '', '', 'False', 'Constitutional rights generally remain protected even during emergencies.', 'False. Presidents cannot suspend constitutional rights at will during emergencies. Most constitutional protections continue to apply, and any restrictions on rights must meet strict constitutional standards and be properly justified and proportionate to the emergency.', '["constitutional rights", "emergency limitations", "presidential authority"]', '[{"name": "Supreme Court - Constitutional Rights in Emergencies", "url": "https://supreme.justia.com/cases/federal/constitutional-law/"}, {"name": "Bill of Rights Institute", "url": "https://www.billofrightsinstitute.org/"}, {"name": "Congressional Research Service - Emergency and Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 19, 'true_false', 'Government', 'Congress can terminate a presidential emergency declaration through a resolution.', '', '', '', '', 'True', 'The National Emergencies Act provides congressional termination authority.', 'True. Congress can terminate a presidential emergency declaration through a resolution under the National Emergencies Act, providing legislative oversight and the ability to end emergency powers when Congress determines they are no longer necessary or appropriate.', '["congressional termination", "National Emergencies Act", "legislative oversight"]', '[{"name": "National Emergencies Act - Termination Procedures", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Congressional Research Service - Emergency Termination", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "House and Senate Procedures", "url": "https://www.congress.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 20, 'true_false', 'Constitutional Law', 'Only Congress has the constitutional authority to suspend habeas corpus.', '', '', '', '', 'True', 'Article I gives Congress, not the President, this specific authority.', 'True. The Constitution\'s Suspension Clause in Article I specifically gives Congress, not the President, the authority to suspend habeas corpus, and only in cases of rebellion or invasion when public safety requires it.', '["habeas corpus", "Suspension Clause", "congressional authority"]', '[{"name": "Constitution - Suspension Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C2-1/"}, {"name": "Supreme Court - Ex parte Milligan", "url": "https://supreme.justia.com/cases/federal/us/71/2/"}, {"name": "Congressional Research Service - Habeas Corpus", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 21, 'true_false', 'Government', 'Emergency declarations automatically expire unless renewed by the President.', '', '', '', '', 'True', 'The National Emergencies Act requires periodic renewal to prevent indefinite emergency rule.', 'True. Under the National Emergencies Act, emergency declarations automatically expire after one year unless renewed by the President, preventing indefinite emergency rule and ensuring periodic review of ongoing emergency authorities.', '["automatic expiration", "periodic renewal", "time limitations"]', '[{"name": "National Emergencies Act - Renewal Requirements", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Congressional Research Service - Emergency Duration", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federal Emergency Management Agency", "url": "https://www.fema.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 22, 'true_false', 'Constitutional Law', 'Courts cannot review emergency actions because they involve political questions.', '', '', '', '', 'False', 'Courts retain authority to review emergency actions for constitutional compliance.', 'False. Courts can and do review emergency actions for constitutional compliance. While some emergency decisions may involve political questions, courts retain authority to ensure that emergency powers are exercised within constitutional bounds and legal authority.', '["judicial review", "constitutional compliance", "political question doctrine"]', '[{"name": "Supreme Court - Emergency Powers Review", "url": "https://supreme.justia.com/cases/federal/executive-power/"}, {"name": "Congressional Research Service - Judicial Review", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federal Courts - Constitutional Review", "url": "https://www.uscourts.gov/"}]'),

-- Short Answer Questions (3 questions)
('2025-04-17-emergency-powers-expansion', 23, 'short_answer', 'Constitutional Law', 'What 1952 Supreme Court case established important limits on presidential emergency powers?', '', '', '', '', 'Youngstown Co. v. Sawyer', 'This case involved presidential seizure of steel mills during the Korean War.', 'Youngstown Co. v. Sawyer (1952) established important limits on presidential emergency powers by rejecting President Truman\'s seizure of steel mills during the Korean War, ruling that presidents cannot exceed constitutional authority even during national emergencies.', '["Youngstown case", "presidential limits", "emergency authority"]', '[{"name": "Supreme Court - Youngstown Co. v. Sawyer", "url": "https://supreme.justia.com/cases/federal/us/343/579/"}, {"name": "Constitutional Law - Presidential Power", "url": "https://www.law.cornell.edu/wex/executive_power"}, {"name": "Congressional Research Service - Youngstown Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]'),

('2025-04-17-emergency-powers-expansion', 24, 'short_answer', 'Government', 'What federal law governs procedures for presidential emergency declarations?', '', '', '', '', 'National Emergencies Act', 'This 1976 law established procedures and oversight for emergency powers.', 'The National Emergencies Act of 1976 governs procedures for presidential emergency declarations, establishing requirements for congressional notification, automatic termination unless renewed, and oversight mechanisms for emergency authorities.', '["National Emergencies Act", "emergency procedures", "congressional oversight"]', '[{"name": "National Emergencies Act", "url": "https://www.law.cornell.edu/uscode/text/50/chapter-34"}, {"name": "Congressional Research Service - Emergency Powers Law", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}, {"name": "Federal Emergency Management Agency", "url": "https://www.fema.gov/"}]'),

('2025-04-17-emergency-powers-expansion', 25, 'short_answer', 'Constitutional Law', 'What constitutional provision specifically addresses suspension of habeas corpus?', '', '', '', '', 'Suspension Clause', 'This provision is found in Article I, Section 9 of the Constitution.', 'The Suspension Clause in Article I, Section 9 specifically addresses suspension of habeas corpus, stating it can only be suspended "when in Cases of Rebellion or Invasion the public Safety may require it," giving this authority to Congress, not the President.', '["Suspension Clause", "Article I", "habeas corpus"]', '[{"name": "Constitution - Suspension Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C2-1/"}, {"name": "Supreme Court - Habeas Corpus Cases", "url": "https://supreme.justia.com/cases/federal/habeas-corpus/"}, {"name": "Congressional Research Service - Habeas Corpus", "url": "https://crsreports.congress.gov/product/pdf/R/R46618"}]');