INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-04-14-federal-workforce-purge (Multiple Choice - 17 questions)
('2025-04-14-federal-workforce-purge', 1, 'multiple_choice', 'Government', 'What protections exist for federal civil service employees?', 'No protections exist', 'Civil service laws, merit system principles, and due process rights', 'Only union protections', 'State employment laws only', 'Civil service laws, merit system principles, and due process rights', 'Federal employees have legal protections against arbitrary removal.', 'Federal civil service employees are protected by civil service laws including the Civil Service Reform Act, merit system principles that require hiring and firing based on qualifications rather than politics, and due process rights that ensure fair treatment in employment actions.', '["civil service protection", "merit system", "due process"]', '[{"name": "Office of Personnel Management - Civil Service Laws", "url": "https://www.opm.gov/policy-data-oversight/classification-qualifications/reference-materials/civil-service-laws/"}, {"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "Congressional Research Service - Federal Employment", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 2, 'multiple_choice', 'Constitutional Law', 'What constitutional principle protects government employees from political retaliation?', 'No constitutional protections', 'First Amendment free speech and due process rights', 'Second Amendment protections', 'Commerce Clause protection', 'First Amendment free speech and due process rights', 'Constitutional rights protect federal employees from political persecution.', 'Federal employees are protected by First Amendment free speech rights that prevent retaliation for political views and due process protections under the Fifth and Fourteenth Amendments that require fair procedures before termination or other adverse employment actions.', '["First Amendment", "due process", "political retaliation"]', '[{"name": "Supreme Court - Pickering v. Board of Education", "url": "https://supreme.justia.com/cases/federal/us/391/563/"}, {"name": "Congressional Research Service - Employee Speech Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}, {"name": "Office of Special Counsel - First Amendment", "url": "https://osc.gov/Pages/default.aspx"}]'),

('2025-04-14-federal-workforce-purge', 3, 'multiple_choice', 'Government', 'What is the purpose of the merit system in federal employment?', 'Political loyalty testing', 'Hiring and promoting based on qualifications and performance', 'Reducing government workforce', 'Increasing political control', 'Hiring and promoting based on qualifications and performance', 'Merit systems ensure competence and prevent political corruption.', 'The merit system in federal employment ensures that hiring, promotion, and retention decisions are based on qualifications, performance, and competence rather than political affiliation, personal connections, or other non-merit factors, maintaining professional public service.', '["merit system", "qualifications-based hiring", "professional service"]', '[{"name": "Office of Personnel Management - Merit System Principles", "url": "https://www.opm.gov/policy-data-oversight/oversight-review/merit-system-principles/"}, {"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "Congressional Research Service - Merit System", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 4, 'multiple_choice', 'Government', 'What agency oversees federal civil service protections?', 'Department of Justice exclusively', 'Office of Personnel Management and Merit Systems Protection Board', 'State employment agencies', 'Congress directly', 'Office of Personnel Management and Merit Systems Protection Board', 'Multiple agencies protect federal employee rights.', 'Federal civil service protections are overseen by the Office of Personnel Management (which sets policies and procedures) and the Merit Systems Protection Board (which adjudicates appeals and ensures compliance with merit system principles and employee rights).', '["OPM", "Merit Systems Protection Board", "oversight agencies"]', '[{"name": "Office of Personnel Management", "url": "https://www.opm.gov/"}, {"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "Congressional Research Service - Federal HR Management", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 5, 'multiple_choice', 'Government', 'What procedures must be followed for federal employee terminations?', 'No procedures required', 'Notice, opportunity to respond, and appeal rights', 'Immediate termination allowed', 'Only supervisor approval needed', 'Notice, opportunity to respond, and appeal rights', 'Due process requires fair procedures before adverse employment actions.', 'Federal employee terminations must follow due process procedures including advance notice of proposed action, opportunity for the employee to respond and present evidence, consideration of employee response, and rights to appeal adverse decisions to appropriate reviewing bodies.', '["due process procedures", "notice requirements", "appeal rights"]', '[{"name": "Code of Federal Regulations - Employee Appeals", "url": "https://www.ecfr.gov/current/title-5"}, {"name": "Merit Systems Protection Board - Procedures", "url": "https://www.mspb.gov/appeals/appeals.htm"}, {"name": "Congressional Research Service - Federal Employment Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 6, 'multiple_choice', 'Constitutional Law', 'How does separation of powers affect federal workforce management?', 'No separation of powers impact', 'Congress sets employment laws, executive implements, judiciary reviews', 'Only executive branch controls employment', 'States control federal employment', 'Congress sets employment laws, executive implements, judiciary reviews', 'Each branch has distinct roles in federal employment governance.', 'Separation of powers affects federal workforce management through Congress\'s authority to set employment laws and policies, the executive branch\'s role in implementing and administering those policies, and the judiciary\'s responsibility to review employment actions for legal compliance.', '["separation of powers", "congressional authority", "judicial review"]', '[{"name": "Congressional Research Service - Separation of Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}, {"name": "Supreme Court - Employment Law Cases", "url": "https://supreme.justia.com/cases/federal/employment-law/"}, {"name": "Office of Personnel Management - Legal Framework", "url": "https://www.opm.gov/policy-data-oversight/"}]'),

('2025-04-14-federal-workforce-purge', 7, 'multiple_choice', 'Government', 'What historical reforms created modern civil service protections?', 'No historical reforms', 'Pendleton Act and Civil Service Reform Act', 'Only recent changes', 'State-level reforms only', 'Pendleton Act and Civil Service Reform Act', 'Major legislation established merit-based federal employment.', 'Modern civil service protections were created by historical reforms including the Pendleton Act of 1883 (which established merit-based hiring) and the Civil Service Reform Act of 1978 (which created current federal personnel systems and employee protections).', '["Pendleton Act", "Civil Service Reform Act", "historical development"]', '[{"name": "National Archives - Pendleton Act", "url": "https://www.archives.gov/milestone-documents/pendleton-act"}, {"name": "Office of Personnel Management - History", "url": "https://www.opm.gov/about-us/our-mission-role-history/historical-background/"}, {"name": "Congressional Research Service - Civil Service History", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 8, 'multiple_choice', 'Government', 'What role do federal employee unions play in workforce protection?', 'No role in federal employment', 'Collective bargaining and representation in employment disputes', 'Complete control over hiring decisions', 'Only advisory functions', 'Collective bargaining and representation in employment disputes', 'Federal unions provide important employee protections and representation.', 'Federal employee unions play important roles in workforce protection through collective bargaining for working conditions and procedures, representing employees in grievances and disputes, and advocating for employee rights and protections within the federal personnel system.', '["federal unions", "collective bargaining", "employee representation"]', '[{"name": "Federal Labor Relations Authority", "url": "https://www.flra.gov/"}, {"name": "American Federation of Government Employees", "url": "https://www.afge.org/"}, {"name": "Congressional Research Service - Federal Labor Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 9, 'multiple_choice', 'Government', 'How do Schedule F and similar classifications affect employee protections?', 'Strengthen all employee protections', 'Can reduce civil service protections for policy-related positions', 'Have no effect on protections', 'Only affect contractor positions', 'Can reduce civil service protections for policy-related positions', 'Different employment schedules have varying levels of protection.', 'Schedule F and similar classifications can significantly reduce civil service protections by reclassifying positions as policy-related rather than traditional civil service, making employees in these positions more easily removable and reducing their appeal rights and job security.', '["Schedule F", "classification changes", "reduced protections"]', '[{"name": "Office of Personnel Management - Position Classification", "url": "https://www.opm.gov/policy-data-oversight/classification-qualifications/"}, {"name": "Government Accountability Office - Classification Analysis", "url": "https://www.gao.gov/products/federal-workforce"}, {"name": "Congressional Research Service - Federal Employment Classifications", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 10, 'multiple_choice', 'Constitutional Law', 'What due process rights apply to federal employment decisions?', 'No due process rights', 'Notice, hearing opportunity, and appeal rights for protected employees', 'Only informal consultation required', 'Automatic termination allowed', 'Notice, hearing opportunity, and appeal rights for protected employees', 'Due process protections apply to government employment actions.', 'Federal employees with civil service protection have due process rights including notice of proposed adverse actions, opportunity for hearing and response, right to representation, and appeal rights to independent reviewing bodies for employment decisions affecting their job security.', '["due process rights", "hearing opportunity", "employment protection"]', '[{"name": "Supreme Court - Cleveland Board v. Loudermill", "url": "https://supreme.justia.com/cases/federal/us/470/532/"}, {"name": "Merit Systems Protection Board - Due Process", "url": "https://www.mspb.gov/"}, {"name": "Congressional Research Service - Employee Due Process", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 11, 'multiple_choice', 'Government', 'What oversight mechanisms exist for federal personnel actions?', 'No oversight mechanisms', 'Inspector Generals, congressional oversight, and judicial review', 'Only internal management review', 'State government oversight', 'Inspector Generals, congressional oversight, and judicial review', 'Multiple oversight mechanisms protect against personnel abuse.', 'Federal personnel actions are subject to oversight through Inspector Generals investigating misconduct, congressional oversight of personnel policies and practices, judicial review of employment decisions, and administrative appeals processes ensuring accountability and fairness.', '["oversight mechanisms", "Inspector General", "congressional review"]', '[{"name": "Council of Inspectors General", "url": "https://www.ignet.gov/"}, {"name": "Government Accountability Office - Federal Workforce", "url": "https://www.gao.gov/products/federal-workforce"}, {"name": "Congressional Research Service - Personnel Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 12, 'multiple_choice', 'Government', 'How do federal workforce changes affect government operations?', 'No operational impact', 'Can disrupt institutional knowledge, expertise, and service delivery', 'Always improve efficiency', 'Only affect administrative functions', 'Can disrupt institutional knowledge, expertise, and service delivery', 'Workforce stability is important for effective government operations.', 'Federal workforce changes can significantly affect government operations by disrupting institutional knowledge and expertise, affecting service delivery to citizens, impacting regulatory enforcement, and potentially compromising the government\'s ability to effectively implement laws and policies.', '["operational impact", "institutional knowledge", "service delivery"]', '[{"name": "Government Accountability Office - Workforce Planning", "url": "https://www.gao.gov/products/workforce-planning"}, {"name": "Partnership for Public Service - Government Effectiveness", "url": "https://ourpublicservice.org/"}, {"name": "Congressional Research Service - Government Operations", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 13, 'multiple_choice', 'Constitutional Law', 'What equal protection concerns arise from targeted workforce actions?', 'No equal protection issues', 'Discrimination based on protected characteristics or political views', 'Equal protection only applies to voting', 'No constitutional protections for employees', 'Discrimination based on protected characteristics or political views', 'Equal protection prevents discriminatory employment practices.', 'Targeted workforce actions raise equal protection concerns when they discriminate based on race, gender, religion, political affiliation, or other protected characteristics, violating constitutional requirements for equal treatment and due process in government employment decisions.', '["equal protection", "employment discrimination", "protected characteristics"]', '[{"name": "Equal Employment Opportunity Commission", "url": "https://www.eeoc.gov/"}, {"name": "Supreme Court - Equal Protection Cases", "url": "https://supreme.justia.com/cases/federal/equal-protection/"}, {"name": "Congressional Research Service - Employment Discrimination", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 14, 'multiple_choice', 'Government', 'What role does Congress play in protecting federal employees?', 'No congressional role', 'Setting employment laws, oversight, and investigating violations', 'Only budget authority', 'Direct management of employees', 'Setting employment laws, oversight, and investigating violations', 'Congress has important oversight and legislative responsibilities.', 'Congress plays crucial roles in protecting federal employees by setting employment laws and civil service protections, conducting oversight of personnel practices, investigating violations of employee rights, and ensuring accountability in federal workforce management.', '["congressional oversight", "employment legislation", "employee protection"]', '[{"name": "House Oversight Committee", "url": "https://oversight.house.gov/"}, {"name": "Senate Homeland Security Committee", "url": "https://www.hsgac.senate.gov/"}, {"name": "Congressional Research Service - Congressional Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

('2025-04-14-federal-workforce-purge', 15, 'multiple_choice', 'Civic Participation', 'How can citizens monitor federal workforce practices?', 'Citizens have no monitoring role', 'Freedom of Information Act, congressional contact, and advocacy organizations', 'Only government officials can monitor', 'State governments monitor federal practices', 'Freedom of Information Act, congressional contact, and advocacy organizations', 'Citizens have tools for monitoring government employment practices.', 'Citizens can monitor federal workforce practices through Freedom of Information Act requests for personnel data, contacting congressional representatives about concerns, supporting advocacy organizations that monitor government employment, and participating in public oversight processes.', '["FOIA requests", "citizen monitoring", "advocacy organizations"]', '[{"name": "Freedom of Information Act", "url": "https://www.foia.gov/"}, {"name": "Government Accountability Project", "url": "https://www.whistleblower.org/"}, {"name": "American Civil Liberties Union", "url": "https://www.aclu.org/"}]'),

('2025-04-14-federal-workforce-purge', 16, 'multiple_choice', 'Government', 'What protections exist for federal whistleblowers?', 'No whistleblower protections', 'Whistleblower Protection Act and Office of Special Counsel oversight', 'Only internal complaint processes', 'State whistleblower laws only', 'Whistleblower Protection Act and Office of Special Counsel oversight', 'Federal law specifically protects government whistleblowers.', 'Federal whistleblowers are protected by the Whistleblower Protection Act, which prohibits retaliation for reporting waste, fraud, or abuse, and the Office of Special Counsel, which investigates retaliation complaints and ensures protection for employees who expose wrongdoing.', '["Whistleblower Protection Act", "Office of Special Counsel", "retaliation protection"]', '[{"name": "Office of Special Counsel", "url": "https://osc.gov/"}, {"name": "Whistleblower Protection Act", "url": "https://osc.gov/Services/Pages/WBdisc.aspx"}, {"name": "Congressional Research Service - Whistleblower Protection", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 17, 'multiple_choice', 'Constitutional Law', 'How do mass federal employee terminations affect constitutional governance?', 'No constitutional implications', 'Can undermine separation of powers and democratic accountability', 'Strengthen constitutional governance', 'Only affect administrative efficiency', 'Can undermine separation of powers and democratic accountability', 'Institutional continuity is important for constitutional governance.', 'Mass federal employee terminations can undermine constitutional governance by disrupting institutional knowledge needed for effective separation of powers, compromising professional expertise required for democratic accountability, and potentially enabling political control over traditionally nonpartisan government functions.', '["constitutional governance", "institutional continuity", "democratic accountability"]', '[{"name": "Brookings Institution - Government Effectiveness", "url": "https://www.brookings.edu/research/governance-studies/"}, {"name": "Partnership for Public Service", "url": "https://ourpublicservice.org/"}, {"name": "Congressional Research Service - Constitutional Governance", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}]'),

-- True/False Questions (5 questions)
('2025-04-14-federal-workforce-purge', 18, 'true_false', 'Government', 'Federal employees can be terminated without any due process procedures.', '', '', '', '', 'False', 'Civil service employees have due process protections before termination.', 'False. Federal employees with civil service protection cannot be terminated without due process procedures including notice, opportunity to respond, and appeal rights. These protections ensure fair treatment and prevent arbitrary or politically motivated terminations.', '["due process protection", "termination procedures", "employee rights"]', '[{"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "Code of Federal Regulations - Personnel", "url": "https://www.ecfr.gov/current/title-5"}, {"name": "Congressional Research Service - Federal Employment", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 19, 'true_false', 'Constitutional Law', 'The First Amendment protects federal employees from retaliation for their political views.', '', '', '', '', 'True', 'Constitutional free speech protections apply to federal employees.', 'True. The First Amendment protects federal employees from retaliation for their political views and expressions, preventing government employers from punishing employees for exercising their constitutional rights to free speech and political participation.', '["First Amendment protection", "political retaliation", "free speech rights"]', '[{"name": "Supreme Court - Pickering v. Board of Education", "url": "https://supreme.justia.com/cases/federal/us/391/563/"}, {"name": "Office of Special Counsel - First Amendment", "url": "https://osc.gov/"}, {"name": "Congressional Research Service - Employee Speech Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 20, 'true_false', 'Government', 'The merit system requires hiring and promotion based on qualifications rather than political loyalty.', '', '', '', '', 'True', 'Merit system principles prioritize competence over political connections.', 'True. The merit system specifically requires that hiring, promotion, and retention decisions be based on qualifications, performance, and competence rather than political loyalty, personal connections, or other non-merit factors, ensuring professional public service.', '["merit system principles", "qualifications-based", "nonpartisan hiring"]', '[{"name": "Office of Personnel Management - Merit Principles", "url": "https://www.opm.gov/policy-data-oversight/oversight-review/merit-system-principles/"}, {"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "Congressional Research Service - Merit System", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 21, 'true_false', 'Government', 'Congress has no authority to investigate federal workforce management practices.', '', '', '', '', 'False', 'Congress has broad oversight authority over executive branch operations.', 'False. Congress has extensive authority to investigate federal workforce management practices through its oversight powers, including the ability to hold hearings, subpoena documents and witnesses, and investigate violations of employment laws and procedures.', '["congressional oversight", "investigation authority", "workforce oversight"]', '[{"name": "Congressional Research Service - Congressional Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "House Oversight Committee", "url": "https://oversight.house.gov/"}, {"name": "Government Accountability Office", "url": "https://www.gao.gov/"}]'),

('2025-04-14-federal-workforce-purge', 22, 'true_false', 'Government', 'Federal employee unions can collectively bargain for job security protections.', '', '', '', '', 'True', 'Federal labor law allows collective bargaining on working conditions and procedures.', 'True. Federal employee unions can collectively bargain for job security protections, working conditions, and procedures within the framework of federal labor relations law, providing additional protections beyond basic civil service requirements.', '["collective bargaining", "job security", "federal unions"]', '[{"name": "Federal Labor Relations Authority", "url": "https://www.flra.gov/"}, {"name": "American Federation of Government Employees", "url": "https://www.afge.org/"}, {"name": "Congressional Research Service - Federal Labor Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

-- Short Answer Questions (3 questions)
('2025-04-14-federal-workforce-purge', 23, 'short_answer', 'Government', 'What federal law established merit-based hiring for government employees?', '', '', '', '', 'Pendleton Act', 'This 1883 law created the foundation of modern civil service.', 'The Pendleton Act of 1883 established merit-based hiring for federal government employees, creating the foundation of the modern civil service system by requiring competitive examinations and merit-based selection rather than political patronage.', '["Pendleton Act", "merit-based hiring", "civil service reform"]', '[{"name": "National Archives - Pendleton Act", "url": "https://www.archives.gov/milestone-documents/pendleton-act"}, {"name": "Office of Personnel Management - History", "url": "https://www.opm.gov/about-us/our-mission-role-history/"}, {"name": "Congressional Research Service - Civil Service History", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 24, 'short_answer', 'Government', 'What agency adjudicates appeals from federal employees facing adverse employment actions?', '', '', '', '', 'Merit Systems Protection Board', 'This independent agency provides due process hearings for federal employees.', 'The Merit Systems Protection Board (MSPB) adjudicates appeals from federal employees facing adverse employment actions, providing independent review and ensuring that employment decisions comply with merit system principles and due process requirements.', '["Merit Systems Protection Board", "MSPB", "employment appeals"]', '[{"name": "Merit Systems Protection Board", "url": "https://www.mspb.gov/"}, {"name": "MSPB Appeals Process", "url": "https://www.mspb.gov/appeals/appeals.htm"}, {"name": "Congressional Research Service - Federal Employment Appeals", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]'),

('2025-04-14-federal-workforce-purge', 25, 'short_answer', 'Constitutional Law', 'What constitutional amendment protects federal employees from retaliation for political speech?', '', '', '', '', 'First Amendment', 'This amendment protects freedom of speech and political expression.', 'The First Amendment protects federal employees from retaliation for political speech and expression, preventing government employers from punishing employees for exercising their constitutional rights to free speech and political participation.', '["First Amendment", "free speech protection", "political retaliation"]', '[{"name": "Constitution - First Amendment", "url": "https://constitution.congress.gov/browse/amendment-1/"}, {"name": "Supreme Court - Employee Speech Cases", "url": "https://supreme.justia.com/cases/federal/first-amendment/"}, {"name": "Congressional Research Service - Employee Speech Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R44426"}]');