INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-04-20-judicial-ethics-violations (Multiple Choice - 17 questions)
('2025-04-20-judicial-ethics-violations', 1, 'multiple_choice', 'Constitutional Law', 'What constitutional principle requires judges to be impartial?', 'No constitutional requirement for impartiality', 'Due process requires fair and impartial tribunals', 'Only legislative oversight', 'Executive branch supervision', 'Due process requires fair and impartial tribunals', 'Due process guarantees include the right to an impartial judge.', 'The Due Process Clauses of the Fifth and Fourteenth Amendments require fair and impartial tribunals, establishing a constitutional foundation for judicial ethics that ensures judges decide cases based on law and facts rather than personal interests or bias.', '["due process", "judicial impartiality", "constitutional requirements"]', '[{"name": "Supreme Court - Due Process and Fair Tribunals", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Congressional Research Service - Judicial Ethics", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 2, 'multiple_choice', 'Justice', 'What federal law governs ethics for federal judges?', 'No federal ethics law exists', 'Code of Conduct for United States Judges', 'Hatch Act', 'Civil Service Reform Act', 'Code of Conduct for United States Judges', 'This code establishes ethical standards for federal judicial conduct.', 'The Code of Conduct for United States Judges governs ethics for federal judges by establishing standards for integrity, independence, avoiding impropriety, performing judicial duties fairly, and regulating outside activities that might compromise judicial responsibilities.', '["Code of Conduct", "federal judicial ethics", "ethical standards"]', '[{"name": "Administrative Office of U.S. Courts - Code of Conduct", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Judicial Conference Ethics Committee", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/ethics-policies"}, {"name": "Congressional Research Service - Federal Judicial Ethics", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 3, 'multiple_choice', 'Justice', 'What types of conflicts of interest must judges avoid?', 'No conflicts need to be avoided', 'Financial interests, personal relationships, and bias-creating situations', 'Only criminal conflicts', 'Conflicts are only advisory concerns', 'Financial interests, personal relationships, and bias-creating situations', 'Judges must avoid conflicts that could compromise impartial decision-making.', 'Judges must avoid conflicts of interest including financial interests in case outcomes, personal relationships with parties or attorneys, family connections to cases, and any situations that could create bias or the appearance of bias in judicial decision-making.', '["conflicts of interest", "financial interests", "judicial bias"]', '[{"name": "Code of Conduct - Canon 3", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Ethics and Public Policy Center - Judicial Ethics", "url": "https://eppc.org/"}, {"name": "Congressional Research Service - Judicial Conflicts", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 4, 'multiple_choice', 'Justice', 'What enforcement mechanisms exist for judicial ethics violations?', 'No enforcement mechanisms', 'Judicial conduct committees, impeachment, and appellate review', 'Only congressional oversight', 'Executive branch discipline', 'Judicial conduct committees, impeachment, and appellate review', 'Multiple mechanisms ensure accountability for judicial misconduct.', 'Enforcement of judicial ethics includes judicial conduct committees that investigate complaints, impeachment for serious misconduct, appellate review of biased decisions, and administrative measures to ensure compliance with ethical standards and maintain public trust in the judiciary.', '["judicial conduct committees", "impeachment", "enforcement mechanisms"]', '[{"name": "U.S. Courts - Judicial Conduct and Disability", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/judicial-conduct-disability"}, {"name": "Congressional Research Service - Judicial Discipline", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}, {"name": "Administrative Office of U.S. Courts", "url": "https://www.uscourts.gov/"}]'),

('2025-04-20-judicial-ethics-violations', 5, 'multiple_choice', 'Constitutional Law', 'How does judicial independence relate to ethics enforcement?', 'Independence prevents all ethics enforcement', 'Independence must be balanced with accountability for misconduct', 'Ethics enforcement eliminates independence', 'No relationship exists', 'Independence must be balanced with accountability for misconduct', 'Judicial independence and accountability must be carefully balanced.', 'Judicial independence must be balanced with accountability for misconduct. While judges need independence to make impartial decisions, they must also be held accountable for ethical violations through appropriate mechanisms that protect both judicial independence and public trust.', '["judicial independence", "accountability balance", "ethical enforcement"]', '[{"name": "Federal Judicial Center - Judicial Independence", "url": "https://www.fjc.gov/"}, {"name": "American Bar Association - Judicial Independence", "url": "https://www.americanbar.org/groups/judicial/"}, {"name": "Congressional Research Service - Judicial Independence", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 6, 'multiple_choice', 'Justice', 'What disclosure requirements apply to federal judges?', 'No disclosure requirements', 'Financial disclosure reports and recusal when appropriate', 'Only voluntary disclosure', 'Complete transparency of all activities', 'Financial disclosure reports and recusal when appropriate', 'Transparency requirements help identify and prevent conflicts of interest.', 'Federal judges must file annual financial disclosure reports detailing investments, income, and potential conflicts, and must recuse themselves from cases where they have financial interests or other conflicts that could compromise impartial judgment.', '["financial disclosure", "recusal requirements", "transparency"]', '[{"name": "Ethics in Government Act - Judicial Disclosure", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Financial+Disclosure"}, {"name": "Administrative Office of U.S. Courts - Financial Disclosure", "url": "https://www.uscourts.gov/"}, {"name": "Congressional Research Service - Judicial Disclosure", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 7, 'multiple_choice', 'Justice', 'What restrictions exist on judges\' outside activities?', 'No restrictions on outside activities', 'Limitations on business, political, and speaking activities that could compromise judicial duties', 'Complete prohibition of all outside activities', 'Only criminal activities are restricted', 'Limitations on business, political, and speaking activities that could compromise judicial duties', 'Outside activities must not compromise judicial responsibilities or independence.', 'Judges face restrictions on outside activities including limitations on business involvement, political activities, partisan speaking engagements, and other activities that could compromise judicial duties, create conflicts of interest, or undermine public confidence in judicial impartiality.', '["outside activities", "political restrictions", "business limitations"]', '[{"name": "Code of Conduct - Canon 4", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Judicial Ethics Committee Guidelines", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/ethics-policies"}, {"name": "Congressional Research Service - Judicial Activities", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 8, 'multiple_choice', 'Constitutional Law', 'What constitutional remedy exists for serious judicial misconduct?', 'No constitutional remedy', 'Impeachment by House and removal by Senate', 'Executive branch removal', 'Automatic term expiration', 'Impeachment by House and removal by Senate', 'Article II provides the impeachment process for federal judges.', 'The Constitution provides impeachment as the remedy for serious judicial misconduct, with the House having the power to impeach federal judges for "high Crimes and Misdemeanors" and the Senate having the power to try and remove judges from office.', '["impeachment process", "constitutional remedy", "judicial removal"]', '[{"name": "Constitution - Impeachment Provisions", "url": "https://constitution.congress.gov/browse/essay/artII-S4-1/"}, {"name": "Congressional Research Service - Judicial Impeachment", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}, {"name": "House Judiciary Committee - Impeachment", "url": "https://judiciary.house.gov/"}]'),

('2025-04-20-judicial-ethics-violations', 9, 'multiple_choice', 'Justice', 'How do appearance standards affect judicial ethics?', 'Appearance standards don\'t apply to judges', 'Judges must avoid even the appearance of impropriety', 'Only actual misconduct matters', 'Appearance is purely subjective', 'Judges must avoid even the appearance of impropriety', 'Maintaining public confidence requires avoiding apparent as well as actual impropriety.', 'Judicial ethics require judges to avoid even the appearance of impropriety because public confidence in the judiciary depends not only on actual impartiality but also on the public perception that judges are fair, unbiased, and free from conflicts of interest.', '["appearance of impropriety", "public confidence", "ethical standards"]', '[{"name": "Code of Conduct - Canon 2", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "American Bar Association - Model Code", "url": "https://www.americanbar.org/groups/professional_responsibility/"}, {"name": "Congressional Research Service - Judicial Appearance Standards", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 10, 'multiple_choice', 'Justice', 'What role do judicial conduct committees play?', 'No role in judicial oversight', 'Investigate complaints and recommend disciplinary action', 'Only advisory functions', 'Criminal prosecution authority', 'Investigate complaints and recommend disciplinary action', 'These committees provide oversight and accountability for judicial conduct.', 'Judicial conduct committees investigate complaints against judges, review allegations of misconduct, conduct hearings when appropriate, and recommend disciplinary actions ranging from private reprimands to recommendations for impeachment in serious cases.', '["conduct committees", "complaint investigation", "disciplinary recommendations"]', '[{"name": "U.S. Courts - Judicial Conduct Process", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/judicial-conduct-disability"}, {"name": "Rules for Judicial-Conduct and Judicial-Disability Proceedings", "url": "https://www.uscourts.gov/sites/default/files/jcdr-2019.pdf"}, {"name": "Congressional Research Service - Judicial Discipline", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 11, 'multiple_choice', 'Constitutional Law', 'How do ethics violations affect judicial decisions?', 'Ethics violations don\'t affect decisions', 'Violations can lead to recusal requirements or case reversals', 'Ethics only affect future cases', 'Violations automatically void all decisions', 'Violations can lead to recusal requirements or case reversals', 'Ethical violations can compromise the validity of judicial decisions.', 'Ethics violations can affect judicial decisions by requiring recusal from conflicted cases, potentially leading to reversal of decisions made with conflicts of interest, and undermining the legitimacy of judicial rulings when impartiality is compromised.', '["decision validity", "recusal requirements", "case reversals"]', '[{"name": "Supreme Court - Judicial Bias Cases", "url": "https://supreme.justia.com/cases/federal/judicial-bias/"}, {"name": "Federal Rules of Civil Procedure - Judicial Disqualification", "url": "https://www.law.cornell.edu/rules/frcp"}, {"name": "Congressional Research Service - Ethics and Decisions", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 12, 'multiple_choice', 'Justice', 'What transparency measures help prevent judicial ethics violations?', 'No transparency measures exist', 'Public financial disclosures and open complaint processes', 'Complete secrecy requirements', 'Only internal monitoring', 'Public financial disclosures and open complaint processes', 'Transparency helps prevent conflicts and maintains public trust.', 'Transparency measures that help prevent judicial ethics violations include public financial disclosure requirements, open processes for filing ethics complaints, public reporting of disciplinary actions, and clear ethical guidelines that inform both judges and the public about expected standards.', '["transparency measures", "public disclosure", "complaint processes"]', '[{"name": "Ethics in Government Act", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Ethics+in+Government+Act"}, {"name": "Administrative Office of U.S. Courts - Transparency", "url": "https://www.uscourts.gov/"}, {"name": "Congressional Research Service - Judicial Transparency", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 13, 'multiple_choice', 'Constitutional Law', 'How do ethics requirements interact with First Amendment rights of judges?', 'Ethics requirements eliminate First Amendment rights', 'Ethics restrictions must be balanced with free speech rights', 'First Amendment provides unlimited judicial speech rights', 'No interaction between ethics and speech rights', 'Ethics restrictions must be balanced with free speech rights', 'Constitutional rights must be balanced with judicial responsibilities.', 'Ethics requirements for judges must be balanced with First Amendment rights, allowing appropriate restrictions on speech and activities that could compromise judicial duties while preserving core constitutional rights within the context of judicial responsibilities and public trust.', '["First Amendment balance", "judicial speech rights", "constitutional balance"]', '[{"name": "Supreme Court - Judicial Speech Cases", "url": "https://supreme.justia.com/cases/federal/first-amendment/"}, {"name": "American Bar Association - Judicial Speech", "url": "https://www.americanbar.org/groups/judicial/"}, {"name": "Congressional Research Service - Judicial First Amendment", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 14, 'multiple_choice', 'Justice', 'What standards apply to judicial gifts and benefits?', 'No restrictions on gifts', 'Strict limitations on gifts that could influence decisions or create conflicts', 'Unlimited gifts allowed', 'Only valuable gifts are restricted', 'Strict limitations on gifts that could influence decisions or create conflicts', 'Gift restrictions prevent conflicts of interest and maintain judicial integrity.', 'Judicial ethics impose strict limitations on gifts and benefits that judges may accept, prohibiting gifts that could influence judicial decisions, create conflicts of interest, or give the appearance of impropriety, with limited exceptions for personal relationships and ceremonial gifts.', '["gift restrictions", "conflict prevention", "judicial integrity"]', '[{"name": "Code of Conduct - Gift Provisions", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Ethics Committee Advisory Opinions", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/ethics-policies"}, {"name": "Congressional Research Service - Judicial Gifts", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 15, 'multiple_choice', 'Civic Participation', 'How can citizens report suspected judicial ethics violations?', 'Citizens cannot report judicial violations', 'Through formal complaint processes with judicial conduct committees', 'Only through media reporting', 'Direct contact with judges required', 'Through formal complaint processes with judicial conduct committees', 'Citizens have formal channels for reporting judicial misconduct.', 'Citizens can report suspected judicial ethics violations through formal complaint processes established by judicial conduct committees, which investigate allegations of misconduct and ensure accountability while protecting both judicial independence and public trust in the judiciary.', '["citizen complaints", "reporting processes", "judicial accountability"]', '[{"name": "U.S. Courts - How to File a Complaint", "url": "https://www.uscourts.gov/judges-judgeships/judicial-conduct-disability/how-file-complaint"}, {"name": "Judicial Conduct Rules", "url": "https://www.uscourts.gov/sites/default/files/jcdr-2019.pdf"}, {"name": "Congressional Research Service - Citizen Complaints", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 16, 'multiple_choice', 'Justice', 'What training and education requirements exist for judicial ethics?', 'No training requirements exist', 'Ongoing ethics education and training programs', 'One-time training only', 'Ethics training is optional', 'Ongoing ethics education and training programs', 'Continuous education helps judges understand and comply with ethical requirements.', 'Federal judges participate in ongoing ethics education and training programs that cover ethical requirements, conflict identification, disclosure obligations, and best practices for maintaining judicial integrity and avoiding ethical violations throughout their judicial careers.', '["ethics education", "judicial training", "continuous learning"]', '[{"name": "Federal Judicial Center - Ethics Training", "url": "https://www.fjc.gov/"}, {"name": "Administrative Office of U.S. Courts - Education", "url": "https://www.uscourts.gov/"}, {"name": "Congressional Research Service - Judicial Education", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 17, 'multiple_choice', 'Constitutional Law', 'How do judicial ethics violations affect public trust in the legal system?', 'Ethics violations have no effect on public trust', 'Violations significantly undermine confidence in judicial fairness and integrity', 'Violations always improve public understanding', 'Public trust is unrelated to judicial conduct', 'Violations significantly undermine confidence in judicial fairness and integrity', 'Public trust in the judiciary depends on perceptions of judicial integrity and impartiality.', 'Judicial ethics violations significantly undermine public trust in the legal system by creating doubt about judicial fairness, impartiality, and integrity, potentially compromising the legitimacy of court decisions and the rule of law in democratic society.', '["public trust", "judicial legitimacy", "democratic confidence"]', '[{"name": "American Bar Association - Public Trust Studies", "url": "https://www.americanbar.org/"}, {"name": "National Center for State Courts - Public Trust", "url": "https://www.ncsc.org/"}, {"name": "Congressional Research Service - Judicial Trust", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

-- True/False Questions (5 questions)
('2025-04-20-judicial-ethics-violations', 18, 'true_false', 'Justice', 'Federal judges must recuse themselves from cases where they have financial interests.', '', '', '', '', 'True', 'Financial conflicts of interest require judicial recusal to ensure impartiality.', 'True. Federal judges must recuse themselves from cases where they have financial interests that could be affected by the outcome, ensuring impartiality and maintaining public confidence in judicial decision-making.', '["recusal requirements", "financial conflicts", "judicial impartiality"]', '[{"name": "28 USC 455 - Judicial Disqualification", "url": "https://www.law.cornell.edu/uscode/text/28/455"}, {"name": "Code of Conduct for U.S. Judges", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Congressional Research Service - Judicial Recusal", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 19, 'true_false', 'Constitutional Law', 'Judges can be impeached and removed for ethical violations that constitute high crimes and misdemeanors.', '', '', '', '', 'True', 'The Constitution provides impeachment as the remedy for serious judicial misconduct.', 'True. Federal judges can be impeached by the House and removed by the Senate for ethical violations that constitute "high Crimes and Misdemeanors," providing a constitutional mechanism for addressing serious judicial misconduct.', '["impeachment", "judicial removal", "constitutional remedy"]', '[{"name": "Constitution - Impeachment Provisions", "url": "https://constitution.congress.gov/browse/essay/artII-S4-1/"}, {"name": "Congressional Research Service - Judicial Impeachment", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}, {"name": "House Judiciary Committee", "url": "https://judiciary.house.gov/"}]'),

('2025-04-20-judicial-ethics-violations', 20, 'true_false', 'Justice', 'Judges have unlimited freedom to engage in political activities outside their judicial duties.', '', '', '', '', 'False', 'Judicial ethics impose restrictions on political activities to maintain impartiality.', 'False. Judges face significant restrictions on political activities including campaigning, partisan speaking, and political endorsements to maintain judicial impartiality and avoid conflicts that could compromise their judicial duties.', '["political restrictions", "judicial neutrality", "ethical limitations"]', '[{"name": "Code of Conduct - Canon 5", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Judicial Ethics Committee Guidelines", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/ethics-policies"}, {"name": "Congressional Research Service - Judicial Political Activity", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 21, 'true_false', 'Justice', 'Citizens can file complaints against federal judges for alleged ethics violations.', '', '', '', '', 'True', 'Formal complaint processes exist for reporting judicial misconduct.', 'True. Citizens can file formal complaints against federal judges for alleged ethics violations through established judicial conduct committees, which investigate complaints and recommend appropriate disciplinary actions when misconduct is found.', '["citizen complaints", "misconduct reporting", "judicial accountability"]', '[{"name": "U.S. Courts - Filing Complaints", "url": "https://www.uscourts.gov/judges-judgeships/judicial-conduct-disability/how-file-complaint"}, {"name": "Judicial Conduct and Disability Rules", "url": "https://www.uscourts.gov/sites/default/files/jcdr-2019.pdf"}, {"name": "Congressional Research Service - Complaint Process", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 22, 'true_false', 'Constitutional Law', 'Due process requires judges to be impartial and free from bias.', '', '', '', '', 'True', 'Constitutional due process guarantees include the right to an impartial tribunal.', 'True. Due process under the Fifth and Fourteenth Amendments requires judges to be impartial and free from bias, establishing a constitutional foundation for judicial ethics and ensuring fair treatment in legal proceedings.', '["due process", "judicial impartiality", "constitutional requirements"]', '[{"name": "Supreme Court - Due Process Cases", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Congressional Research Service - Due Process and Judges", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

-- Short Answer Questions (3 questions)
('2025-04-20-judicial-ethics-violations', 23, 'short_answer', 'Justice', 'What federal code governs ethical conduct for United States judges?', '', '', '', '', 'Code of Conduct for United States Judges', 'This code establishes ethical standards and guidelines for federal judicial behavior.', 'The Code of Conduct for United States Judges governs ethical conduct for federal judges, establishing standards for integrity, independence, avoiding impropriety, performing judicial duties fairly, and regulating outside activities.', '["Code of Conduct", "judicial ethics", "federal standards"]', '[{"name": "Administrative Office of U.S. Courts - Code of Conduct", "url": "https://www.uscourts.gov/judges-judgeships/code-conduct-united-states-judges"}, {"name": "Judicial Conference Ethics Policies", "url": "https://www.uscourts.gov/rules-policies/judiciary-policies/ethics-policies"}, {"name": "Congressional Research Service - Judicial Ethics Code", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]'),

('2025-04-20-judicial-ethics-violations', 24, 'short_answer', 'Constitutional Law', 'What constitutional process can remove federal judges for serious misconduct?', '', '', '', '', 'Impeachment', 'This process involves the House and Senate removing judges for high crimes and misdemeanors.', 'Impeachment is the constitutional process for removing federal judges for serious misconduct, with the House having the power to impeach and the Senate having the power to try and remove judges from office.', '["impeachment", "judicial removal", "constitutional process"]', '[{"name": "Constitution - Impeachment Provisions", "url": "https://constitution.congress.gov/browse/essay/artII-S4-1/"}, {"name": "Congressional Research Service - Judicial Impeachment", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}, {"name": "House Judiciary Committee", "url": "https://judiciary.house.gov/"}]'),

('2025-04-20-judicial-ethics-violations', 25, 'short_answer', 'Constitutional Law', 'What constitutional principle requires judges to provide fair and impartial tribunals?', '', '', '', '', 'Due process', 'This constitutional guarantee ensures fair treatment in legal proceedings.', 'Due process under the Fifth and Fourteenth Amendments requires judges to provide fair and impartial tribunals, establishing the constitutional foundation for judicial ethics and ensuring unbiased decision-making in legal proceedings.', '["due process", "fair tribunals", "constitutional guarantee"]', '[{"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Supreme Court - Due Process and Fair Tribunals", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Congressional Research Service - Due Process Requirements", "url": "https://crsreports.congress.gov/product/pdf/R/R45175"}]');