INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-03-30-saudi-ai-deals (Multiple Choice - 17 questions)
('2025-03-30-saudi-ai-deals', 1, 'multiple_choice', 'Foreign Policy', 'What country did the Trump administration sign major AI partnerships with in 2025?', 'China', 'Saudi Arabia', 'Russia', 'Iran', 'Saudi Arabia', 'This Middle Eastern kingdom is led by Crown Prince Mohammed bin Salman.', 'The Trump administration signed major artificial intelligence development agreements with Saudi Arabia in 2025. These partnerships involve AI technology sharing and cooperation despite ongoing human rights concerns about the Saudi government\'s record.', '["Saudi Arabia", "AI partnerships", "technology cooperation"]', '[{"name": "U.S. Department of State - Saudi Arabia Relations", "url": "https://www.state.gov/countries-areas/saudi-arabia/"}, {"name": "Reuters - US-Saudi AI Partnership", "url": "https://www.reuters.com/technology/artificial-intelligence/"}, {"name": "Congressional Research Service - US-Saudi Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46365"}]'),

('2025-03-30-saudi-ai-deals', 2, 'multiple_choice', 'National Security', 'What national security concerns arise from AI technology sharing with authoritarian allies?', 'No security concerns exist', 'Risk of technology misuse for surveillance and oppression', 'Enhanced global security', 'Improved democratic governance', 'Risk of technology misuse for surveillance and oppression', 'AI technology can be used to monitor and control populations.', 'AI technology sharing with authoritarian allies raises national security concerns about potential misuse for mass surveillance, political oppression, and human rights violations. Advanced AI capabilities can enhance authoritarian control over populations and dissent.', '["national security", "AI misuse", "surveillance technology"]', '[{"name": "National Security Agency - AI Security", "url": "https://www.nsa.gov/What-We-Do/Cybersecurity/"}, {"name": "Congressional Research Service - AI National Security", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}, {"name": "Center for Strategic and International Studies", "url": "https://www.csis.org/programs/strategic-technologies-program"}]'),

('2025-03-30-saudi-ai-deals', 3, 'multiple_choice', 'Government', 'What human rights concerns exist regarding Saudi Arabia\'s government?', 'No human rights concerns exist', 'Suppression of dissent, lack of press freedom, and treatment of activists', 'Exemplary human rights record', 'Only economic concerns matter', 'Suppression of dissent, lack of press freedom, and treatment of activists', 'International organizations document significant human rights issues in Saudi Arabia.', 'Human rights concerns regarding Saudi Arabia include suppression of political dissent, severe restrictions on press freedom, treatment of women\'s rights activists, and the killing of journalist Jamal Khashoggi. These issues raise questions about partnering with the Saudi government.', '["human rights", "political dissent", "press freedom"]', '[{"name": "Human Rights Watch - Saudi Arabia", "url": "https://www.hrw.org/middle-east/north-africa/saudi-arabia"}, {"name": "Amnesty International - Saudi Arabia", "url": "https://www.amnesty.org/en/location/middle-east-and-north-africa/middle-east/saudi-arabia/"}, {"name": "U.S. State Department - Human Rights Report", "url": "https://www.state.gov/reports/2023-country-reports-on-human-rights-practices/"}]'),

('2025-03-30-saudi-ai-deals', 4, 'multiple_choice', 'Foreign Policy', 'How do economic interests conflict with human rights concerns in foreign policy?', 'They never conflict', 'Economic partnerships may compromise criticism of human rights violations', 'Economic interests always take priority', 'Human rights always take priority', 'Economic partnerships may compromise criticism of human rights violations', 'Countries often face difficult choices between economic and moral considerations.', 'Economic interests can conflict with human rights concerns when lucrative partnerships discourage criticism of human rights violations. Countries may prioritize trade relationships and economic benefits over consistent advocacy for democratic values and human rights.', '["economic vs human rights", "foreign policy dilemmas", "moral considerations"]', '[{"name": "Council on Foreign Relations - Human Rights and Foreign Policy", "url": "https://www.cfr.org/backgrounder/role-human-rights-us-foreign-policy"}, {"name": "Brookings Institution - Economic Diplomacy", "url": "https://www.brookings.edu/research/economic-diplomacy/"}, {"name": "Congressional Research Service - Human Rights in Foreign Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}]'),

('2025-03-30-saudi-ai-deals', 5, 'multiple_choice', 'National Security', 'What strategic implications exist for AI cooperation with foreign governments?', 'No strategic implications', 'Technology dependence, security vulnerabilities, and competitive advantages', 'Only positive outcomes', 'Purely domestic effects', 'Technology dependence, security vulnerabilities, and competitive advantages', 'AI cooperation creates complex strategic relationships and dependencies.', 'AI cooperation with foreign governments creates strategic implications including potential technology dependence, security vulnerabilities from shared systems, and questions about competitive advantages in critical emerging technologies that affect national security.', '["strategic implications", "technology dependence", "competitive advantages"]', '[{"name": "National Security Commission on AI", "url": "https://www.nscai.gov/"}, {"name": "Department of Defense - AI Strategy", "url": "https://www.defense.gov/News/Releases/artificial-intelligence/"}, {"name": "Congressional Research Service - AI Competition", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 6, 'multiple_choice', 'Government', 'What congressional oversight exists for international AI agreements?', 'No congressional oversight', 'Committee hearings, treaty ratification, and funding authorization', 'Only executive oversight', 'State government oversight only', 'Committee hearings, treaty ratification, and funding authorization', 'Congress has multiple tools for overseeing international agreements.', 'Congressional oversight of international AI agreements includes committee hearings on foreign policy implications, Senate ratification for treaties, appropriations control over funding, and investigation of national security concerns related to technology sharing.', '["congressional oversight", "committee hearings", "treaty ratification"]', '[{"name": "House Foreign Affairs Committee", "url": "https://foreignaffairs.house.gov/"}, {"name": "Senate Foreign Relations Committee", "url": "https://www.foreign.senate.gov/"}, {"name": "Congressional Research Service - Congressional Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

('2025-03-30-saudi-ai-deals', 7, 'multiple_choice', 'Foreign Policy', 'How do AI partnerships affect broader Middle East diplomatic relationships?', 'They have no effect on regional diplomacy', 'They can influence regional balance of power and alliance structures', 'They only affect bilateral relations', 'They improve all regional relationships equally', 'They can influence regional balance of power and alliance structures', 'Technology partnerships often have wider regional implications.', 'AI partnerships can affect broader Middle East diplomacy by influencing regional balance of power, affecting alliance structures between countries, and potentially causing concern among other regional partners about technology advantages and strategic relationships.', '["Middle East diplomacy", "regional balance", "alliance structures"]', '[{"name": "U.S. Department of State - Middle East Policy", "url": "https://www.state.gov/policy-issues/middle-east-and-north-africa/"}, {"name": "Council on Foreign Relations - Middle East", "url": "https://www.cfr.org/regions/middle-east"}, {"name": "Congressional Research Service - Middle East Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46365"}]'),

('2025-03-30-saudi-ai-deals', 8, 'multiple_choice', 'National Security', 'What export controls typically govern AI technology transfers?', 'No export controls exist', 'International Traffic in Arms Regulations and Export Administration Regulations', 'Only domestic regulations', 'Complete prohibition on all transfers', 'International Traffic in Arms Regulations and Export Administration Regulations', 'Federal regulations control the export of sensitive technologies.', 'AI technology transfers are governed by export controls including International Traffic in Arms Regulations (ITAR) and Export Administration Regulations (EAR) that restrict transfer of sensitive technologies based on national security considerations and recipient countries.', '["export controls", "ITAR", "Export Administration Regulations"]', '[{"name": "Bureau of Industry and Security - Export Controls", "url": "https://www.bis.doc.gov/index.php/regulations/export-administration-regulations-ear"}, {"name": "State Department - ITAR", "url": "https://www.pmddtc.state.gov/ddtc_public"}, {"name": "Congressional Research Service - Export Controls", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 9, 'multiple_choice', 'Government', 'How do international AI agreements affect domestic technology development?', 'They have no domestic effects', 'They can influence research priorities, funding, and innovation directions', 'They only affect foreign companies', 'They always benefit domestic development', 'They can influence research priorities, funding, and innovation directions', 'International partnerships often reshape domestic technology landscapes.', 'International AI agreements can affect domestic technology development by influencing research priorities, directing government funding toward partnership areas, and potentially creating dependencies that affect innovation directions and competitive advantages for domestic companies.', '["domestic technology", "research priorities", "innovation directions"]', '[{"name": "National Science Foundation - AI Research", "url": "https://www.nsf.gov/cise/ai/"}, {"name": "Department of Energy - AI Research", "url": "https://www.energy.gov/artificial-intelligence"}, {"name": "Congressional Research Service - Domestic AI Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 10, 'multiple_choice', 'Foreign Policy', 'What role does economic diplomacy play in AI partnerships?', 'No role in AI partnerships', 'Economic incentives can drive technology cooperation despite other concerns', 'Only military considerations matter', 'Cultural factors are the only driver', 'Economic incentives can drive technology cooperation despite other concerns', 'Economic benefits often influence foreign policy decisions.', 'Economic diplomacy plays a significant role in AI partnerships by creating financial incentives for technology cooperation, trade opportunities, and investment relationships that can override other foreign policy concerns including human rights considerations.', '["economic diplomacy", "financial incentives", "trade opportunities"]', '[{"name": "U.S. Chamber of Commerce - International Trade", "url": "https://www.uschamber.com/international"}, {"name": "Brookings Institution - Economic Diplomacy", "url": "https://www.brookings.edu/research/economic-diplomacy/"}, {"name": "Congressional Research Service - Economic Statecraft", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}]'),

('2025-03-30-saudi-ai-deals', 11, 'multiple_choice', 'National Security', 'How do AI partnerships with authoritarian governments affect democratic values promotion?', 'They strengthen democratic values', 'They can undermine credibility in promoting democracy and human rights', 'They have no effect on values promotion', 'They only affect economic relations', 'They can undermine credibility in promoting democracy and human rights', 'Partnerships with authoritarian regimes can compromise moral authority.', 'AI partnerships with authoritarian governments can undermine credibility in promoting democratic values by appearing to prioritize economic and strategic interests over human rights concerns, potentially weakening moral authority in international democracy promotion efforts.', '["democratic values", "moral authority", "credibility"]', '[{"name": "Freedom House - Democracy Promotion", "url": "https://freedomhouse.org/"}, {"name": "National Endowment for Democracy", "url": "https://www.ned.org/"}, {"name": "Congressional Research Service - Democracy Promotion", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}]'),

('2025-03-30-saudi-ai-deals', 12, 'multiple_choice', 'Government', 'What transparency requirements exist for international technology agreements?', 'No transparency requirements', 'Congressional notification, public disclosure, and oversight reporting', 'Complete secrecy required', 'Only executive branch access', 'Congressional notification, public disclosure, and oversight reporting', 'Democratic oversight requires transparency in international agreements.', 'International technology agreements are subject to transparency requirements including congressional notification of significant agreements, public disclosure of general terms, and oversight reporting to ensure democratic accountability and public understanding of government commitments.', '["transparency requirements", "congressional notification", "public disclosure"]', '[{"name": "Congressional Research Service - International Agreements", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}, {"name": "Government Accountability Office - International Agreements", "url": "https://www.gao.gov/products/international-agreements"}, {"name": "House Foreign Affairs Committee", "url": "https://foreignaffairs.house.gov/"}]'),

('2025-03-30-saudi-ai-deals', 13, 'multiple_choice', 'National Security', 'What intelligence sharing concerns arise from AI partnerships?', 'No intelligence concerns exist', 'Risk of exposing sensitive capabilities and sources', 'Enhanced intelligence cooperation only', 'Purely beneficial outcomes', 'Risk of exposing sensitive capabilities and sources', 'AI partnerships can reveal sensitive intelligence capabilities and methods.', 'AI partnerships raise intelligence sharing concerns about potential exposure of sensitive intelligence capabilities, sources, and methods when collaborative technology development might reveal classified information or advanced intelligence techniques to foreign partners.', '["intelligence sharing", "sensitive capabilities", "classified information"]', '[{"name": "Director of National Intelligence", "url": "https://www.dni.gov/"}, {"name": "Central Intelligence Agency - Technology", "url": "https://www.cia.gov/what-we-do/technology/"}, {"name": "Congressional Research Service - Intelligence Sharing", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 14, 'multiple_choice', 'Foreign Policy', 'How do AI deals affect relationships with traditional democratic allies?', 'They strengthen all relationships equally', 'They may strain relationships with allies concerned about authoritarian partnerships', 'They have no effect on ally relationships', 'They only improve relationships', 'They may strain relationships with allies concerned about authoritarian partnerships', 'Democratic allies may have concerns about partnerships with authoritarian regimes.', 'AI deals with authoritarian governments may strain relationships with traditional democratic allies who have concerns about legitimizing authoritarian regimes, technology sharing that could be misused, and potential shifts away from values-based foreign policy.', '["democratic allies", "authoritarian partnerships", "values-based policy"]', '[{"name": "NATO - Technology Cooperation", "url": "https://www.nato.int/cps/en/natohq/topics_184303.htm"}, {"name": "Council on Foreign Relations - Alliance Management", "url": "https://www.cfr.org/backgrounder/nato-summit"}, {"name": "Congressional Research Service - Alliance Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46365"}]'),

('2025-03-30-saudi-ai-deals', 15, 'multiple_choice', 'Government', 'What constitutional authority governs international technology agreements?', 'No constitutional authority needed', 'Presidential foreign affairs power and congressional commerce authority', 'Only state government authority', 'Judicial branch authority exclusively', 'Presidential foreign affairs power and congressional commerce authority', 'Multiple constitutional provisions govern international agreements.', 'International technology agreements are governed by presidential foreign affairs power for conducting international relations and congressional authority over interstate and foreign commerce, creating shared constitutional responsibility for technology cooperation agreements.', '["presidential foreign affairs", "congressional commerce power", "constitutional authority"]', '[{"name": "Constitution - Foreign Affairs Powers", "url": "https://constitution.congress.gov/browse/essay/artII-S2-C2-1/"}, {"name": "Congressional Research Service - Constitutional Foreign Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}, {"name": "Supreme Court - Foreign Affairs Cases", "url": "https://supreme.justia.com/cases/federal/foreign-affairs/"}]'),

('2025-03-30-saudi-ai-deals', 16, 'multiple_choice', 'National Security', 'How do AI partnerships affect global technology competition?', 'They have no competitive effects', 'They can shift global AI leadership and influence technological standards', 'They only affect domestic markets', 'They eliminate all competition', 'They can shift global AI leadership and influence technological standards', 'AI partnerships affect the global competitive landscape for emerging technologies.', 'AI partnerships can significantly affect global technology competition by shifting AI leadership positions, influencing international technological standards, and creating new competitive alliances that may advantage some countries while disadvantaging others in critical technologies.', '["global competition", "AI leadership", "technological standards"]', '[{"name": "National Security Commission on AI", "url": "https://www.nscai.gov/"}, {"name": "Department of Commerce - Technology Competition", "url": "https://www.commerce.gov/issues/technology"}, {"name": "Congressional Research Service - Technology Competition", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 17, 'multiple_choice', 'Foreign Policy', 'What precedent do AI partnerships with authoritarian allies set for other countries?', 'No precedent is set', 'They may encourage other democracies to prioritize economic over human rights concerns', 'They discourage all international cooperation', 'They only affect bilateral relationships', 'They may encourage other democracies to prioritize economic over human rights concerns', 'Foreign policy decisions often influence other countries\' behavior.', 'AI partnerships with authoritarian allies may set precedent encouraging other democracies to prioritize economic and strategic interests over human rights concerns, potentially weakening international commitment to values-based foreign policy and human rights advocacy.', '["foreign policy precedent", "international behavior", "values-based policy"]', '[{"name": "Council on Foreign Relations - International Norms", "url": "https://www.cfr.org/"}, {"name": "Brookings Institution - Foreign Policy", "url": "https://www.brookings.edu/research/foreign-policy/"}, {"name": "Congressional Research Service - International Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}]'),

-- True/False Questions (5 questions)
('2025-03-30-saudi-ai-deals', 18, 'true_false', 'Foreign Policy', 'Economic partnerships always take priority over human rights concerns in foreign policy.', '', '', '', '', 'False', 'Foreign policy involves balancing multiple competing interests and values.', 'False. While economic interests are important in foreign policy, they do not always take priority over human rights concerns. Democratic countries often struggle to balance economic benefits with moral values and human rights advocacy in their international relationships.', '["economic vs human rights", "foreign policy balance", "moral considerations"]', '[{"name": "Council on Foreign Relations - Human Rights Policy", "url": "https://www.cfr.org/backgrounder/role-human-rights-us-foreign-policy"}, {"name": "Congressional Research Service - Human Rights in Foreign Policy", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}, {"name": "Brookings Institution - Values in Foreign Policy", "url": "https://www.brookings.edu/research/foreign-policy/"}]'),

('2025-03-30-saudi-ai-deals', 19, 'true_false', 'National Security', 'AI technology sharing with foreign governments has no national security implications.', '', '', '', '', 'False', 'AI technology has significant national security and strategic implications.', 'False. AI technology sharing with foreign governments has major national security implications including potential misuse for surveillance, competitive advantages in critical technologies, and risks of exposing sensitive capabilities or creating technological dependencies.', '["AI security", "technology sharing", "national security implications"]', '[{"name": "National Security Commission on AI", "url": "https://www.nscai.gov/"}, {"name": "Congressional Research Service - AI National Security", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}, {"name": "Department of Defense - AI Strategy", "url": "https://www.defense.gov/artificial-intelligence/"}]'),

('2025-03-30-saudi-ai-deals', 20, 'true_false', 'Government', 'Congress has no oversight role in international technology agreements.', '', '', '', '', 'False', 'Congress has constitutional oversight authority over international agreements.', 'False. Congress has significant oversight role in international technology agreements through committee hearings, treaty ratification authority, appropriations control, and investigation of agreements\' implications for national security and foreign policy.', '["congressional oversight", "international agreements", "constitutional authority"]', '[{"name": "House Foreign Affairs Committee", "url": "https://foreignaffairs.house.gov/"}, {"name": "Senate Foreign Relations Committee", "url": "https://www.foreign.senate.gov/"}, {"name": "Congressional Research Service - Congressional Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

('2025-03-30-saudi-ai-deals', 21, 'true_false', 'Foreign Policy', 'Partnerships with authoritarian governments can undermine credibility in promoting democratic values.', '', '', '', '', 'True', 'Actions often speak louder than words in international relations.', 'True. Partnerships with authoritarian governments can undermine credibility in promoting democratic values by appearing to prioritize strategic or economic interests over human rights concerns, potentially weakening moral authority in international democracy promotion efforts.', '["democratic credibility", "authoritarian partnerships", "moral authority"]', '[{"name": "Freedom House - Democracy Reports", "url": "https://freedomhouse.org/"}, {"name": "National Endowment for Democracy", "url": "https://www.ned.org/"}, {"name": "Congressional Research Service - Democracy Promotion", "url": "https://crsreports.congress.gov/product/pdf/R/R46456"}]'),

('2025-03-30-saudi-ai-deals', 22, 'true_false', 'National Security', 'Export controls regulate the transfer of sensitive AI technologies to foreign countries.', '', '', '', '', 'True', 'Federal regulations control the export of technologies with national security implications.', 'True. Export controls including International Traffic in Arms Regulations (ITAR) and Export Administration Regulations (EAR) regulate the transfer of sensitive AI technologies to foreign countries based on national security considerations and the nature of recipient countries.', '["export controls", "technology transfer", "national security regulations"]', '[{"name": "Bureau of Industry and Security", "url": "https://www.bis.doc.gov/"}, {"name": "State Department - ITAR", "url": "https://www.pmddtc.state.gov/"}, {"name": "Congressional Research Service - Export Controls", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

-- Short Answer Questions (3 questions)
('2025-03-30-saudi-ai-deals', 23, 'short_answer', 'Foreign Policy', 'What country did the Trump administration sign major AI partnership agreements with in 2025?', '', '', '', '', 'Saudi Arabia', 'This Middle Eastern kingdom is led by Crown Prince Mohammed bin Salman.', 'The Trump administration signed major AI partnership agreements with Saudi Arabia in 2025. These agreements involve artificial intelligence development cooperation and technology sharing despite ongoing human rights concerns about the Saudi government.', '["Saudi Arabia", "AI partnerships", "Trump administration"]', '[{"name": "U.S. Department of State - Saudi Relations", "url": "https://www.state.gov/countries-areas/saudi-arabia/"}, {"name": "Reuters - US-Saudi AI Deal", "url": "https://www.reuters.com/"}, {"name": "Congressional Research Service - US-Saudi Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46365"}]'),

('2025-03-30-saudi-ai-deals', 24, 'short_answer', 'National Security', 'What federal regulations typically control the export of sensitive AI technologies?', '', '', '', '', 'Export controls', 'These include ITAR and Export Administration Regulations.', 'Export controls typically govern the transfer of sensitive AI technologies to foreign countries. These include International Traffic in Arms Regulations (ITAR) and Export Administration Regulations (EAR) that restrict technology transfers based on national security considerations.', '["export controls", "ITAR", "technology regulations"]', '[{"name": "Bureau of Industry and Security", "url": "https://www.bis.doc.gov/"}, {"name": "State Department - Export Controls", "url": "https://www.pmddtc.state.gov/"}, {"name": "Congressional Research Service - Export Controls", "url": "https://crsreports.congress.gov/product/pdf/R/R46795"}]'),

('2025-03-30-saudi-ai-deals', 25, 'short_answer', 'Government', 'What type of congressional oversight exists for international technology agreements?', '', '', '', '', 'Committee hearings', 'Congress can also control funding and ratify treaties.', 'Congressional oversight of international technology agreements includes committee hearings, appropriations control, treaty ratification authority, and investigations of national security implications. These oversight mechanisms ensure democratic accountability for international commitments.', '["committee hearings", "congressional oversight", "democratic accountability"]', '[{"name": "House Foreign Affairs Committee", "url": "https://foreignaffairs.house.gov/"}, {"name": "Senate Foreign Relations Committee", "url": "https://www.foreign.senate.gov/"}, {"name": "Congressional Research Service - Congressional Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]');