INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-03-27-shadow-docket-cases (Multiple Choice - 17 questions)
('2025-03-27-shadow-docket-cases', 1, 'multiple_choice', 'Justice', 'What is the "shadow docket" at the Supreme Court?', 'A secret court proceeding', 'Emergency orders issued without full briefing or oral argument', 'Classified case files', 'Administrative court functions', 'Emergency orders issued without full briefing or oral argument', 'This refers to expedited decisions made outside the normal Supreme Court process.', 'The "shadow docket" refers to Supreme Court emergency orders and decisions issued without the full briefing, oral argument, and detailed opinions that characterize the Court\'s regular merits docket. These emergency procedures bypass normal judicial processes for urgent matters.', '["shadow docket", "emergency orders", "Supreme Court procedures"]', '[{"name": "Supreme Court of the United States", "url": "https://www.supremecourt.gov/"}, {"name": "SCOTUSblog - Shadow Docket Analysis", "url": "https://www.scotusblog.com/2021/shadow-docket/"}, {"name": "Congressional Research Service - Supreme Court Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 2, 'multiple_choice', 'Constitutional Law', 'How does shadow docket usage affect due process in major constitutional cases?', 'It enhances due process', 'It can limit due process by reducing briefing and argument opportunities', 'It has no effect on due process', 'It only affects criminal cases', 'It can limit due process by reducing briefing and argument opportunities', 'Full briefing and argument are traditional components of due process.', 'Shadow docket usage can limit due process in major constitutional cases by reducing opportunities for comprehensive briefing, oral argument, and thorough judicial consideration that normally characterize Supreme Court review of significant legal questions.', '["due process", "constitutional cases", "judicial procedures"]', '[{"name": "Supreme Court - Due Process Cases", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "American Bar Association - Due Process Standards", "url": "https://www.americanbar.org/groups/judicial/publications/judges_journal/"}, {"name": "Congressional Research Service - Constitutional Procedure", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]'),

('2025-03-27-shadow-docket-cases', 3, 'multiple_choice', 'Government', 'What types of cases typically use shadow docket procedures?', 'Only criminal appeals', 'Emergency requests for stays, injunctions, and expedited review', 'Routine civil disputes', 'State court appeals only', 'Emergency requests for stays, injunctions, and expedited review', 'These procedures are designed for urgent legal matters requiring immediate attention.', 'Shadow docket procedures typically handle emergency requests including stays of lower court orders, preliminary injunctions, expedited appeals, and other matters requiring immediate Supreme Court attention that cannot wait for the normal briefing and argument schedule.', '["emergency requests", "stays", "injunctions"]', '[{"name": "Supreme Court Rules", "url": "https://www.supremecourt.gov/ctrules/rulesofthecourt.aspx"}, {"name": "Federal Courts - Emergency Procedures", "url": "https://www.uscourts.gov/about-federal-courts"}, {"name": "American Law Institute - Emergency Relief", "url": "https://www.ali.org/"}]'),

('2025-03-27-shadow-docket-cases', 4, 'multiple_choice', 'Constitutional Law', 'How does increased shadow docket use affect separation of powers?', 'It strengthens separation of powers', 'It may weaken judicial oversight by expediting approval of executive actions', 'It has no effect on separation of powers', 'It only affects legislative power', 'It may weaken judicial oversight by expediting approval of executive actions', 'Thorough judicial review is an important check on executive power.', 'Increased shadow docket use may weaken separation of powers by reducing thorough judicial oversight of executive actions. Emergency procedures can expedite approval of controversial policies without the comprehensive review that serves as an important check on executive power.', '["separation of powers", "judicial oversight", "executive actions"]', '[{"name": "Supreme Court - Separation of Powers Cases", "url": "https://supreme.justia.com/cases/federal/separation-of-powers/"}, {"name": "Congressional Research Service - Separation of Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}, {"name": "Federalist Papers - Judicial Review", "url": "https://guides.loc.gov/federalist-papers/judicial-review"}]'),

('2025-03-27-shadow-docket-cases', 5, 'multiple_choice', 'Justice', 'What transparency concerns arise from shadow docket decisions?', 'Shadow docket increases transparency', 'Limited public information about reasoning and dissents', 'No transparency concerns exist', 'Only affects lower courts', 'Limited public information about reasoning and dissents', 'These decisions often lack detailed explanations and public reasoning.', 'Shadow docket decisions raise transparency concerns because they often provide limited public information about the Court\'s reasoning, may not include detailed dissenting opinions, and can approve significant policy changes without the full public discourse that accompanies regular Supreme Court review.', '["transparency", "public information", "judicial reasoning"]', '[{"name": "Supreme Court - Public Information", "url": "https://www.supremecourt.gov/publicinfo/"}, {"name": "Reporters Committee for Freedom of the Press", "url": "https://www.rcfp.org/"}, {"name": "American Civil Liberties Union - Court Transparency", "url": "https://www.aclu.org/issues/free-speech/freedom-press"}]'),

('2025-03-27-shadow-docket-cases', 6, 'multiple_choice', 'Constitutional Law', 'What standard governs Supreme Court emergency relief?', 'Automatic approval for government requests', 'Likelihood of success, irreparable harm, balance of harms, and public interest', 'Political considerations only', 'No standards apply', 'Likelihood of success, irreparable harm, balance of harms, and public interest', 'These are the traditional factors for emergency judicial relief.', 'Supreme Court emergency relief is governed by traditional standards including likelihood of success on the merits, irreparable harm without relief, balance of harms between parties, and the public interest. These factors guide decisions on stays and emergency orders.', '["emergency relief standards", "likelihood of success", "irreparable harm"]', '[{"name": "Supreme Court - Emergency Applications", "url": "https://www.supremecourt.gov/ctrules/emergency-applications"}, {"name": "Federal Rules of Civil Procedure", "url": "https://www.law.cornell.edu/rules/frcp"}, {"name": "Congressional Research Service - Injunctive Relief", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 7, 'multiple_choice', 'Government', 'How do shadow docket decisions affect lower court authority?', 'They strengthen lower court independence', 'They can override lower court decisions without full Supreme Court review', 'They have no effect on lower courts', 'They only affect federal appeals courts', 'They can override lower court decisions without full Supreme Court review', 'Emergency orders can bypass normal appellate procedures.', 'Shadow docket decisions can override lower court decisions without the full Supreme Court review process, potentially undermining careful lower court analysis and creating uncertainty about legal standards when emergency orders lack detailed reasoning.', '["lower court authority", "appellate process", "judicial hierarchy"]', '[{"name": "Federal Judicial Center", "url": "https://www.fjc.gov/"}, {"name": "Administrative Office of U.S. Courts", "url": "https://www.uscourts.gov/"}, {"name": "Congressional Research Service - Federal Court System", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 8, 'multiple_choice', 'Constitutional Law', 'What constitutional principles guide judicial decision-making procedures?', 'Speed and efficiency only', 'Due process, equal protection, and fair procedures', 'Political considerations', 'Executive preference', 'Due process, equal protection, and fair procedures', 'Constitutional principles require fair and thorough judicial procedures.', 'Constitutional principles guiding judicial decision-making include due process requirements for fair procedures, equal protection in applying legal standards, and ensuring adequate opportunity for parties to present their cases before judicial decisions.', '["due process", "equal protection", "fair procedures"]', '[{"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/essay/amdt5-1/"}, {"name": "Supreme Court - Procedural Due Process", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Congressional Research Service - Constitutional Procedure", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]'),

('2025-03-27-shadow-docket-cases', 9, 'multiple_choice', 'Justice', 'How does public access to judicial reasoning affect democratic accountability?', 'Public access has no effect on accountability', 'Transparent reasoning enables public understanding and oversight of judicial decisions', 'Secret reasoning improves judicial independence', 'Only affects elected officials', 'Transparent reasoning enables public understanding and oversight of judicial decisions', 'Democracy requires public understanding of how government institutions make decisions.', 'Public access to judicial reasoning is essential for democratic accountability because it enables citizens to understand how courts make decisions, evaluate judicial performance, and maintain confidence in the fairness and legitimacy of the judicial system.', '["democratic accountability", "transparent reasoning", "public understanding"]', '[{"name": "American Bar Association - Judicial Independence", "url": "https://www.americanbar.org/advocacy/rule_of_law/"}, {"name": "Brennan Center for Justice", "url": "https://www.brennancenter.org/"}, {"name": "Congressional Research Service - Judicial Accountability", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 10, 'multiple_choice', 'Constitutional Law', 'What role do dissenting opinions play in Supreme Court transparency?', 'Dissents have no role in transparency', 'Dissents provide alternative reasoning and highlight disagreements', 'Dissents are always classified', 'Only majority opinions matter', 'Dissents provide alternative reasoning and highlight disagreements', 'Dissenting opinions offer important perspectives on legal reasoning.', 'Dissenting opinions play a crucial role in Supreme Court transparency by providing alternative legal reasoning, highlighting areas of disagreement among justices, and offering different perspectives on constitutional interpretation for public understanding.', '["dissenting opinions", "alternative reasoning", "judicial disagreement"]', '[{"name": "Supreme Court - Opinion Database", "url": "https://www.supremecourt.gov/opinions/"}, {"name": "Justia - Supreme Court Opinions", "url": "https://supreme.justia.com/"}, {"name": "Legal Information Institute - Court Opinions", "url": "https://www.law.cornell.edu/supreme-court/text"}]'),

('2025-03-27-shadow-docket-cases', 11, 'multiple_choice', 'Government', 'How do emergency procedures affect the adversarial process in courts?', 'They strengthen the adversarial process', 'They can limit full adversarial presentation of arguments', 'They have no effect on advocacy', 'They only affect criminal cases', 'They can limit full adversarial presentation of arguments', 'The adversarial system depends on thorough presentation by both sides.', 'Emergency procedures can limit the adversarial process by reducing time for comprehensive briefing, research, and argument presentation that normally ensures all sides of legal issues receive thorough consideration before judicial decisions.', '["adversarial process", "legal arguments", "comprehensive briefing"]', '[{"name": "American Bar Association - Adversarial System", "url": "https://www.americanbar.org/groups/litigation/"}, {"name": "Federal Rules of Civil Procedure", "url": "https://www.law.cornell.edu/rules/frcp"}, {"name": "Congressional Research Service - Judicial Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 12, 'multiple_choice', 'Constitutional Law', 'What constitutional authority allows Congress to regulate Supreme Court procedures?', 'No congressional authority exists', 'Article III and necessary and proper clause', 'Only executive authority applies', 'State government authority only', 'Article III and necessary and proper clause', 'Congress has constitutional authority over federal court organization and procedures.', 'Congress has constitutional authority under Article III and the Necessary and Proper Clause to regulate Supreme Court procedures, including rules for emergency applications, briefing requirements, and other aspects of federal court operations.', '["Article III", "congressional authority", "court procedures"]', '[{"name": "Constitution - Article III", "url": "https://constitution.congress.gov/browse/article-3/"}, {"name": "Congressional Research Service - Court Administration", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}, {"name": "Federal Judiciary Act History", "url": "https://www.fjc.gov/history/legislation"}]'),

('2025-03-27-shadow-docket-cases', 13, 'multiple_choice', 'Justice', 'How do shadow docket decisions affect legal precedent?', 'They create the same precedent as regular decisions', 'They generally do not create binding precedent but can have practical impact', 'They override all previous precedent', 'They have no legal effect', 'They generally do not create binding precedent but can have practical impact', 'Emergency orders typically lack the detailed reasoning that creates precedent.', 'Shadow docket decisions generally do not create binding legal precedent because they often lack detailed reasoning and analysis. However, they can have significant practical impact on legal development and may influence how lower courts interpret legal standards.', '["legal precedent", "binding authority", "practical impact"]', '[{"name": "Supreme Court - Precedent and Stare Decisis", "url": "https://www.supremecourt.gov/about/procedures.aspx"}, {"name": "Legal Information Institute - Precedent", "url": "https://www.law.cornell.edu/wex/precedent"}, {"name": "Congressional Research Service - Legal Precedent", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 14, 'multiple_choice', 'Government', 'What oversight mechanisms exist for Supreme Court emergency procedures?', 'No oversight mechanisms exist', 'Congressional oversight, judicial self-regulation, and public accountability', 'Only internal court oversight', 'Executive branch oversight only', 'Congressional oversight, judicial self-regulation, and public accountability', 'Multiple accountability mechanisms exist for judicial institutions.', 'Oversight of Supreme Court emergency procedures includes congressional oversight through legislation and hearings, judicial self-regulation through rule-making, and public accountability through transparency and legal commentary that monitors court practices.', '["congressional oversight", "judicial self-regulation", "public accountability"]', '[{"name": "House Judiciary Committee", "url": "https://judiciary.house.gov/"}, {"name": "Senate Judiciary Committee", "url": "https://www.judiciary.senate.gov/"}, {"name": "Congressional Research Service - Court Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

('2025-03-27-shadow-docket-cases', 15, 'multiple_choice', 'Constitutional Law', 'How does the shadow docket affect constitutional interpretation?', 'It improves constitutional interpretation', 'It can lead to less thorough constitutional analysis', 'It has no effect on interpretation', 'It only affects statutory interpretation', 'It can lead to less thorough constitutional analysis', 'Constitutional questions require careful analysis and reasoning.', 'The shadow docket can lead to less thorough constitutional analysis because emergency procedures may not allow for the comprehensive briefing, research, and deliberation that complex constitutional questions typically require for proper judicial consideration.', '["constitutional interpretation", "thorough analysis", "judicial deliberation"]', '[{"name": "Supreme Court - Constitutional Cases", "url": "https://www.supremecourt.gov/opinions/boundvolumes/"}, {"name": "Constitutional Law Scholars Network", "url": "https://www.law.cornell.edu/constitution"}, {"name": "Congressional Research Service - Constitutional Interpretation", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]'),

('2025-03-27-shadow-docket-cases', 16, 'multiple_choice', 'Justice', 'What reforms have been proposed for shadow docket procedures?', 'No reforms have been proposed', 'Increased transparency, briefing requirements, and limiting emergency use', 'Eliminating all emergency procedures', 'Expanding emergency procedures', 'Increased transparency, briefing requirements, and limiting emergency use', 'Various reforms aim to balance efficiency with proper judicial process.', 'Proposed reforms for shadow docket procedures include increased transparency in decision-making, enhanced briefing requirements for emergency applications, and limiting the use of emergency procedures to truly urgent matters that cannot wait for regular review.', '["shadow docket reforms", "transparency", "briefing requirements"]', '[{"name": "American Bar Association - Court Reform", "url": "https://www.americanbar.org/advocacy/rule_of_law/"}, {"name": "Brennan Center for Justice - Court Reform", "url": "https://www.brennancenter.org/"}, {"name": "Congressional Research Service - Judicial Reform", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 17, 'multiple_choice', 'Constitutional Law', 'How do emergency judicial procedures balance speed with fairness?', 'Speed always takes priority over fairness', 'Courts must balance urgent needs with due process requirements', 'Fairness always takes priority over speed', 'No balancing is required', 'Courts must balance urgent needs with due process requirements', 'Both speed and fairness are important constitutional values.', 'Emergency judicial procedures must balance the urgent need for quick decisions with constitutional requirements for due process and fairness. Courts try to provide expedited review while maintaining essential procedural protections for parties.', '["speed vs fairness", "due process", "emergency procedures"]', '[{"name": "Supreme Court - Emergency Applications Guide", "url": "https://www.supremecourt.gov/ctrules/emergency-applications"}, {"name": "Federal Rules of Civil Procedure", "url": "https://www.law.cornell.edu/rules/frcp"}, {"name": "Congressional Research Service - Emergency Relief", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

-- True/False Questions (5 questions)
('2025-03-27-shadow-docket-cases', 18, 'true_false', 'Justice', 'Shadow docket decisions receive the same level of briefing and oral argument as regular Supreme Court cases.', '', '', '', '', 'False', 'Shadow docket procedures are designed to be expedited with limited briefing.', 'False. Shadow docket decisions are emergency procedures that typically receive much less briefing and no oral argument compared to regular Supreme Court cases on the merits docket, which receive full briefing, oral argument, and detailed written opinions.', '["shadow docket", "limited briefing", "emergency procedures"]', '[{"name": "Supreme Court Rules", "url": "https://www.supremecourt.gov/ctrules/rulesofthecourt.aspx"}, {"name": "SCOTUSblog - Shadow Docket Analysis", "url": "https://www.scotusblog.com/"}, {"name": "Congressional Research Service - Court Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 19, 'true_false', 'Constitutional Law', 'The Supreme Court has unlimited discretion in how it handles emergency applications.', '', '', '', '', 'False', 'Court procedures must still comply with constitutional requirements and legal standards.', 'False. While the Supreme Court has broad discretion in managing its docket, emergency applications must still meet legal standards for relief and comply with constitutional requirements for due process and fair procedures.', '["judicial discretion", "legal standards", "constitutional requirements"]', '[{"name": "Supreme Court - Emergency Relief Standards", "url": "https://www.supremecourt.gov/ctrules/emergency-applications"}, {"name": "Constitutional Law - Due Process", "url": "https://www.law.cornell.edu/wex/due_process"}, {"name": "Congressional Research Service - Court Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 20, 'true_false', 'Government', 'Congress has no authority to regulate Supreme Court procedures.', '', '', '', '', 'False', 'Congress has constitutional authority under Article III to regulate federal court procedures.', 'False. Congress has constitutional authority under Article III and the Necessary and Proper Clause to regulate Supreme Court procedures, including rules for emergency applications, briefing requirements, and other aspects of court operations.', '["congressional authority", "Article III", "court regulation"]', '[{"name": "Constitution - Article III", "url": "https://constitution.congress.gov/browse/article-3/"}, {"name": "Congressional Research Service - Congressional Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "Federal Judiciary Act", "url": "https://www.fjc.gov/history/legislation"}]'),

('2025-03-27-shadow-docket-cases', 21, 'true_false', 'Justice', 'Shadow docket decisions create the same binding legal precedent as regular Supreme Court opinions.', '', '', '', '', 'False', 'Emergency orders typically lack the detailed reasoning that creates binding precedent.', 'False. Shadow docket decisions generally do not create the same binding legal precedent as regular Supreme Court opinions because they often lack detailed reasoning and comprehensive analysis that establishes clear legal principles for future cases.', '["legal precedent", "binding authority", "detailed reasoning"]', '[{"name": "Supreme Court - Precedent System", "url": "https://www.supremecourt.gov/about/procedures.aspx"}, {"name": "Legal Information Institute - Stare Decisis", "url": "https://www.law.cornell.edu/wex/stare_decisis"}, {"name": "Congressional Research Service - Legal Precedent", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 22, 'true_false', 'Constitutional Law', 'Emergency judicial procedures must still comply with constitutional due process requirements.', '', '', '', '', 'True', 'Constitutional requirements apply to all government actions, including emergency procedures.', 'True. Emergency judicial procedures must still comply with constitutional due process requirements, though the specific procedures may be streamlined. Courts must balance speed with fundamental fairness and constitutional protections.', '["due process", "constitutional requirements", "emergency procedures"]', '[{"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/essay/amdt5-1/"}, {"name": "Supreme Court - Procedural Due Process", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Congressional Research Service - Constitutional Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]'),

-- Short Answer Questions (3 questions)
('2025-03-27-shadow-docket-cases', 23, 'short_answer', 'Justice', 'What term describes Supreme Court emergency orders issued without full briefing or oral argument?', '', '', '', '', 'Shadow docket', 'This term contrasts with the regular merits docket that receives full Supreme Court review.', 'The "shadow docket" describes Supreme Court emergency orders and decisions issued without the full briefing, oral argument, and detailed opinions that characterize the Court\'s regular merits docket. These emergency procedures bypass normal judicial processes.', '["shadow docket", "emergency orders", "Supreme Court"]', '[{"name": "SCOTUSblog - Shadow Docket", "url": "https://www.scotusblog.com/"}, {"name": "Supreme Court Procedures", "url": "https://www.supremecourt.gov/about/procedures.aspx"}, {"name": "Congressional Research Service - Court Procedures", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}]'),

('2025-03-27-shadow-docket-cases', 24, 'short_answer', 'Constitutional Law', 'What constitutional article gives Congress authority to regulate federal court procedures?', '', '', '', '', 'Article III', 'This article establishes the federal judicial system and congressional authority over courts.', 'Article III of the Constitution gives Congress authority to regulate federal court procedures. This article establishes the federal judicial system and grants Congress power to organize and regulate the operations of federal courts, including the Supreme Court.', '["Article III", "congressional authority", "federal courts"]', '[{"name": "Constitution - Article III", "url": "https://constitution.congress.gov/browse/article-3/"}, {"name": "Congressional Research Service - Judicial Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R45112"}, {"name": "Federal Judiciary Act History", "url": "https://www.fjc.gov/history/"}]'),

('2025-03-27-shadow-docket-cases', 25, 'short_answer', 'Constitutional Law', 'What constitutional requirement ensures fair procedures in judicial decision-making?', '', '', '', '', 'Due process', 'This fundamental constitutional principle requires fair treatment by government institutions.', 'Due process is the constitutional requirement that ensures fair procedures in judicial decision-making. Found in the Fifth and Fourteenth Amendments, due process requires that government actions, including court procedures, provide fundamental fairness and adequate procedural protections.', '["due process", "fair procedures", "constitutional requirement"]', '[{"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/essay/amdt5-1/"}, {"name": "Supreme Court - Due Process Cases", "url": "https://supreme.justia.com/cases/federal/due-process/"}, {"name": "Congressional Research Service - Due Process Rights", "url": "https://crsreports.congress.gov/product/pdf/R/R45031"}]');