INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-05-05-social-security-privatization (Multiple Choice - 17 questions)
('2025-05-05-social-security-privatization', 1, 'multiple_choice', 'Social Policy', 'What federal agency administers Social Security benefits?', 'Department of Health and Human Services', 'Social Security Administration', 'Department of Treasury', 'Department of Labor', 'Social Security Administration', 'SSA is the independent agency responsible for administering Social Security.', 'The Social Security Administration is the independent federal agency responsible for administering Social Security retirement, disability, and survivor benefits, managing the program\'s finances, and processing benefit applications and payments.', '["Social Security Administration", "federal agency", "benefit administration"]', '[{"name": "Social Security Administration", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - Social Security Administration", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}, {"name": "Government Accountability Office - Social Security", "url": "https://www.gao.gov/social-security"}]'),

('2025-05-05-social-security-privatization', 2, 'multiple_choice', 'Constitutional Law', 'What constitutional authority supports federal Social Security programs?', 'No constitutional authority', 'General welfare clause and taxation powers', 'State police powers only', 'Commerce Clause exclusively', 'General welfare clause and taxation powers', 'Article I provides Congress with broad powers over taxation and general welfare.', 'Federal Social Security programs are supported by constitutional authority including the general welfare clause and taxation powers in Article I, Section 8, which permit Congress to tax and spend for the general welfare, including social insurance programs.', '["general welfare clause", "taxation powers", "congressional authority"]', '[{"name": "Constitution - Article I Powers", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Social Security Cases", "url": "https://supreme.justia.com/cases/federal/social-security/"}, {"name": "Congressional Research Service - Social Security Constitutional Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 3, 'multiple_choice', 'Social Policy', 'What are the primary risks associated with Social Security privatization?', 'No risks involved', 'Market volatility, reduced benefits, and administrative costs', 'Guaranteed benefit increases', 'Automatic poverty elimination', 'Market volatility, reduced benefits, and administrative costs', 'Privatization introduces market risks and administrative complexities to retirement security.', 'Primary risks of Social Security privatization include market volatility affecting retirement income, potential for reduced benefits during market downturns, increased administrative costs, loss of guaranteed benefits, and greater individual responsibility for investment decisions.', '["market volatility", "benefit reductions", "administrative costs"]', '[{"name": "Congressional Budget Office - Social Security", "url": "https://www.cbo.gov/topics/social-security"}, {"name": "Social Security Trustees Report", "url": "https://www.ssa.gov/OACT/TR/"}, {"name": "Congressional Research Service - Social Security Reform", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 4, 'multiple_choice', 'Government', 'What role does Congress play in Social Security policy?', 'No congressional role', 'Legislative authority over program structure, benefits, and funding', 'Only oversight functions', 'Complete delegation to SSA', 'Legislative authority over program structure, benefits, and funding', 'Congress has primary authority over Social Security program design and funding.', 'Congress plays the primary role in Social Security policy through legislative authority over program structure, benefit formulas, eligibility requirements, funding mechanisms, and major reforms, with constitutional responsibility for federal social insurance programs.', '["legislative authority", "program structure", "congressional oversight"]', '[{"name": "House Ways and Means Committee", "url": "https://waysandmeans.house.gov/"}, {"name": "Senate Finance Committee", "url": "https://www.finance.senate.gov/"}, {"name": "Congressional Research Service - Congressional Social Security Role", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 5, 'multiple_choice', 'Social Policy', 'How would privatization affect Social Security\'s guaranteed benefit structure?', 'No change to guarantees', 'Would replace guaranteed benefits with market-dependent returns', 'Strengthen all guarantees', 'Only improve benefit security', 'Would replace guaranteed benefits with market-dependent returns', 'Privatization fundamentally changes from guaranteed to market-based benefits.', 'Privatization would replace Social Security\'s guaranteed benefit structure with market-dependent returns, eliminating the current system\'s promise of defined benefits regardless of market performance and shifting investment risk to individual beneficiaries.', '["guaranteed benefits", "market dependence", "investment risk"]', '[{"name": "Social Security Administration - Benefit Formula", "url": "https://www.ssa.gov/"}, {"name": "Congressional Budget Office - Privatization Analysis", "url": "https://www.cbo.gov/"}, {"name": "Congressional Research Service - Social Security Benefits", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 6, 'multiple_choice', 'Economics', 'What transition costs are associated with Social Security privatization?', 'No transition costs', 'Significant costs from paying current benefits while diverting taxes to private accounts', 'Immediate cost savings', 'Only administrative expenses', 'Significant costs from paying current benefits while diverting taxes to private accounts', 'Transition costs arise from maintaining two systems simultaneously.', 'Social Security privatization involves significant transition costs because the government must continue paying current benefits to existing retirees while diverting payroll taxes to fund private accounts, requiring additional financing during the transition period.', '["transition costs", "dual system funding", "fiscal challenges"]', '[{"name": "Congressional Budget Office - Transition Costs", "url": "https://www.cbo.gov/"}, {"name": "Social Security Trustees Report", "url": "https://www.ssa.gov/OACT/TR/"}, {"name": "Congressional Research Service - Privatization Economics", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 7, 'multiple_choice', 'Constitutional Law', 'What due process considerations apply to Social Security benefit changes?', 'No due process protections', 'Property-like interests in earned benefits and fair procedures', 'Only procedural protections', 'Automatic termination allowed', 'Property-like interests in earned benefits and fair procedures', 'Supreme Court has recognized property interests in Social Security benefits.', 'Due process considerations for Social Security include recognition of property-like interests in earned benefits, requirements for fair procedures before benefit termination or reduction, and protection against arbitrary changes to benefits that individuals have earned through contributions.', '["property interests", "due process", "benefit protection"]', '[{"name": "Supreme Court - Goldberg v. Kelly", "url": "https://supreme.justia.com/cases/federal/us/397/254/"}, {"name": "Constitution - Due Process Clauses", "url": "https://constitution.congress.gov/browse/amendment-5/"}, {"name": "Congressional Research Service - Social Security Due Process", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 8, 'multiple_choice', 'Social Policy', 'How does Social Security privatization affect disability and survivor benefits?', 'No effect on these benefits', 'Complicates provision of disability and survivor protection', 'Automatically improves all benefits', 'Only affects retirement benefits', 'Complicates provision of disability and survivor protection', 'Private accounts are designed for investment growth, not insurance protection.', 'Social Security privatization complicates disability and survivor benefits because private investment accounts cannot easily provide insurance protection against disability or premature death, requiring separate mechanisms to maintain these crucial social insurance functions.', '["disability benefits", "survivor benefits", "insurance protection"]', '[{"name": "Social Security Administration - Disability", "url": "https://www.ssa.gov/disability/"}, {"name": "Congressional Budget Office - Disability Programs", "url": "https://www.cbo.gov/"}, {"name": "Congressional Research Service - Social Security Insurance Features", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 9, 'multiple_choice', 'Government', 'What administrative challenges arise from Social Security privatization?', 'No administrative challenges', 'Complex account management, regulation, and oversight requirements', 'Simplified administration', 'Automatic efficiency gains', 'Complex account management, regulation, and oversight requirements', 'Private accounts require extensive new administrative infrastructure.', 'Social Security privatization creates administrative challenges including managing millions of individual accounts, regulating investment options, ensuring account security, preventing fraud, providing customer service, and overseeing financial institutions managing the accounts.', '["account management", "regulatory oversight", "administrative complexity"]', '[{"name": "Government Accountability Office - Social Security Administration", "url": "https://www.gao.gov/"}, {"name": "Social Security Administration Operations", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - Administrative Challenges", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 10, 'multiple_choice', 'Social Policy', 'How does privatization affect low-income workers differently?', 'No differential impact', 'Greater risk for low-income workers who can less afford market losses', 'Only benefits low-income workers', 'Equal impact on all income levels', 'Greater risk for low-income workers who can less afford market losses', 'Low-income workers have less capacity to recover from market losses.', 'Privatization affects low-income workers more severely because they have less financial cushion to absorb market losses, rely more heavily on Social Security for retirement income, and have fewer alternative resources if private account investments perform poorly.', '["income inequality", "market risk", "retirement security"]', '[{"name": "Congressional Budget Office - Distributional Effects", "url": "https://www.cbo.gov/"}, {"name": "Social Security Administration - Benefit Distribution", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - Income Distribution", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 11, 'multiple_choice', 'Constitutional Law', 'What Equal Protection issues arise from Social Security privatization?', 'No Equal Protection concerns', 'Potential for unequal treatment based on age, income, or investment success', 'Automatic equal treatment', 'Only benefits equality', 'Potential for unequal treatment based on age, income, or investment success', 'Privatization could create systematic inequalities in benefit outcomes.', 'Equal Protection concerns in Social Security privatization include potential for unequal treatment based on when individuals enter the workforce, their ability to make investment decisions, income levels affecting investment capacity, and market timing affecting retirement outcomes.', '["Equal Protection", "systematic inequality", "benefit disparities"]', '[{"name": "Constitution - Equal Protection Clause", "url": "https://constitution.congress.gov/browse/amendment-14/"}, {"name": "Supreme Court - Equal Protection Cases", "url": "https://supreme.justia.com/cases/federal/equal-protection/"}, {"name": "Congressional Research Service - Social Security Equity", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 12, 'multiple_choice', 'Economics', 'What impact would privatization have on federal government debt?', 'Automatic debt reduction', 'Significant increase in federal debt during transition period', 'No effect on government debt', 'Only positive fiscal effects', 'Significant increase in federal debt during transition period', 'Transition costs require additional government borrowing.', 'Social Security privatization would significantly increase federal government debt during the transition period because the government must borrow money to pay current benefits while payroll taxes are diverted to private accounts, creating large fiscal deficits.', '["federal debt", "transition financing", "fiscal impact"]', '[{"name": "Congressional Budget Office - Debt Analysis", "url": "https://www.cbo.gov/"}, {"name": "Treasury Department - Government Debt", "url": "https://home.treasury.gov/"}, {"name": "Congressional Research Service - Fiscal Effects", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 13, 'multiple_choice', 'Government', 'What oversight mechanisms exist for Social Security policy changes?', 'No oversight mechanisms', 'Congressional hearings, actuarial analysis, and public accountability', 'Only internal agency oversight', 'Automatic implementation', 'Congressional hearings, actuarial analysis, and public accountability', 'Major Social Security changes require extensive review and analysis.', 'Oversight of Social Security policy changes includes congressional hearings, actuarial analysis by Social Security trustees, Government Accountability Office reviews, public comment periods, and democratic accountability through the legislative process.', '["congressional oversight", "actuarial analysis", "public accountability"]', '[{"name": "Social Security Trustees", "url": "https://www.ssa.gov/OACT/TR/"}, {"name": "Government Accountability Office", "url": "https://www.gao.gov/"}, {"name": "Congressional Research Service - Policy Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 14, 'multiple_choice', 'Social Policy', 'How does privatization affect retirement security for women?', 'No gender-specific effects', 'Potentially harmful due to wage gaps and career interruptions', 'Only benefits women', 'Equal impact regardless of gender', 'Potentially harmful due to wage gaps and career interruptions', 'Women face unique challenges that privatization could exacerbate.', 'Privatization could particularly harm women\'s retirement security because women typically have lower lifetime earnings, more career interruptions for caregiving, longer life expectancy requiring more retirement income, and greater reliance on Social Security\'s progressive benefit formula.', '["gender equity", "wage gaps", "retirement security"]', '[{"name": "Social Security Administration - Women and Retirement", "url": "https://www.ssa.gov/"}, {"name": "Congressional Budget Office - Gender Analysis", "url": "https://www.cbo.gov/"}, {"name": "Congressional Research Service - Women and Social Security", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 15, 'multiple_choice', 'Civic Participation', 'How can citizens participate in Social Security policy debates?', 'Citizens have no role in Social Security policy', 'Congressional contact, public advocacy, and democratic participation', 'Only experts can participate', 'Policy development is closed to public input', 'Congressional contact, public advocacy, and democratic participation', 'Social Security policy involves democratic participation and public input.', 'Citizens can participate in Social Security policy debates by contacting congressional representatives, participating in public advocacy, engaging in democratic discourse, supporting relevant organizations, and exercising electoral accountability for Social Security decisions.', '["democratic participation", "public advocacy", "citizen engagement"]', '[{"name": "Congress.gov - Contact Representatives", "url": "https://www.congress.gov/"}, {"name": "AARP - Social Security Advocacy", "url": "https://www.aarp.org/"}, {"name": "Social Security Works", "url": "https://www.socialsecurityworks.org/"}]'),

('2025-05-05-social-security-privatization', 16, 'multiple_choice', 'Constitutional Law', 'What contractual obligations does the government have regarding Social Security benefits?', 'Complete contractual obligations', 'Limited obligations subject to congressional modification', 'No government obligations', 'Absolute guarantee of all benefits', 'Limited obligations subject to congressional modification', 'Supreme Court has held that Social Security benefits can be modified by Congress.', 'The government has limited contractual obligations regarding Social Security benefits, as the Supreme Court has held that while individuals have property-like interests in earned benefits, Congress retains authority to modify the program subject to due process requirements.', '["contractual obligations", "congressional authority", "benefit modification"]', '[{"name": "Supreme Court - Flemming v. Nestor", "url": "https://supreme.justia.com/cases/federal/us/363/603/"}, {"name": "Congressional Research Service - Social Security Legal Framework", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}, {"name": "Social Security Administration - Legal Authority", "url": "https://www.ssa.gov/"}]'),

('2025-05-05-social-security-privatization', 17, 'multiple_choice', 'Social Policy', 'What alternative Social Security reforms exist besides privatization?', 'Only privatization is possible', 'Benefit adjustments, tax increases, and gradual reforms', 'No reform options available', 'Privatization is the only solution', 'Benefit adjustments, tax increases, and gradual reforms', 'Multiple reform options exist to address Social Security\'s long-term financing.', 'Alternative Social Security reforms include gradually raising the retirement age, lifting the payroll tax cap, adjusting benefit formulas, increasing payroll tax rates, modifying cost-of-living adjustments, and combining multiple approaches to ensure long-term sustainability.', '["benefit adjustments", "tax reforms", "gradual changes"]', '[{"name": "Social Security Trustees Report", "url": "https://www.ssa.gov/OACT/TR/"}, {"name": "Congressional Budget Office - Reform Options", "url": "https://www.cbo.gov/"}, {"name": "Congressional Research Service - Reform Alternatives", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

-- True/False Questions (5 questions)
('2025-05-05-social-security-privatization', 18, 'true_false', 'Constitutional Law', 'Congress has constitutional authority to establish and modify Social Security programs under the general welfare clause.', '', '', '', '', 'True', 'Article I grants Congress broad powers to tax and spend for the general welfare.', 'True. Congress has constitutional authority to establish and modify Social Security programs under the general welfare clause and taxation powers in Article I, Section 8, which permit federal social insurance programs.', '["congressional authority", "general welfare", "Social Security"]', '[{"name": "Constitution - General Welfare Clause", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Social Security Constitutional Cases", "url": "https://supreme.justia.com/cases/federal/social-security/"}, {"name": "Congressional Research Service - Constitutional Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 19, 'true_false', 'Social Policy', 'Social Security privatization would eliminate guaranteed benefits and replace them with market-dependent returns.', '', '', '', '', 'True', 'Privatization fundamentally changes the nature of retirement benefits from guaranteed to market-based.', 'True. Social Security privatization would eliminate guaranteed benefits and replace them with market-dependent returns, shifting investment risk from the government to individual beneficiaries and ending the current system\'s defined benefit structure.', '["privatization effects", "guaranteed benefits", "market risk"]', '[{"name": "Congressional Budget Office - Privatization Analysis", "url": "https://www.cbo.gov/"}, {"name": "Social Security Administration", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - Privatization Impact", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 20, 'true_false', 'Economics', 'Social Security privatization would require significant government borrowing during the transition period.', '', '', '', '', 'True', 'Transition costs arise from paying current benefits while diverting taxes to private accounts.', 'True. Social Security privatization would require significant government borrowing during the transition period because the government must continue paying benefits to current retirees while payroll taxes are diverted to fund private accounts.', '["transition costs", "government borrowing", "fiscal impact"]', '[{"name": "Congressional Budget Office - Fiscal Analysis", "url": "https://www.cbo.gov/"}, {"name": "Treasury Department", "url": "https://home.treasury.gov/"}, {"name": "Congressional Research Service - Transition Economics", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 21, 'true_false', 'Constitutional Law', 'Individuals have absolute contractual rights to Social Security benefits that cannot be modified by Congress.', '', '', '', '', 'False', 'The Supreme Court has held that Congress retains authority to modify Social Security benefits.', 'False. While individuals have property-like interests in earned Social Security benefits, the Supreme Court has held that Congress retains authority to modify the program subject to due process requirements, meaning benefits are not absolute contractual rights.', '["contractual rights", "congressional authority", "benefit modification"]', '[{"name": "Supreme Court - Flemming v. Nestor", "url": "https://supreme.justia.com/cases/federal/us/363/603/"}, {"name": "Congressional Research Service - Legal Framework", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}, {"name": "Social Security Administration - Legal Authority", "url": "https://www.ssa.gov/"}]'),

('2025-05-05-social-security-privatization', 22, 'true_false', 'Social Policy', 'Social Security privatization would particularly benefit low-income workers and women.', '', '', '', '', 'False', 'Privatization poses greater risks for low-income workers and women who rely more on Social Security.', 'False. Social Security privatization would not particularly benefit low-income workers and women. Instead, these groups face greater risks because they rely more heavily on Social Security, have less capacity to absorb market losses, and benefit from the current system\'s progressive structure.', '["distributional effects", "low-income impact", "gender effects"]', '[{"name": "Congressional Budget Office - Distributional Analysis", "url": "https://www.cbo.gov/"}, {"name": "Social Security Administration - Benefit Distribution", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - Equity Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

-- Short Answer Questions (3 questions)
('2025-05-05-social-security-privatization', 23, 'short_answer', 'Government', 'What federal agency administers Social Security benefits?', '', '', '', '', 'Social Security Administration', 'This independent agency manages all aspects of the Social Security program.', 'The Social Security Administration (SSA) is the independent federal agency that administers Social Security retirement, disability, and survivor benefits, managing the program\'s operations, finances, and benefit determinations.', '["Social Security Administration", "benefit administration", "federal agency"]', '[{"name": "Social Security Administration", "url": "https://www.ssa.gov/"}, {"name": "Congressional Research Service - SSA Operations", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}, {"name": "Government Accountability Office - Social Security", "url": "https://www.gao.gov/"}]'),

('2025-05-05-social-security-privatization', 24, 'short_answer', 'Constitutional Law', 'What constitutional provision primarily supports Congress\'s authority to establish Social Security?', '', '', '', '', 'General welfare clause', 'This Article I power allows Congress to tax and spend for the general welfare.', 'The general welfare clause in Article I, Section 8 primarily supports Congress\'s authority to establish Social Security by permitting federal taxation and spending for social insurance programs that promote the general welfare.', '["general welfare clause", "constitutional authority", "Article I powers"]', '[{"name": "Constitution - General Welfare Clause", "url": "https://constitution.congress.gov/browse/article-1/section-8/"}, {"name": "Supreme Court - Social Security Cases", "url": "https://supreme.justia.com/cases/federal/social-security/"}, {"name": "Congressional Research Service - Constitutional Basis", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]'),

('2025-05-05-social-security-privatization', 25, 'short_answer', 'Social Policy', 'What major risk would privatization introduce to Social Security benefits?', '', '', '', '', 'Market volatility', 'Private accounts would be subject to stock market fluctuations affecting retirement income.', 'Market volatility is the major risk privatization would introduce to Social Security benefits, as private investment accounts would be subject to stock market fluctuations that could significantly reduce retirement income during market downturns.', '["market volatility", "investment risk", "retirement security"]', '[{"name": "Congressional Budget Office - Market Risk Analysis", "url": "https://www.cbo.gov/"}, {"name": "Social Security Trustees Report", "url": "https://www.ssa.gov/OACT/TR/"}, {"name": "Congressional Research Service - Privatization Risks", "url": "https://crsreports.congress.gov/product/pdf/R/R44670"}]');