INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-03-21-state-dept-trump-property-meetings (Multiple Choice - 17 questions)
('2025-03-21-state-dept-trump-property-meetings', 1, 'multiple_choice', 'Government', 'Who serves as Secretary of State in the current administration?', 'Antony Blinken', 'Marco Rubio', 'Mike Pompeo', 'Rex Tillerson', 'Marco Rubio', 'This former Florida Senator was appointed by Trump in 2025.', 'Marco Rubio serves as Secretary of State in the Trump administration. The former Florida Senator and presidential candidate was confirmed in 2025 and leads the State Department, which handles foreign policy and diplomatic relations for the United States.', '["Secretary of State", "Marco Rubio", "State Department"]', '[{"name": "U.S. Department of State - Secretary Biography", "url": "https://www.state.gov/secretary/"}, {"name": "Senate Foreign Relations Committee", "url": "https://www.foreign.senate.gov/"}, {"name": "Associated Press - Rubio Confirmation", "url": "https://apnews.com/article/marco-rubio-secretary-state-confirmation"}]'),

('2025-03-21-state-dept-trump-property-meetings', 2, 'multiple_choice', 'Constitutional Law', 'What constitutional provision prohibits federal officials from receiving payments from foreign governments?', 'Commerce Clause', 'Foreign Emoluments Clause', 'Due Process Clause', 'Equal Protection Clause', 'Foreign Emoluments Clause', 'This clause prevents conflicts between personal interests and official duties.', 'The Foreign Emoluments Clause in Article I, Section 9 prohibits federal officials from receiving payments, gifts, or benefits from foreign governments without congressional consent. This prevents conflicts between personal financial interests and official duties in foreign relations.', '["Foreign Emoluments Clause", "Article I Section 9", "foreign payments"]', '[{"name": "Constitution Annotated - Emoluments Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C8-1/"}, {"name": "Congressional Research Service - Emoluments Clause Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R44334"}, {"name": "Government Accountability Office - Emoluments Issues", "url": "https://www.gao.gov/products/gao-18-253"}]'),

('2025-03-21-state-dept-trump-property-meetings', 3, 'multiple_choice', 'Ethics', 'What ethical concern arises when government officials conduct business at presidential properties?', 'No ethical concerns exist', 'Appearance of corruption and financial benefit to the president', 'Improved government efficiency', 'Enhanced diplomatic relations', 'Appearance of corruption and financial benefit to the president', 'Government payments to presidential businesses create obvious conflicts of interest.', 'Conducting government business at presidential properties creates ethical concerns about the appearance of corruption and financial benefit to the president. Such arrangements can appear to prioritize personal profit over public service and undermine public trust in government.', '["appearance of corruption", "conflicts of interest", "presidential properties"]', '[{"name": "Office of Government Ethics - Standards of Conduct", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "Government Accountability Office - Ethics Analysis", "url": "https://www.gao.gov/products/gao-18-253"}, {"name": "Congressional Research Service - Government Ethics", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-03-21-state-dept-trump-property-meetings', 4, 'multiple_choice', 'Government', 'What federal agency typically provides ethics guidance to executive branch officials?', 'General Services Administration', 'Office of Government Ethics', 'Federal Election Commission', 'Government Accountability Office', 'Office of Government Ethics', 'This independent agency oversees executive branch ethics compliance.', 'The Office of Government Ethics (OGE) provides ethics guidance to executive branch officials, including rules about conflicts of interest, financial disclosures, and standards of conduct. OGE helps ensure that government officials act in the public interest rather than for personal financial gain.', '["Office of Government Ethics", "OGE", "ethics guidance"]', '[{"name": "Office of Government Ethics Official Website", "url": "https://www.oge.gov/"}, {"name": "OGE - Standards of Conduct", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "Congressional Research Service - Ethics Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-03-21-state-dept-trump-property-meetings', 5, 'multiple_choice', 'Constitutional Law', 'How do emoluments violations affect separation of powers?', 'They strengthen separation of powers', 'They can compromise official independence and create competing loyalties', 'They have no effect on separation of powers', 'They only affect the judicial branch', 'They can compromise official independence and create competing loyalties', 'Financial interests can influence official decision-making.', 'Emoluments violations can compromise separation of powers by creating competing loyalties between public duties and personal financial interests, potentially influencing official decision-making and undermining the independence required for proper checks and balances between government branches.', '["separation of powers", "competing loyalties", "official independence"]', '[{"name": "Supreme Court - Separation of Powers Cases", "url": "https://supreme.justia.com/cases/federal/separation-of-powers/"}, {"name": "Congressional Research Service - Separation of Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}, {"name": "Federalist Papers - Separation of Powers", "url": "https://guides.loc.gov/federalist-papers/separation-of-powers"}]'),

('2025-03-21-state-dept-trump-property-meetings', 6, 'multiple_choice', 'Government', 'What oversight mechanisms exist for monitoring government spending on travel and accommodations?', 'No oversight mechanisms exist', 'Congressional oversight, GAO audits, and inspector general reviews', 'Only media oversight', 'Presidential discretion only', 'Congressional oversight, GAO audits, and inspector general reviews', 'Multiple institutions monitor how taxpayer money is spent.', 'Government spending on travel and accommodations is monitored through congressional oversight committees, Government Accountability Office audits, inspector general reviews, and public transparency requirements that track how taxpayer funds are used for official business.', '["congressional oversight", "GAO audits", "government spending"]', '[{"name": "Government Accountability Office - Travel Oversight", "url": "https://www.gao.gov/products/travel-oversight"}, {"name": "House Oversight Committee", "url": "https://oversight.house.gov/"}, {"name": "Congressional Research Service - Government Spending Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

('2025-03-21-state-dept-trump-property-meetings', 7, 'multiple_choice', 'Ethics', 'What is the "appearance of impropriety" standard in government ethics?', 'Only actual corruption matters', 'Actions that could appear corrupt to reasonable observers should be avoided', 'Appearance standards only apply to judicial officials', 'There is no such standard', 'Actions that could appear corrupt to reasonable observers should be avoided', 'Ethics requires avoiding even the appearance of conflicts or corruption.', 'The "appearance of impropriety" standard requires government officials to avoid actions that could appear corrupt or conflicted to reasonable observers, even if no actual wrongdoing occurs. This maintains public trust by ensuring that government actions appear legitimate and ethical.', '["appearance of impropriety", "government ethics", "public trust"]', '[{"name": "Office of Government Ethics - Appearance Standards", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "American Bar Association - Ethics Standards", "url": "https://www.americanbar.org/groups/professional_responsibility/"}, {"name": "Congressional Research Service - Ethics Standards", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-03-21-state-dept-trump-property-meetings', 8, 'multiple_choice', 'Constitutional Law', 'What constitutional principle requires officials to serve the public interest rather than personal financial interests?', 'Due Process', 'Faithful execution of laws and public trust', 'Equal Protection', 'Commerce regulation', 'Faithful execution of laws and public trust', 'Constitutional principles require officials to prioritize public service over personal gain.', 'Constitutional principles of faithful execution of laws and public trust require officials to serve the public interest rather than personal financial interests. This ensures that government decisions are made based on public good rather than private financial benefit.', '["faithful execution", "public trust", "public interest"]', '[{"name": "Constitution Annotated - Take Care Clause", "url": "https://constitution.congress.gov/browse/essay/artII-S3-1/"}, {"name": "Supreme Court - Public Trust Cases", "url": "https://supreme.justia.com/cases/federal/corruption-cases/"}, {"name": "Congressional Research Service - Constitutional Duties", "url": "https://crsreports.congress.gov/product/pdf/R/R42733"}]'),

('2025-03-21-state-dept-trump-property-meetings', 9, 'multiple_choice', 'Government', 'How does conducting official business at presidential properties affect taxpayer interests?', 'It saves taxpayer money', 'It potentially diverts taxpayer funds to benefit the president personally', 'It has no effect on taxpayers', 'It only affects foreign relations', 'It potentially diverts taxpayer funds to benefit the president personally', 'Public money should not flow to officials\' private businesses.', 'Conducting official business at presidential properties potentially diverts taxpayer funds to benefit the president personally, creating a conflict between public service and private profit. This raises concerns about the proper use of public money and ethical governance.', '["taxpayer funds", "personal benefit", "public money"]', '[{"name": "Government Accountability Office - Federal Spending", "url": "https://www.gao.gov/about/what-gao-does/government-contracting"}, {"name": "Congressional Budget Office - Government Spending", "url": "https://www.cbo.gov/"}, {"name": "Congressional Research Service - Government Contracting", "url": "https://crsreports.congress.gov/product/pdf/R/R40516"}]'),

('2025-03-21-state-dept-trump-property-meetings', 10, 'multiple_choice', 'Constitutional Law', 'What congressional authority exists to investigate potential emoluments violations?', 'No congressional authority exists', 'Oversight, investigation, and subpoena powers', 'Only budget authority', 'State government oversight only', 'Oversight, investigation, and subpoena powers', 'Congress has broad constitutional oversight responsibilities.', 'Congress has constitutional authority to investigate potential emoluments violations through oversight hearings, investigative powers, subpoena authority, and budget control. This oversight ensures compliance with constitutional restrictions on official conduct.', '["congressional oversight", "investigation powers", "constitutional authority"]', '[{"name": "House Oversight Committee Authority", "url": "https://oversight.house.gov/about"}, {"name": "Congressional Research Service - Congressional Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "Government Accountability Office - Congressional Authority", "url": "https://www.gao.gov/about/congressional-oversight"}]'),

('2025-03-21-state-dept-trump-property-meetings', 11, 'multiple_choice', 'Ethics', 'What alternatives exist to using presidential properties for government meetings?', 'No alternatives exist', 'Government facilities, neutral venues, and established conference centers', 'Only foreign government facilities', 'Private homes only', 'Government facilities, neutral venues, and established conference centers', 'Many appropriate venues exist that avoid conflicts of interest.', 'Alternatives to using presidential properties include government facilities, neutral commercial venues, established conference centers, and other locations that do not create financial benefits for government officials, avoiding conflicts of interest while serving official needs.', '["government facilities", "neutral venues", "conflict avoidance"]', '[{"name": "General Services Administration - Federal Facilities", "url": "https://www.gsa.gov/real-estate"}, {"name": "Government Accountability Office - Meeting Venues", "url": "https://www.gao.gov/products/government-meetings"}, {"name": "State Department - Meeting Protocols", "url": "https://www.state.gov/about/facilities/"}]'),

('2025-03-21-state-dept-trump-property-meetings', 12, 'multiple_choice', 'Government', 'How do State Department meeting locations affect diplomatic relationships?', 'They have no effect on diplomacy', 'Venue choices can send signals about priorities and values', 'Only formal venues matter', 'Location is irrelevant to diplomacy', 'Venue choices can send signals about priorities and values', 'Diplomatic protocol and venue choices communicate important messages.', 'State Department meeting locations can affect diplomatic relationships because venue choices send signals about priorities, values, and respect for diplomatic protocol. Using presidential properties may suggest prioritizing personal business interests over diplomatic neutrality.', '["diplomatic protocol", "venue significance", "international relations"]', '[{"name": "U.S. Department of State - Diplomatic Protocol", "url": "https://www.state.gov/protocol/"}, {"name": "Foreign Policy - Diplomatic Venues", "url": "https://foreignpolicy.com/diplomatic-protocol/"}, {"name": "Congressional Research Service - Diplomatic Relations", "url": "https://crsreports.congress.gov/product/pdf/R/R46656"}]'),

('2025-03-21-state-dept-trump-property-meetings', 13, 'multiple_choice', 'Constitutional Law', 'What role does congressional consent play in the Emoluments Clause?', 'Congress has no role in emoluments', 'Congress can provide consent for certain payments or benefits', 'Only the courts can authorize emoluments', 'Presidential discretion determines emoluments', 'Congress can provide consent for certain payments or benefits', 'The Constitution allows congressional consent for some emoluments.', 'The Emoluments Clause allows Congress to provide consent for certain payments or benefits that would otherwise be prohibited. This congressional role ensures legislative oversight of potential conflicts between official duties and personal financial interests.', '["congressional consent", "Emoluments Clause", "legislative oversight"]', '[{"name": "Constitution - Emoluments Clause Text", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C8-1/"}, {"name": "Congressional Research Service - Emoluments Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R44334"}, {"name": "Government Accountability Office - Constitutional Issues", "url": "https://www.gao.gov/products/gao-18-253"}]'),

('2025-03-21-state-dept-trump-property-meetings', 14, 'multiple_choice', 'Government', 'What transparency requirements exist for government travel and meeting expenses?', 'All expenses are classified', 'Public disclosure through databases and reporting requirements', 'No transparency requirements', 'Only congressional access', 'Public disclosure through databases and reporting requirements', 'Government spending is generally subject to public transparency.', 'Government travel and meeting expenses are subject to transparency requirements including public disclosure through spending databases, agency reporting requirements, and freedom of information laws that allow citizens to track how public funds are used.', '["transparency requirements", "public disclosure", "government spending"]', '[{"name": "USAspending.gov - Government Spending Database", "url": "https://www.usaspending.gov/"}, {"name": "Freedom of Information Act", "url": "https://www.foia.gov/"}, {"name": "Congressional Research Service - Government Transparency", "url": "https://crsreports.congress.gov/product/pdf/R/R40516"}]'),

('2025-03-21-state-dept-trump-property-meetings', 15, 'multiple_choice', 'Ethics', 'How do conflicts of interest affect public trust in government?', 'They improve public trust', 'They undermine confidence in fair and impartial governance', 'They have no effect on public opinion', 'They only affect international relations', 'They undermine confidence in fair and impartial governance', 'Conflicts suggest officials prioritize personal interests over public service.', 'Conflicts of interest undermine public trust by suggesting that officials prioritize personal financial interests over public service. This erosion of confidence threatens democratic legitimacy and citizen engagement with government institutions.', '["public trust", "conflicts of interest", "democratic legitimacy"]', '[{"name": "Pew Research - Trust in Government", "url": "https://www.pewresearch.org/politics/2019/09/19/public-trust-in-government-1958-2019/"}, {"name": "American Bar Association - Public Trust Survey", "url": "https://www.americanbar.org/advocacy/rule_of_law/public-trust-survey/"}, {"name": "Brookings Institution - Government Trust", "url": "https://www.brookings.edu/research/public-trust-government/"}]'),

('2025-03-21-state-dept-trump-property-meetings', 16, 'multiple_choice', 'Constitutional Law', 'What constitutional powers allow Congress to address emoluments violations?', 'No congressional powers exist', 'Impeachment, oversight, and legislative authority', 'Only judicial powers apply', 'Executive branch self-regulation only', 'Impeachment, oversight, and legislative authority', 'Congress has multiple constitutional tools to address violations.', 'Congress can address emoluments violations through impeachment power for high crimes and misdemeanors, oversight authority to investigate violations, and legislative power to clarify or strengthen emoluments restrictions and enforcement mechanisms.', '["impeachment power", "congressional oversight", "legislative authority"]', '[{"name": "Constitution - Impeachment Powers", "url": "https://constitution.congress.gov/browse/essay/artI-S2-C5-1/"}, {"name": "Congressional Research Service - Congressional Powers", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "House Judiciary Committee - Constitutional Authority", "url": "https://judiciary.house.gov/about"}]'),

('2025-03-21-state-dept-trump-property-meetings', 17, 'multiple_choice', 'Government', 'How do international standards view mixing official duties with personal business interests?', 'International standards encourage such mixing', 'International standards prohibit conflicts of interest and require separation', 'No international standards exist', 'Standards only apply to authoritarian governments', 'International standards prohibit conflicts of interest and require separation', 'Global governance standards emphasize integrity and public service.', 'International standards prohibit mixing official duties with personal business interests, requiring clear separation between public service and private financial gain. These standards promote integrity, transparency, and public trust in democratic governance worldwide.', '["international standards", "governance integrity", "public service ethics"]', '[{"name": "United Nations - Public Service Ethics", "url": "https://www.un.org/development/desa/dpadm/themes/governance-and-public-administration/public-service-ethics"}, {"name": "OECD - Government Integrity", "url": "https://www.oecd.org/governance/integrity/"}, {"name": "Transparency International - Corruption Index", "url": "https://www.transparency.org/en/cpi"}]'),

-- True/False Questions (5 questions)
('2025-03-21-state-dept-trump-property-meetings', 18, 'true_false', 'Constitutional Law', 'The Emoluments Clause only applies to payments from foreign governments, not domestic business.', '', '', '', '', 'False', 'The clause covers various forms of benefits and creates broader conflict concerns.', 'False. While the Foreign Emoluments Clause specifically addresses foreign payments, broader constitutional principles and ethics laws also govern domestic conflicts of interest. Officials must avoid situations where personal business interests could influence official duties regardless of the source.', '["Emoluments Clause", "conflict of interest", "domestic business"]', '[{"name": "Constitution - Emoluments Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C8-1/"}, {"name": "Office of Government Ethics - Standards", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "Congressional Research Service - Ethics Laws", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-03-21-state-dept-trump-property-meetings', 19, 'true_false', 'Ethics', 'Government officials should avoid even the appearance of impropriety in their conduct.', '', '', '', '', 'True', 'Ethics standards require avoiding both actual and apparent conflicts.', 'True. Government ethics standards require officials to avoid even the appearance of impropriety because public trust depends on government actions appearing legitimate and ethical. Citizens must have confidence that officials act in the public interest rather than for personal gain.', '["appearance of impropriety", "government ethics", "public trust"]', '[{"name": "Office of Government Ethics - Appearance Standards", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "American Bar Association - Ethics Rules", "url": "https://www.americanbar.org/groups/professional_responsibility/"}, {"name": "Congressional Research Service - Ethics Standards", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-03-21-state-dept-trump-property-meetings', 20, 'true_false', 'Government', 'Using presidential properties for government meetings saves taxpayer money.', '', '', '', '', 'False', 'Such arrangements often cost more and create conflicts of interest.', 'False. Using presidential properties for government meetings often costs more than government facilities or neutral venues, while creating conflicts of interest and the appearance of corruption. Government facilities and established venues typically provide more cost-effective and ethically appropriate options.', '["government meetings", "taxpayer costs", "presidential properties"]', '[{"name": "Government Accountability Office - Government Spending", "url": "https://www.gao.gov/products/government-meetings"}, {"name": "General Services Administration - Federal Facilities", "url": "https://www.gsa.gov/real-estate"}, {"name": "Congressional Research Service - Government Contracting", "url": "https://crsreports.congress.gov/product/pdf/R/R40516"}]'),

('2025-03-21-state-dept-trump-property-meetings', 21, 'true_false', 'Constitutional Law', 'Congress has authority to investigate potential violations of the Emoluments Clause.', '', '', '', '', 'True', 'Congressional oversight includes investigating constitutional violations.', 'True. Congress has constitutional authority to investigate potential Emoluments Clause violations through its oversight powers, investigative authority, and responsibility to ensure compliance with constitutional requirements. This includes subpoena power and budget oversight.', '["congressional oversight", "Emoluments Clause", "constitutional authority"]', '[{"name": "Congressional Research Service - Congressional Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "House Oversight Committee", "url": "https://oversight.house.gov/about"}, {"name": "Government Accountability Office - Congressional Authority", "url": "https://www.gao.gov/about/congressional-oversight"}]'),

('2025-03-21-state-dept-trump-property-meetings', 22, 'true_false', 'Government', 'All government meeting expenses are automatically exempt from public disclosure requirements.', '', '', '', '', 'False', 'Government spending is generally subject to transparency requirements.', 'False. Government meeting expenses are generally subject to public disclosure requirements through spending databases, agency reporting, and Freedom of Information Act requests. Only specific national security information may be classified or redacted while maintaining transparency about general spending patterns.', '["government spending", "public disclosure", "transparency requirements"]', '[{"name": "USAspending.gov - Government Transparency", "url": "https://www.usaspending.gov/"}, {"name": "Freedom of Information Act", "url": "https://www.foia.gov/"}, {"name": "Congressional Research Service - Government Transparency", "url": "https://crsreports.congress.gov/product/pdf/R/R40516"}]'),

-- Short Answer Questions (3 questions)
('2025-03-21-state-dept-trump-property-meetings', 23, 'short_answer', 'Government', 'Who serves as Secretary of State in the current administration?', '', '', '', '', 'Marco Rubio', 'This former Florida Senator leads the State Department.', 'Marco Rubio serves as Secretary of State in the current administration. The former Florida Senator and presidential candidate was confirmed in 2025 and heads the State Department, responsible for foreign policy and diplomatic relations.', '["Marco Rubio", "Secretary of State", "State Department"]', '[{"name": "U.S. Department of State", "url": "https://www.state.gov/secretary/"}, {"name": "Associated Press - Rubio Confirmation", "url": "https://apnews.com/article/marco-rubio-secretary-state"}, {"name": "Senate Foreign Relations Committee", "url": "https://www.foreign.senate.gov/"}]'),

('2025-03-21-state-dept-trump-property-meetings', 24, 'short_answer', 'Constitutional Law', 'What constitutional clause prohibits federal officials from receiving payments from foreign governments without congressional consent?', '', '', '', '', 'Foreign Emoluments Clause', 'This constitutional provision is found in Article I, Section 9.', 'The Foreign Emoluments Clause in Article I, Section 9 prohibits federal officials from receiving payments, gifts, or benefits from foreign governments without congressional consent. This prevents conflicts between personal financial interests and official duties.', '["Foreign Emoluments Clause", "Article I Section 9", "constitutional provision"]', '[{"name": "Constitution - Emoluments Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S9-C8-1/"}, {"name": "Congressional Research Service - Emoluments Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R44334"}, {"name": "Government Accountability Office - Constitutional Issues", "url": "https://www.gao.gov/products/gao-18-253"}]'),

('2025-03-21-state-dept-trump-property-meetings', 25, 'short_answer', 'Ethics', 'What federal agency provides ethics guidance to executive branch officials?', '', '', '', '', 'Office of Government Ethics', 'This independent agency oversees executive branch ethics compliance.', 'The Office of Government Ethics (OGE) provides ethics guidance to executive branch officials. This independent agency helps ensure that government officials comply with ethics laws and act in the public interest rather than for personal financial gain.', '["Office of Government Ethics", "OGE", "ethics guidance"]', '[{"name": "Office of Government Ethics", "url": "https://www.oge.gov/"}, {"name": "OGE - Standards of Conduct", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Standards+of+Ethical+Conduct"}, {"name": "Congressional Research Service - Ethics Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]');