SELECT 
    topic_id,
    COUNT(*) as total_questions,
    COUNT(CASE WHEN difficulty_level = 'easy' THEN 1 END) as easy,
    COUNT(CASE WHEN difficulty_level = 'medium' THEN 1 END) as medium,
    COUNT(CASE WHEN difficulty_level = 'hard' THEN 1 END) as hard,
    COUNT(CASE WHEN question_type = 'multiple_choice' THEN 1 END) as multiple_choice,
    COUNT(CASE WHEN question_type = 'true_false' THEN 1 END) as true_false
FROM questions 
WHERE topic_id IN (
    'palantir-government-contracts-2025',
    'anduril-border-surveillance-2025',
    'clearview-ai-database-2025', 
    'spacex-starlink-military-surveillance',
    'aws-nsa-cloud-contract-2025',
    'surveillance-industrial-complex-2025'
)
GROUP BY topic_id
ORDER BY topic_id;

