INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-03-12-transportation-secretary-privatization (Multiple Choice - 17 questions)
('2025-03-12-transportation-secretary-privatization', 1, 'multiple_choice', 'Public Policy', 'Who serves as Transportation Secretary in the current administration?', 'Pete Buttigieg', 'Sean Duffy', 'Elaine Chao', 'Ray LaHood', 'Sean Duffy', 'This former congressman was appointed by Trump in 2025.', 'Sean Duffy serves as Transportation Secretary in the Trump administration. A former congressman from Wisconsin and Fox News host, Duffy has proposed infrastructure policies that prioritize private investment and toll road development over traditional public transportation funding.', '["Transportation Secretary", "Sean Duffy", "Cabinet appointment"]', '[{"name": "U.S. Department of Transportation - Secretary Biography", "url": "https://www.transportation.gov/about/secretary"}, {"name": "Senate Commerce Committee Confirmation Hearing", "url": "https://www.commerce.senate.gov/2025/confirmation-hearings"}, {"name": "Reuters - Duffy Transportation Appointment", "url": "https://www.reuters.com/world/us/sean-duffy-transportation-secretary/"}]'),

('2025-03-12-transportation-secretary-privatization', 2, 'multiple_choice', 'Economy', 'What is the primary funding mechanism in Duffy\'s infrastructure proposal?', 'Federal gasoline taxes', 'Private toll roads and investor financing', 'State bond issues', 'Municipal property taxes', 'Private toll roads and investor financing', 'This approach shifts costs from government to private investors and users.', 'Duffy\'s infrastructure proposal emphasizes private toll roads and investor financing rather than traditional public funding mechanisms. This approach relies on private companies building and operating roads in exchange for collecting tolls from users over multi-decade periods.', '["private financing", "toll roads", "infrastructure funding"]', '[{"name": "Department of Transportation - Infrastructure Funding", "url": "https://www.transportation.gov/policy/transportation-policy/infrastructure-funding"}, {"name": "Congressional Budget Office - Highway Financing", "url": "https://www.cbo.gov/topics/transportation/highway-financing"}, {"name": "Brookings Institution - Infrastructure Investment", "url": "https://www.brookings.edu/research/infrastructure-investment-funding/"}]'),

('2025-03-12-transportation-secretary-privatization', 3, 'multiple_choice', 'Government', 'What federal department oversees national transportation policy?', 'Department of Commerce', 'Department of Transportation', 'Department of Energy', 'Department of the Interior', 'Department of Transportation', 'This cabinet-level department was created in 1966.', 'The Department of Transportation (DOT) oversees national transportation policy, including highways, aviation, railroads, maritime, and public transit systems. DOT coordinates federal transportation funding and regulates transportation safety across all modes of travel.', '["Department of Transportation", "DOT", "federal oversight"]', '[{"name": "U.S. Department of Transportation Official Website", "url": "https://www.transportation.gov/"}, {"name": "Congressional Research Service - DOT Overview", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}, {"name": "Federal Highway Administration", "url": "https://www.fhwa.dot.gov/"}]'),

('2025-03-12-transportation-secretary-privatization', 4, 'multiple_choice', 'Public Policy', 'How do toll roads typically affect low-income drivers?', 'They provide equal access for all income levels', 'They create disproportionate financial burden on low-income families', 'They only affect wealthy drivers', 'They have no income-related impacts', 'They create disproportionate financial burden on low-income families', 'Tolls represent a larger percentage of income for lower-income households.', 'Toll roads create disproportionate financial burden on low-income families because tolls represent a larger percentage of their income compared to wealthy drivers. This can force low-income drivers onto slower, less convenient routes or limit their mobility for work and essential services.', '["economic inequality", "transportation access", "regressive taxation"]', '[{"name": "Urban Institute - Transportation Equity Study", "url": "https://www.urban.org/research/publication/transportation-equity-tolling"}, {"name": "Brookings Institution - Transportation Access", "url": "https://www.brookings.edu/research/transportation-access-low-income/"}, {"name": "Congressional Research Service - Highway Equity", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 5, 'multiple_choice', 'Economy', 'What is the typical length of private toll road contracts?', '5-10 years', '15-20 years', '30-75 years', '100+ years', '30-75 years', 'These contracts span multiple decades to allow investor cost recovery.', 'Private toll road contracts typically span 30-75 years to allow private investors to recover construction costs and earn profits from toll collection. These long-term agreements can limit government flexibility to modify transportation policies or pricing over many decades.', '["contract length", "long-term agreements", "investor recovery"]', '[{"name": "Federal Highway Administration - Public-Private Partnerships", "url": "https://www.fhwa.dot.gov/ipd/p3/"}, {"name": "Government Accountability Office - Toll Road Contracts", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "Congressional Research Service - Transportation PPPs", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}]'),

('2025-03-12-transportation-secretary-privatization', 6, 'multiple_choice', 'Public Policy', 'What happens to public oversight when roads are privatized?', 'Oversight increases significantly', 'Oversight becomes limited by contract terms', 'No change in oversight', 'Complete elimination of oversight', 'Oversight becomes limited by contract terms', 'Private contracts can restrict government\'s ability to regulate operations.', 'When roads are privatized, public oversight becomes limited by contract terms negotiated with private companies. Governments may lose ability to control toll rates, maintenance standards, or operational decisions that were previously under direct public control.', '["public oversight", "contract limitations", "democratic control"]', '[{"name": "Government Accountability Office - Transportation Privatization", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "Congressional Research Service - Public-Private Infrastructure", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}, {"name": "Brookings Institution - Infrastructure Governance", "url": "https://www.brookings.edu/research/infrastructure-governance/"}]'),

('2025-03-12-transportation-secretary-privatization', 7, 'multiple_choice', 'Government', 'Which level of government traditionally builds and maintains highways?', 'Federal government exclusively', 'State governments with federal support', 'Local governments only', 'Private companies only', 'State governments with federal support', 'This represents the classic federal-state partnership in transportation.', 'State governments traditionally build and maintain highways with significant federal funding support through programs like the Federal Highway Trust Fund. This federal-state partnership has characterized American highway development since the Interstate Highway System was created in the 1950s.', '["federal-state partnership", "highway funding", "traditional model"]', '[{"name": "Federal Highway Administration - Federal-Aid Highway Program", "url": "https://www.fhwa.dot.gov/federal-aidessentials/"}, {"name": "Congressional Research Service - Highway Funding", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}, {"name": "American Association of State Highway Officials", "url": "https://www.transportation.org/"}]'),

('2025-03-12-transportation-secretary-privatization', 8, 'multiple_choice', 'Economy', 'What motivates private companies to invest in toll roads?', 'Public service commitment', 'Long-term profit potential from toll collection', 'Government contracts requiring participation', 'Environmental benefits', 'Long-term profit potential from toll collection', 'Private investment is driven by expected financial returns.', 'Private companies invest in toll roads primarily for long-term profit potential from toll collection over decades. The guaranteed revenue stream from mandatory tolls provides predictable returns that make infrastructure investment attractive to private investors and pension funds.', '["profit motive", "revenue streams", "investment returns"]', '[{"name": "Brookings Institution - Infrastructure Investment", "url": "https://www.brookings.edu/research/private-infrastructure-investment/"}, {"name": "Congressional Budget Office - Transportation Finance", "url": "https://www.cbo.gov/topics/transportation"}, {"name": "Government Accountability Office - Private Infrastructure", "url": "https://www.gao.gov/products/gao-18-386"}]'),

('2025-03-12-transportation-secretary-privatization', 9, 'multiple_choice', 'Public Policy', 'How does privatization affect public transportation funding?', 'It increases public transportation investment', 'It often diverts resources away from public transportation', 'It has no effect on public transportation', 'It only affects highway funding', 'It often diverts resources away from public transportation', 'Toll road privatization typically prioritizes highways over public transit.', 'Privatization often diverts transportation resources away from public transportation systems like buses and trains toward profitable toll roads. Private investors prefer highway projects that generate toll revenue over public transit systems that serve broader public good but generate less profit.', '["public transportation", "resource allocation", "transit funding"]', '[{"name": "American Public Transportation Association", "url": "https://www.apta.com/research-technical-resources/transit-statistics/"}, {"name": "Urban Institute - Transportation Investment", "url": "https://www.urban.org/research/publication/public-transportation-investment"}, {"name": "Congressional Research Service - Transit Funding", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 10, 'multiple_choice', 'Government', 'What constitutional authority allows federal transportation spending?', 'General Welfare Clause', 'Commerce Clause', 'Necessary and Proper Clause', 'All of the above', 'All of the above', 'Multiple constitutional provisions support federal transportation authority.', 'Federal transportation spending derives authority from multiple constitutional sources: the Commerce Clause (regulating interstate commerce), the General Welfare Clause (promoting national welfare), and the Necessary and Proper Clause (implementing constitutional powers). Together, these provide comprehensive federal transportation authority.', '["constitutional authority", "Commerce Clause", "federal powers"]', '[{"name": "Supreme Court - Transportation Commerce Cases", "url": "https://supreme.justia.com/cases/federal/commerce-clause/"}, {"name": "Congressional Research Service - Federal Transportation Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}, {"name": "Constitution Annotated - Commerce Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S8-C3-1/"}]'),

('2025-03-12-transportation-secretary-privatization', 11, 'multiple_choice', 'Economy', 'What are typical toll rates on privatized highways?', '$1-2 for short trips', '$5-15 for moderate distances', 'Both A and B depending on distance', 'Tolls are always free', 'Both A and B depending on distance', 'Toll rates vary significantly based on distance and location.', 'Toll rates on privatized highways typically range from $1-2 for short trips to $5-15 or more for moderate distances, with some long-distance toll roads charging $20+ for complete trips. Rates are often higher than traditional public toll roads due to profit requirements.', '["toll rates", "user costs", "pricing structure"]', '[{"name": "Federal Highway Administration - Toll Facility Data", "url": "https://www.fhwa.dot.gov/ipd/tolling/"}, {"name": "International Bridge, Tunnel and Turnpike Association", "url": "https://ibtta.org/tolling-statistics"}, {"name": "Government Accountability Office - Toll Road Analysis", "url": "https://www.gao.gov/products/gao-18-386"}]'),

('2025-03-12-transportation-secretary-privatization', 12, 'multiple_choice', 'Public Policy', 'How do privatized roads affect regional economic development?', 'They always boost economic development equally', 'They can create uneven development favoring toll corridor areas', 'They have no effect on economic patterns', 'They only affect rural areas', 'They can create uneven development favoring toll corridor areas', 'Toll roads concentrate development along profitable corridors.', 'Privatized roads can create uneven economic development by concentrating investment and growth along profitable toll corridors while potentially neglecting areas that don\'t generate sufficient toll revenue. This can exacerbate regional economic inequalities over time.', '["economic development", "regional inequality", "development patterns"]', '[{"name": "Brookings Institution - Transportation Economic Development", "url": "https://www.brookings.edu/research/transportation-economic-development/"}, {"name": "Urban Institute - Infrastructure Development Effects", "url": "https://www.urban.org/research/publication/infrastructure-economic-development"}, {"name": "Congressional Research Service - Transportation Economic Impact", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 13, 'multiple_choice', 'Government', 'What oversight role does Congress have in transportation policy?', 'No role in transportation policy', 'Authorization, appropriations, and oversight of federal programs', 'Only highway construction oversight', 'State-level oversight only', 'Authorization, appropriations, and oversight of federal programs', 'Congress has comprehensive constitutional oversight responsibilities.', 'Congress has extensive oversight role in transportation policy through program authorization, budget appropriations, agency oversight hearings, and investigation of transportation programs. Congressional committees regularly review DOT activities and transportation spending effectiveness.', '["congressional oversight", "appropriations", "program authorization"]', '[{"name": "House Transportation and Infrastructure Committee", "url": "https://transportation.house.gov/"}, {"name": "Senate Commerce Committee", "url": "https://www.commerce.senate.gov/"}, {"name": "Congressional Research Service - Transportation Oversight", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 14, 'multiple_choice', 'Public Policy', 'What alternative exists to toll road privatization for infrastructure funding?', 'No alternatives exist', 'Gas taxes, bond issues, and federal grants', 'Only private investment', 'State taxes exclusively', 'Gas taxes, bond issues, and federal grants', 'Multiple public financing mechanisms exist for infrastructure.', 'Alternatives to toll road privatization include traditional gas taxes, government bond issues, federal infrastructure grants, vehicle registration fees, and general fund appropriations. These public financing mechanisms maintain democratic control over transportation policy and pricing.', '["public financing", "gas taxes", "government bonds"]', '[{"name": "Congressional Budget Office - Highway Financing Options", "url": "https://www.cbo.gov/topics/transportation/highway-financing"}, {"name": "Federal Highway Administration - Funding Sources", "url": "https://www.fhwa.dot.gov/policy/financing/"}, {"name": "Government Accountability Office - Transportation Funding", "url": "https://www.gao.gov/products/gao-21-459"}]'),

('2025-03-12-transportation-secretary-privatization', 15, 'multiple_choice', 'Economy', 'How do privatized toll roads affect competition?', 'They increase transportation competition', 'They can create monopolies over specific travel corridors', 'They have no effect on competition', 'They only affect freight transport', 'They can create monopolies over specific travel corridors', 'Private toll roads often have exclusive rights to specific routes.', 'Privatized toll roads can create monopolies over specific travel corridors because private companies typically receive exclusive rights to operate toll facilities for decades. This eliminates competition and gives companies significant power to set toll rates without market constraints.', '["monopoly power", "exclusive rights", "market competition"]', '[{"name": "Federal Trade Commission - Infrastructure Competition", "url": "https://www.ftc.gov/policy/advocacy/economic-liberty"}, {"name": "Government Accountability Office - Toll Road Competition", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "Congressional Research Service - Infrastructure Market Structure", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}]'),

('2025-03-12-transportation-secretary-privatization', 16, 'multiple_choice', 'Public Policy', 'What happens to transportation equity under privatization?', 'Equity always improves', 'Equity concerns increase due to ability-to-pay barriers', 'No change in equity', 'Only affects urban areas', 'Equity concerns increase due to ability-to-pay barriers', 'Toll roads create financial barriers that affect access equity.', 'Transportation equity concerns increase under privatization because toll roads create ability-to-pay barriers that can limit mobility for low-income individuals and families. This can restrict access to employment, healthcare, education, and other essential services for vulnerable populations.', '["transportation equity", "access barriers", "social justice"]', '[{"name": "Urban Institute - Transportation Equity", "url": "https://www.urban.org/research/publication/transportation-equity-access"}, {"name": "Brookings Institution - Infrastructure Equity", "url": "https://www.brookings.edu/research/infrastructure-equity/"}, {"name": "Federal Highway Administration - Environmental Justice", "url": "https://www.fhwa.dot.gov/environment/environmental_justice/"}]'),

('2025-03-12-transportation-secretary-privatization', 17, 'multiple_choice', 'Government', 'How can citizens influence transportation policy decisions?', 'Citizens have no influence over transportation policy', 'Public hearings, elections, and advocacy to elected officials', 'Only through private companies', 'Federal courts exclusively', 'Public hearings, elections, and advocacy to elected officials', 'Multiple democratic channels exist for citizen participation.', 'Citizens can influence transportation policy through public hearings for major projects, elections for officials who set transportation policy, advocacy to representatives and transportation agencies, and participation in regional transportation planning processes that shape infrastructure investments.', '["citizen participation", "public hearings", "democratic input"]', '[{"name": "Federal Highway Administration - Public Involvement", "url": "https://www.fhwa.dot.gov/planning/public_involvement/"}, {"name": "American Planning Association - Transportation Planning", "url": "https://www.planning.org/transportation/"}, {"name": "Congressional Research Service - Transportation Planning", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

-- True/False Questions (5 questions)
('2025-03-12-transportation-secretary-privatization', 18, 'true_false', 'Public Policy', 'Private toll roads always cost less to build than government-funded highways.', '', '', '', '', 'False', 'Private financing often costs more due to profit requirements and higher borrowing costs.', 'False. Private toll roads often cost more to build than government-funded highways because private companies must include profit margins, pay higher borrowing costs than government bonds, and add administrative overhead. Government financing through bonds typically offers lower overall costs.', '["financing costs", "government vs private", "total project costs"]', '[{"name": "Congressional Budget Office - Infrastructure Financing Costs", "url": "https://www.cbo.gov/topics/transportation/highway-financing"}, {"name": "Government Accountability Office - Public-Private Cost Comparison", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "Brookings Institution - Infrastructure Finance", "url": "https://www.brookings.edu/research/infrastructure-financing-costs/"}]'),

('2025-03-12-transportation-secretary-privatization', 19, 'true_false', 'Economy', 'Toll roads generate revenue that can fund other transportation projects.', '', '', '', '', 'True', 'Toll revenue can cross-subsidize other transportation investments when publicly controlled.', 'True. When toll roads are publicly controlled, toll revenue can fund other transportation projects including public transit, road maintenance, and new infrastructure. However, when privatized, toll revenue typically goes to private investors rather than public transportation needs.', '["revenue generation", "cross-subsidization", "public vs private control"]', '[{"name": "Federal Highway Administration - Toll Revenue Use", "url": "https://www.fhwa.dot.gov/ipd/tolling/revenue_use/"}, {"name": "International Bridge, Tunnel and Turnpike Association", "url": "https://ibtta.org/toll-revenue-investment"}, {"name": "Congressional Research Service - Transportation Revenue", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 20, 'true_false', 'Government', 'The federal government has no constitutional authority to regulate transportation.', '', '', '', '', 'False', 'The Commerce Clause provides clear federal authority over interstate transportation.', 'False. The federal government has clear constitutional authority to regulate transportation through the Commerce Clause, which covers interstate commerce. This authority has been used to create the Interstate Highway System, regulate aviation and railroads, and establish national transportation safety standards.', '["constitutional authority", "Commerce Clause", "federal powers"]', '[{"name": "Supreme Court - Commerce Clause Transportation Cases", "url": "https://supreme.justia.com/cases/federal/commerce-clause/"}, {"name": "Congressional Research Service - Federal Transportation Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}, {"name": "Constitution Annotated - Commerce Power", "url": "https://constitution.congress.gov/browse/essay/artI-S8-C3-1/"}]'),

('2025-03-12-transportation-secretary-privatization', 21, 'true_false', 'Public Policy', 'Public-private partnerships always transfer all risk from government to private companies.', '', '', '', '', 'False', 'Risk allocation in PPPs varies significantly by contract terms and project type.', 'False. Public-private partnerships involve complex risk-sharing arrangements that vary by project. While some risks transfer to private partners, governments often retain risks related to regulatory changes, public opposition, environmental issues, or project changes that affect public policy goals.', '["risk allocation", "public-private partnerships", "contract complexity"]', '[{"name": "Government Accountability Office - PPP Risk Analysis", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "Federal Highway Administration - PPP Risk Guide", "url": "https://www.fhwa.dot.gov/ipd/p3/toolkit/risk/"}, {"name": "Congressional Research Service - Infrastructure Risk", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}]'),

('2025-03-12-transportation-secretary-privatization', 22, 'true_false', 'Economy', 'Privatized highways typically charge higher tolls than publicly operated toll roads.', '', '', '', '', 'True', 'Private operators must generate profits in addition to covering costs.', 'True. Privatized highways typically charge higher tolls than publicly operated toll roads because private operators must generate profits for investors in addition to covering construction and maintenance costs. Public toll roads generally aim only to cover costs and fund transportation improvements.', '["toll pricing", "profit motive", "public vs private operations"]', '[{"name": "Government Accountability Office - Toll Rate Analysis", "url": "https://www.gao.gov/products/gao-18-386"}, {"name": "International Bridge, Tunnel and Turnpike Association", "url": "https://ibtta.org/toll-pricing-studies"}, {"name": "Congressional Research Service - Toll Road Economics", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}]'),

-- Short Answer Questions (3 questions)
('2025-03-12-transportation-secretary-privatization', 23, 'short_answer', 'Government', 'Which cabinet department oversees federal transportation policy and highway funding?', '', '', '', '', 'Department of Transportation', 'This department was created in 1966 to coordinate federal transportation programs.', 'The Department of Transportation (DOT) oversees federal transportation policy and highway funding. Created in 1966, DOT coordinates transportation programs across all modes including highways, aviation, railroads, maritime, and public transit systems.', '["Department of Transportation", "DOT", "cabinet department"]', '[{"name": "U.S. Department of Transportation", "url": "https://www.transportation.gov/"}, {"name": "DOT Historical Overview", "url": "https://www.transportation.gov/mission/about-us"}, {"name": "Congressional Research Service - DOT Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 24, 'short_answer', 'Public Policy', 'What constitutional clause provides the primary federal authority for regulating interstate transportation?', '', '', '', '', 'Commerce Clause', 'This constitutional provision allows Congress to regulate commerce between states.', 'The Commerce Clause provides the primary federal authority for regulating interstate transportation. Found in Article I, Section 8 of the Constitution, this clause allows Congress to regulate commerce among the states, including transportation systems that cross state boundaries.', '["Commerce Clause", "constitutional authority", "interstate regulation"]', '[{"name": "Constitution Annotated - Commerce Clause", "url": "https://constitution.congress.gov/browse/essay/artI-S8-C3-1/"}, {"name": "Supreme Court - Commerce Clause Cases", "url": "https://supreme.justia.com/cases/federal/commerce-clause/"}, {"name": "Congressional Research Service - Constitutional Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R46659"}]'),

('2025-03-12-transportation-secretary-privatization', 25, 'short_answer', 'Economy', 'What type of financing mechanism does Transportation Secretary Sean Duffy emphasize in his infrastructure proposals?', '', '', '', '', 'Private toll roads', 'This approach relies on private investment and user fees rather than government funding.', 'Transportation Secretary Sean Duffy emphasizes private toll roads and investor financing in his infrastructure proposals. This approach shifts funding from traditional government sources to private companies that build roads in exchange for collecting tolls from users over long-term contracts.', '["private toll roads", "private financing", "infrastructure funding"]', '[{"name": "Department of Transportation - Infrastructure Policy", "url": "https://www.transportation.gov/policy"}, {"name": "Federal Highway Administration - Private Investment", "url": "https://www.fhwa.dot.gov/ipd/p3/"}, {"name": "Congressional Research Service - Transportation Finance", "url": "https://crsreports.congress.gov/product/pdf/R/R44571"}]');