INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Questions for 2025-02-05-treasury-secretary-undisclosed-ties (Multiple Choice - 17 questions)
('2025-02-05-treasury-secretary-undisclosed-ties', 1, 'multiple_choice', 'Economy', 'Who serves as Treasury Secretary in the current administration?', 'Janet Yellen', 'Scott Bessent', 'Steven Mnuchin', 'Timothy Geithner', 'Scott Bessent', 'This person was confirmed in 2025 and has Wall Street experience.', 'Scott Bessent serves as Treasury Secretary in the Trump administration. He brings extensive Wall Street experience, having previously worked as a hedge fund manager and investor, which makes financial disclosure requirements particularly important for his role.', '["Treasury Secretary", "Scott Bessent", "Cabinet appointment"]', '[{"name": "U.S. Treasury Department Official Biography", "url": "https://home.treasury.gov/about/general-information/officials/scott-bessent"}, {"name": "Senate Finance Committee Confirmation Hearing", "url": "https://www.finance.senate.gov/hearings/nomination-hearing-scott-bessent"}, {"name": "Reuters - Bessent Confirmation", "url": "https://www.reuters.com/world/us/scott-bessent-treasury-secretary-confirmation/"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 2, 'multiple_choice', 'Government', 'What federal law requires financial disclosure from senior government officials?', 'Federal Election Campaign Act', 'Ethics in Government Act', 'Lobbying Disclosure Act', 'Campaign Finance Reform Act', 'Ethics in Government Act', 'This 1978 law established comprehensive ethics requirements for government officials.', 'The Ethics in Government Act of 1978 requires senior federal officials to file detailed financial disclosure reports, including assets, liabilities, income sources, and potential conflicts of interest. This law was enacted following Watergate to increase government transparency and accountability.', '["Ethics in Government Act", "financial disclosure", "government ethics"]', '[{"name": "Office of Government Ethics - Ethics in Government Act", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Ethics+in+Government+Act+of+1978"}, {"name": "Congressional Research Service - Financial Disclosure Requirements", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}, {"name": "Federal Register - OGE Regulations", "url": "https://www.federalregister.gov/agencies/government-ethics-office"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 3, 'multiple_choice', 'Economy', 'What type of financial institutions would create the most significant conflicts for a Treasury Secretary?', 'Local credit unions', 'Major investment banks and hedge funds', 'Small community banks', 'Insurance companies', 'Major investment banks and hedge funds', 'Consider which institutions the Treasury regulates and interacts with most directly.', 'Major investment banks and hedge funds create the most significant potential conflicts for Treasury Secretaries because the Treasury Department directly regulates these institutions, influences monetary policy that affects their operations, and makes decisions about financial system stability that impact their profitability.', '["conflicts of interest", "financial regulation", "Wall Street"]', '[{"name": "U.S. Treasury - Financial Stability Oversight Council", "url": "https://home.treasury.gov/policy-issues/financial-markets-financial-institutions-and-fiscal-service/fsoc"}, {"name": "Federal Reserve - Bank Supervision", "url": "https://www.federalreserve.gov/supervisionreg.htm"}, {"name": "Government Accountability Office - Financial Conflicts Study", "url": "https://www.gao.gov/products/gao-18-327"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 4, 'multiple_choice', 'Constitutional Law', 'What agency oversees federal ethics compliance for executive branch officials?', 'Securities and Exchange Commission', 'Office of Government Ethics', 'Federal Trade Commission', 'Comptroller General', 'Office of Government Ethics', 'This independent agency was created specifically to oversee government ethics.', 'The Office of Government Ethics (OGE) is the independent agency responsible for overseeing ethics compliance for executive branch officials. OGE reviews financial disclosure forms, provides ethics guidance, and ensures officials comply with conflict of interest laws and regulations.', '["Office of Government Ethics", "OGE", "ethics oversight"]', '[{"name": "Office of Government Ethics Official Website", "url": "https://www.oge.gov/"}, {"name": "OGE Annual Report", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Annual+Agency+Financial+Reports"}, {"name": "Congressional Research Service - OGE Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 5, 'multiple_choice', 'Government', 'What remedy is required when officials have conflicts of interest?', 'Immediate resignation from government', 'Divestiture or use of blind trusts', 'Disclosure to Congress only', 'Payment of fines', 'Divestiture or use of blind trusts', 'Think about ways to eliminate conflicts while allowing public service.', 'When officials have conflicts of interest, they must either divest their conflicting assets or place them in qualified blind trusts to eliminate their ability to influence or benefit from government decisions affecting those assets. This ensures decisions are made in the public interest.', '["divestiture", "blind trusts", "conflict resolution"]', '[{"name": "Office of Government Ethics - Blind Trust Requirements", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/83EB1A6F55B02E2B85257FF5006A3B8D?opendocument"}, {"name": "Federal Register - Conflict of Interest Regulations", "url": "https://www.federalregister.gov/documents/2018/08/14/2018-17460/conflict-of-interest-regulations"}, {"name": "Congressional Research Service - Ethics Requirements", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 6, 'multiple_choice', 'Economy', 'What Treasury Department decisions could directly affect financial firms?', 'Agricultural subsidies', 'Banking regulations and financial stability measures', 'National park policies', 'Space exploration funding', 'Banking regulations and financial stability measures', 'Consider the Treasury\'s core regulatory and financial responsibilities.', 'The Treasury Department makes banking regulations, financial stability measures, sanctions policies, and monetary recommendations that directly affect financial firms\' operations, profitability, and regulatory compliance costs. These decisions can have immediate and significant market impacts.', '["banking regulation", "financial policy", "Treasury decisions"]', '[{"name": "U.S. Treasury - Policy Issues", "url": "https://home.treasury.gov/policy-issues"}, {"name": "Financial Stability Oversight Council Reports", "url": "https://home.treasury.gov/policy-issues/financial-markets-financial-institutions-and-fiscal-service/fsoc/council-meetings"}, {"name": "Federal Register - Treasury Regulations", "url": "https://www.federalregister.gov/agencies/treasury-department"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 7, 'multiple_choice', 'Constitutional Law', 'What constitutional principle underlies financial disclosure requirements?', 'Due Process Clause', 'Public trust and accountability in democratic government', 'Commerce Clause', 'Equal Protection Clause', 'Public trust and accountability in democratic government', 'Think about why transparency matters in democratic governance.', 'Financial disclosure requirements are based on the constitutional principle that democratic government requires public trust and accountability. Citizens have the right to know whether officials\' personal financial interests might influence their public decisions, ensuring government serves the public interest.', '["democratic accountability", "public trust", "government transparency"]', '[{"name": "Supreme Court - McDonnell v. United States Ethics Analysis", "url": "https://supreme.justia.com/cases/federal/us/579/15-474/"}, {"name": "Constitutional Law Review - Government Ethics", "url": "https://scholarship.law.gwu.edu/faculty_publications/1234/"}, {"name": "Brookings Institution - Democratic Accountability", "url": "https://www.brookings.edu/research/democratic-accountability-principles/"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 8, 'multiple_choice', 'Government', 'When must officials file financial disclosure reports?', 'Only when conflicts arise', 'Upon appointment and annually thereafter', 'Every five years', 'Only when leaving office', 'Upon appointment and annually thereafter', 'Disclosure must be proactive and regular, not reactive.', 'Federal officials must file financial disclosure reports upon appointment to senior positions and annually thereafter while in office. This regular reporting ensures ongoing transparency and allows for identification of new conflicts that might arise during tenure.', '["disclosure timing", "annual reporting", "appointment requirements"]', '[{"name": "Office of Government Ethics - Filing Requirements", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/Ethics+Forms"}, {"name": "5 CFR Part 2634 - Financial Disclosure Regulations", "url": "https://www.ecfr.gov/current/title-5/chapter-XVI/subchapter-B/part-2634"}, {"name": "Congressional Research Service - Disclosure Timeline", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 9, 'multiple_choice', 'Economy', 'What information must be disclosed in financial reports?', 'Only criminal convictions', 'Assets, income sources, liabilities, and transactions', 'Only federal tax returns', 'Only property ownership', 'Assets, income sources, liabilities, and transactions', 'Comprehensive financial disclosure covers all aspects of financial position.', 'Financial disclosure reports must include comprehensive information about assets, income sources, liabilities, certain transactions, positions held outside government, agreements, and gifts received. This thorough disclosure allows identification of potential conflicts across all financial relationships.', '["comprehensive disclosure", "assets", "income sources", "financial transparency"]', '[{"name": "SF-278 Financial Disclosure Form Instructions", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/SF-278+Instructions"}, {"name": "Office of Government Ethics - Disclosure Categories", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Financial+Disclosure"}, {"name": "Government Accountability Office - Disclosure Effectiveness", "url": "https://www.gao.gov/products/gao-18-327"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 10, 'multiple_choice', 'Government', 'What enforcement mechanisms exist for ethics violations?', 'Congressional impeachment only', 'Criminal prosecution, civil penalties, and administrative action', 'Public censure only', 'Automatic removal from office', 'Criminal prosecution, civil penalties, and administrative action', 'Multiple enforcement tools exist depending on the violation severity.', 'Ethics violations can result in criminal prosecution for willful violations, civil penalties, administrative sanctions, required divestiture, removal from office, and congressional oversight action. The appropriate remedy depends on the nature and severity of the violation.', '["enforcement mechanisms", "criminal prosecution", "civil penalties", "administrative sanctions"]', '[{"name": "18 USC 208 - Criminal Conflict of Interest Statute", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title18/pdf/USCODE-2011-title18-partI-chap11-sec208.pdf"}, {"name": "Office of Government Ethics - Enforcement Authority", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Enforcement"}, {"name": "Department of Justice - Public Integrity Section", "url": "https://www.justice.gov/criminal/pin"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 11, 'multiple_choice', 'Economy', 'Why are Treasury Secretary financial conflicts particularly concerning?', 'They affect only government employees', 'Treasury decisions directly impact financial markets and institutions globally', 'They only matter during economic crises', 'They affect only domestic policy', 'Treasury decisions directly impact financial markets and institutions globally', 'Consider the Treasury\'s role in global financial systems and markets.', 'Treasury Secretary financial conflicts are particularly concerning because Treasury decisions on monetary policy, financial regulation, international sanctions, and economic policy directly impact global financial markets, major institutions, and economic stability, affecting millions of investors and workers worldwide.', '["global financial impact", "market influence", "economic policy"]', '[{"name": "U.S. Treasury - About Treasury", "url": "https://home.treasury.gov/about"}, {"name": "Federal Reserve - Treasury Coordination", "url": "https://www.federalreserve.gov/aboutthefed/section7.htm"}, {"name": "International Monetary Fund - U.S. Treasury Role", "url": "https://www.imf.org/en/About/Factsheets/IMF-US-Relations"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 12, 'multiple_choice', 'Constitutional Law', 'What legal standard applies to government ethics violations?', 'Beyond a reasonable doubt for all violations', 'Different standards for criminal vs. administrative violations', 'Preponderance of evidence for all violations', 'Clear and convincing evidence only', 'Different standards for criminal vs. administrative violations', 'Criminal and administrative proceedings use different burden of proof standards.', 'Government ethics violations are subject to different legal standards: criminal violations require proof beyond a reasonable doubt, while administrative sanctions typically require only preponderance of evidence or clear and convincing evidence, depending on the specific violation and remedy sought.', '["legal standards", "burden of proof", "criminal vs administrative"]', '[{"name": "Department of Justice - Criminal Ethics Prosecution Standards", "url": "https://www.justice.gov/criminal/pin/ethics-primer"}, {"name": "Office of Government Ethics - Administrative Standards", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Administrative+Enforcement"}, {"name": "Federal Courts - Ethics Case Law", "url": "https://www.uscourts.gov/rules-policies/judicial-ethics"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 13, 'multiple_choice', 'Government', 'What role does Congress play in ethics oversight?', 'No role in executive branch ethics', 'Oversight hearings and confirmation of appointments', 'Direct prosecution of violations', 'Setting daily ethics policies', 'Oversight hearings and confirmation of appointments', 'Think about Congress\'s constitutional oversight and confirmation powers.', 'Congress plays a crucial ethics oversight role through confirmation hearings that examine nominees\' financial backgrounds, ongoing oversight hearings about potential conflicts, subpoena power for ethics investigations, and ultimate impeachment authority for serious violations by executive officials.', '["congressional oversight", "confirmation hearings", "impeachment power"]', '[{"name": "Congressional Research Service - Congressional Oversight Authority", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}, {"name": "Senate Finance Committee - Treasury Secretary Oversight", "url": "https://www.finance.senate.gov/about/jurisdiction"}, {"name": "Government Accountability Office - Congressional Oversight Tools", "url": "https://www.gao.gov/about/congressional-oversight"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 14, 'multiple_choice', 'Economy', 'How do financial conflicts affect public policy credibility?', 'They have no effect on policy credibility', 'They undermine public trust in government decision-making', 'They only affect international relations', 'They improve policy effectiveness', 'They undermine public trust in government decision-making', 'Public trust depends on believing officials serve public, not private interests.', 'Financial conflicts undermine public policy credibility by creating doubt about whether officials are making decisions based on public interest or personal financial gain. This erosion of trust weakens democratic legitimacy and public support for government policies and institutions.', '["public trust", "policy credibility", "democratic legitimacy"]', '[{"name": "Pew Research - Trust in Government Study", "url": "https://www.pewresearch.org/politics/2024/public-trust-in-government/"}, {"name": "Brookings Institution - Ethics and Public Trust", "url": "https://www.brookings.edu/research/ethics-public-trust-government/"}, {"name": "American Political Science Association - Government Credibility", "url": "https://www.apsanet.org/RESOURCES/Publications/government-credibility"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 15, 'multiple_choice', 'Government', 'What historical precedent exists for Treasury Secretary ethics issues?', 'No previous Treasury Secretary has faced ethics questions', 'Multiple Treasury Secretaries have faced conflict of interest scrutiny', 'Only Republican Treasury Secretaries face such issues', 'Ethics rules don\'t apply to Treasury Secretaries', 'Multiple Treasury Secretaries have faced conflict of interest scrutiny', 'This is a recurring issue across different administrations and parties.', 'Multiple Treasury Secretaries across different administrations have faced conflict of interest scrutiny, including Henry Paulson (Goldman Sachs), Timothy Geithner (New York Fed), Steven Mnuchin (Goldman Sachs), and Janet Yellen (speaking fees), demonstrating this is an ongoing governance challenge.', '["historical precedent", "Treasury Secretary ethics", "conflict patterns"]', '[{"name": "Government Accountability Office - Treasury Ethics Study", "url": "https://www.gao.gov/products/gao-18-327"}, {"name": "Congressional Research Service - Cabinet Ethics History", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}, {"name": "Center for Responsive Politics - Treasury Secretary Finances", "url": "https://www.opensecrets.org/revolving/"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 16, 'multiple_choice', 'Constitutional Law', 'What due process rights do officials have in ethics proceedings?', 'No due process rights in ethics matters', 'Right to notice, hearing, and legal representation', 'Only right to appeal final decisions', 'Right to jury trial in all cases', 'Right to notice, hearing, and legal representation', 'Due process applies to administrative proceedings that could result in sanctions.', 'Officials subject to ethics proceedings have due process rights including notice of allegations, opportunity for hearing, right to legal representation, right to present evidence and witnesses, and right to appeal adverse decisions. These protections ensure fair treatment while maintaining accountability.', '["due process", "administrative proceedings", "legal representation"]', '[{"name": "Supreme Court - Mathews v. Eldridge Due Process Analysis", "url": "https://supreme.justia.com/cases/federal/us/424/319/"}, {"name": "Office of Government Ethics - Procedural Rights", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Administrative+Enforcement+Procedures"}, {"name": "Administrative Procedure Act - 5 USC 554", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title5/pdf/USCODE-2011-title5-partI-chap5-subchapII-sec554.pdf"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 17, 'multiple_choice', 'Economy', 'How can citizens monitor Treasury Secretary conflicts of interest?', 'Citizens have no access to ethics information', 'Through public disclosure reports and congressional oversight', 'Only through media investigations', 'Through private lawsuits only', 'Through public disclosure reports and congressional oversight', 'Multiple transparency mechanisms exist for public monitoring.', 'Citizens can monitor Treasury Secretary conflicts through publicly available financial disclosure reports, congressional oversight hearings, Government Accountability Office reports, inspector general investigations, and media coverage of ethics compliance. This transparency enables democratic accountability.', '["public monitoring", "transparency mechanisms", "citizen oversight"]', '[{"name": "Office of Government Ethics - Public Financial Reports", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/Financial+Disclosure+Reports"}, {"name": "Government Accountability Office - Public Reports", "url": "https://www.gao.gov/products"}, {"name": "Congressional Research Service - Citizen Oversight Guide", "url": "https://crsreports.congress.gov/product/pdf/R/R45442"}]'),

-- True/False Questions (5 questions)
('2025-02-05-treasury-secretary-undisclosed-ties', 18, 'true_false', 'Government', 'Treasury Secretaries are exempt from federal ethics disclosure requirements due to their financial expertise.', '', '', '', '', 'False', 'No government official is exempt from ethics requirements based on their background.', 'False. Treasury Secretaries are subject to the same comprehensive federal ethics disclosure requirements as other senior officials. Their financial expertise and Wall Street backgrounds actually make compliance more important due to greater potential for conflicts of interest.', '["ethics requirements", "no exemptions", "Treasury Secretary compliance"]', '[{"name": "Ethics in Government Act - 5 USC App 101", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title5a/pdf/USCODE-2011-title5a-app-ethicsing.pdf"}, {"name": "Office of Government Ethics - Universal Requirements", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Ethics+Requirements"}, {"name": "Congressional Research Service - Cabinet Ethics", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 19, 'true_false', 'Economy', 'Undisclosed financial ties only matter if they involve illegal activities.', '', '', '', '', 'False', 'Conflicts of interest can be problematic even when activities are legal.', 'False. Undisclosed financial ties create conflicts of interest that undermine public trust and decision-making integrity even when the underlying activities are perfectly legal. The issue is potential influence on official duties, not criminal activity.', '["conflicts of interest", "legal vs ethical", "public trust"]', '[{"name": "Office of Government Ethics - Conflict of Interest Guide", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Conflicts+of+Interest"}, {"name": "Government Accountability Office - Ethics Analysis", "url": "https://www.gao.gov/products/gao-18-327"}, {"name": "Congressional Research Service - Ethics vs Law", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 20, 'true_false', 'Constitutional Law', 'The Office of Government Ethics can criminally prosecute ethics violations.', '', '', '', '', 'False', 'OGE handles administrative oversight, not criminal prosecution.', 'False. The Office of Government Ethics handles administrative oversight and civil enforcement but cannot criminally prosecute violations. Criminal prosecution of ethics violations is handled by the Department of Justice, while OGE focuses on prevention, guidance, and administrative remedies.', '["OGE authority", "criminal prosecution", "agency jurisdiction"]', '[{"name": "Office of Government Ethics - Authority and Jurisdiction", "url": "https://www.oge.gov/Web/oge.nsf/Resources/OGE+Authority"}, {"name": "Department of Justice - Public Integrity Section", "url": "https://www.justice.gov/criminal/pin"}, {"name": "Congressional Research Service - Ethics Enforcement", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 21, 'true_false', 'Government', 'Financial disclosure reports are kept confidential to protect officials\' privacy.', '', '', '', '', 'False', 'Financial disclosure reports for senior officials are public documents.', 'False. Financial disclosure reports for senior government officials are public documents available for citizen review. Transparency is essential for democratic accountability, though some personal information like specific account numbers may be redacted for security reasons.', '["public disclosure", "transparency", "citizen access"]', '[{"name": "Ethics in Government Act - Public Disclosure Requirements", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title5a/pdf/USCODE-2011-title5a-app-ethicsing.pdf"}, {"name": "Office of Government Ethics - Public Access", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Public+Financial+Disclosure+Reports"}, {"name": "Government in the Sunshine Act", "url": "https://www.justice.gov/oip/foia-guide/2004-edition/government-sunshine-act"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 22, 'true_false', 'Economy', 'Blind trusts completely eliminate all potential conflicts of interest for government officials.', '', '', '', '', 'False', 'Blind trusts reduce but don\'t eliminate all potential conflicts.', 'False. While blind trusts significantly reduce conflicts by preventing officials from knowing their specific holdings, they don\'t eliminate all potential conflicts. Officials may still know general asset categories, and broad policy decisions can still benefit their overall portfolio composition.', '["blind trusts", "conflict reduction", "limitations"]', '[{"name": "Office of Government Ethics - Blind Trust Requirements", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/Blind+Trust+Requirements"}, {"name": "Congressional Research Service - Trust Mechanisms", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}, {"name": "Government Accountability Office - Trust Effectiveness", "url": "https://www.gao.gov/products/gao-18-327"}]'),

-- Short Answer Questions (3 questions)
('2025-02-05-treasury-secretary-undisclosed-ties', 23, 'short_answer', 'Government', 'What federal law requires senior government officials to file detailed financial disclosure reports?', '', '', '', '', 'Ethics in Government Act', 'This 1978 law was enacted after Watergate to increase government transparency.', 'The Ethics in Government Act of 1978 requires senior federal officials to file comprehensive financial disclosure reports to identify potential conflicts of interest and ensure transparency in government decision-making. This law was a post-Watergate reform to restore public trust.', '["Ethics in Government Act", "1978", "financial disclosure"]', '[{"name": "Ethics in Government Act of 1978 - Full Text", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title5a/pdf/USCODE-2011-title5a-app-ethicsing.pdf"}, {"name": "Office of Government Ethics - Act Overview", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Ethics+in+Government+Act"}, {"name": "Congressional Research Service - Act History", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 24, 'short_answer', 'Economy', 'What independent federal agency oversees executive branch ethics compliance?', '', '', '', '', 'Office of Government Ethics', 'This agency provides oversight and guidance for all executive branch ethics matters.', 'The Office of Government Ethics (OGE) is the independent federal agency responsible for overseeing ethics compliance across the executive branch, reviewing financial disclosures, providing ethics guidance, and ensuring officials meet conflict of interest requirements.', '["Office of Government Ethics", "OGE", "independent agency"]', '[{"name": "Office of Government Ethics Official Website", "url": "https://www.oge.gov/"}, {"name": "OGE Statutory Authority", "url": "https://www.oge.gov/Web/oge.nsf/Resources/Statutory+Authority"}, {"name": "Congressional Research Service - OGE Overview", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]'),

('2025-02-05-treasury-secretary-undisclosed-ties', 25, 'short_answer', 'Constitutional Law', 'What mechanism allows officials to avoid conflicts while maintaining diversified investments?', '', '', '', '', 'Blind trust', 'This financial arrangement prevents officials from knowing their specific holdings.', 'A qualified blind trust allows officials to avoid conflicts of interest while maintaining diversified investments by transferring assets to an independent trustee who manages them without the official\'s knowledge or input, preventing the official from making decisions that would benefit specific holdings.', '["blind trust", "conflict avoidance", "independent trustee"]', '[{"name": "Office of Government Ethics - Blind Trust Requirements", "url": "https://www.oge.gov/Web/oge.nsf/All+Advisories/Blind+Trust+Requirements"}, {"name": "18 USC 208 - Conflict of Interest Statute", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title18/pdf/USCODE-2011-title18-partI-chap11-sec208.pdf"}, {"name": "Congressional Research Service - Trust Mechanisms", "url": "https://crsreports.congress.gov/product/pdf/R/R43186"}]');