INSERT INTO "public"."questions" ("topic_id", "question_number", "question_type", "category", "question", "option_a", "option_b", "option_c", "option_d", "correct_answer", "hint", "explanation", "tags", "sources") VALUES

-- Multiple Choice Questions (17 questions)
('2025-01-25-whistleblower-retaliation-concerns', 1, 'multiple_choice', 'Civil Rights', 'What federal law provides the primary protection for government employees who report wrongdoing?', 'Whistleblower Protection Act of 1989', 'Civil Service Reform Act of 1978', 'Federal Employees Rights Act', 'Government Accountability Act', 'Whistleblower Protection Act of 1989', 'Think about the specific law named after the protection it provides.', 'The Whistleblower Protection Act of 1989 is the cornerstone federal law protecting government employees who disclose waste, fraud, abuse, or threats to public health and safety. This law was strengthened in 2012 to expand protections and clarify that disclosures to supervisors are protected.', '["whistleblower protection", "federal law", "government employees"]', '[{"name": "Office of Special Counsel - Whistleblower Protection Act Overview", "url": "https://osc.gov/Services/Pages/WPA-Whistleblowers.aspx"}, {"name": "Government Accountability Office - Whistleblower Protection", "url": "https://www.gao.gov/about/workforce/whistleblower-protection"}, {"name": "Congressional Research Service - Federal Whistleblower Protection Laws", "url": "https://crsreports.congress.gov/product/pdf/R/R44944"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 2, 'multiple_choice', 'Government', 'Which federal agency serves as the independent prosecutor for whistleblower retaliation cases?', 'Office of Personnel Management', 'Office of Special Counsel', 'Department of Justice Civil Rights Division', 'Government Accountability Office', 'Office of Special Counsel', 'This agency has "Special" in its name and focuses on personnel matters.', 'The Office of Special Counsel (OSC) is an independent federal agency that protects federal employees from prohibited personnel practices, including retaliation for whistleblowing. OSC investigates complaints and can seek corrective action for affected employees.', '["Office of Special Counsel", "whistleblower protection", "federal agencies"]', '[{"name": "Office of Special Counsel Official Website", "url": "https://osc.gov/"}, {"name": "OSC Annual Report to Congress", "url": "https://osc.gov/Resources/Pages/Annual-Reports.aspx"}, {"name": "Federal Register - OSC Jurisdiction", "url": "https://www.federalregister.gov/agencies/special-counsel-office"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 3, 'multiple_choice', 'Constitutional Law', 'What constitutional principle underlies whistleblower protection laws?', 'Due Process Clause', 'Equal Protection Clause', 'First Amendment Free Speech', 'Fourth Amendment Privacy', 'First Amendment Free Speech', 'Consider which amendment protects expression and petition for redress.', 'Whistleblower protections are rooted in the First Amendment\'s protection of free speech and the right to petition government for redress of grievances. Courts have recognized that government employees don\'t forfeit their constitutional rights when they report misconduct.', '["First Amendment", "free speech", "constitutional rights"]', '[{"name": "Supreme Court - Garcetti v. Ceballos Decision", "url": "https://supreme.justia.com/cases/federal/us/547/410/"}, {"name": "Congressional Research Service - First Amendment and Government Employees", "url": "https://crsreports.congress.gov/product/pdf/R/R45043"}, {"name": "Federal Circuit Court Analysis - Whistleblower Speech", "url": "https://www.cafc.uscourts.gov/opinions-orders"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 4, 'multiple_choice', 'Government', 'What is the role of Inspectors General in whistleblower protection?', 'They prosecute retaliation cases in federal court', 'They investigate disclosures and protect whistleblower identities', 'They determine financial compensation for whistleblowers', 'They oversee the Office of Special Counsel', 'They investigate disclosures and protect whistleblower identities', 'IGs are the internal watchdogs who receive and investigate reports.', 'Inspectors General serve as independent watchdogs within federal agencies who receive whistleblower disclosures, investigate wrongdoing, and protect the identities of those who report misconduct. They play a crucial role in maintaining government accountability.', '["Inspectors General", "government accountability", "whistleblower protection"]', '[{"name": "Council of Inspectors General on Integrity and Efficiency", "url": "https://www.ignet.gov/"}, {"name": "Inspector General Act of 1978", "url": "https://www.govinfo.gov/content/pkg/COMPS-1684/pdf/COMPS-1684.pdf"}, {"name": "Government Accountability Office - IG Effectiveness Study", "url": "https://www.gao.gov/products/gao-19-282"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 5, 'multiple_choice', 'Civil Rights', 'Which Supreme Court case significantly limited federal employee speech rights?', 'Pickering v. Board of Education', 'Garcetti v. Ceballos', 'Connick v. Myers', 'Rankin v. McPherson', 'Garcetti v. Ceballos', 'This 2006 case involved a prosecutor and official duties.', 'Garcetti v. Ceballos (2006) ruled that when public employees speak pursuant to their official duties, they are not protected by the First Amendment. This decision significantly limited federal employee speech rights and made whistleblower protection laws more crucial.', '["Supreme Court", "Garcetti v. Ceballos", "employee speech rights"]', '[{"name": "Supreme Court - Garcetti v. Ceballos Full Decision", "url": "https://supreme.justia.com/cases/federal/us/547/410/"}, {"name": "Harvard Law Review - Impact of Garcetti Decision", "url": "https://harvardlawreview.org/2007/05/the-decline-of-the-pickering-framework/"}, {"name": "American Bar Association - Public Employee Speech After Garcetti", "url": "https://www.americanbar.org/groups/state_local_government/publications/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 6, 'multiple_choice', 'Government', 'What protection does the Whistleblower Protection Enhancement Act of 2012 provide?', 'Financial rewards for successful disclosures', 'Protection for disclosures to supervisors and colleagues', 'Automatic job promotion for whistleblowers', 'Immunity from all civil lawsuits', 'Protection for disclosures to supervisors and colleagues', 'The 2012 enhancement expanded who you can safely report to.', 'The Whistleblower Protection Enhancement Act of 2012 expanded protections to cover disclosures made to supervisors, colleagues, and others within the chain of command, not just to designated agencies. This closed a significant gap in protection.', '["Whistleblower Protection Enhancement Act", "2012 law", "disclosure protection"]', '[{"name": "Congress.gov - Whistleblower Protection Enhancement Act", "url": "https://www.congress.gov/bill/112th-congress/senate-bill/743"}, {"name": "Office of Special Counsel - 2012 Enhancements Explained", "url": "https://osc.gov/Services/Pages/WhistleblowerProtectionEnhancementAct.aspx"}, {"name": "Government Executive - Impact of 2012 Changes", "url": "https://www.govexec.com/management/2013/01/whistleblower-protections-enhanced/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 7, 'multiple_choice', 'Constitutional Law', 'What standard must be met for a disclosure to receive whistleblower protection?', 'Beyond a reasonable doubt of wrongdoing', 'Reasonable belief of wrongdoing', 'Absolute proof of criminal activity', 'Administrative violation with financial impact', 'Reasonable belief of wrongdoing', 'The standard is about what the employee reasonably believes, not absolute proof.', 'Federal whistleblower protection requires only that the employee have a "reasonable belief" that the disclosed information evidences wrongdoing. This protects employees who report suspected violations even if investigations don\'t prove criminal activity.', '["reasonable belief", "disclosure standards", "whistleblower protection"]', '[{"name": "Office of Special Counsel - Standards for Protection", "url": "https://osc.gov/Services/Pages/WPA-Whistleblowers.aspx"}, {"name": "Federal Circuit Court - Reasonable Belief Standard", "url": "https://www.cafc.uscourts.gov/opinions-orders"}, {"name": "Merit Systems Protection Board - Whistleblower Cases", "url": "https://www.mspb.gov/studies/studies.htm"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 8, 'multiple_choice', 'Government', 'Which of these actions constitutes prohibited retaliation against whistleblowers?', 'Transferring to a different office location', 'Denying previously promised promotions', 'Requiring additional training courses', 'All of the above', 'All of the above', 'Retaliation can take many forms beyond just firing someone.', 'Prohibited retaliation includes any personnel action that might deter a reasonable employee from making a disclosure, including transfers, denial of promotions, increased scrutiny, or changed working conditions. The law protects against subtle retaliation, not just termination.', '["prohibited retaliation", "personnel actions", "whistleblower protection"]', '[{"name": "Office of Special Counsel - Types of Retaliation", "url": "https://osc.gov/Services/Pages/Retaliation.aspx"}, {"name": "Merit Systems Protection Board - Retaliation Cases", "url": "https://www.mspb.gov/appeals/whistleblower.htm"}, {"name": "Government Accountability Office - Retaliation Study", "url": "https://www.gao.gov/products/gao-21-322"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 9, 'multiple_choice', 'Civil Rights', 'What remedy can the Office of Special Counsel seek for retaliation victims?', 'Criminal prosecution of supervisors', 'Monetary damages only', 'Reinstatement and corrective action', 'Transfer to another agency', 'Reinstatement and corrective action', 'OSC seeks to make the whistleblower whole and correct the situation.', 'The Office of Special Counsel can seek corrective action including reinstatement, back pay, restoration of benefits, and other measures to make the whistleblower whole. OSC can also recommend disciplinary action against retaliating officials.', '["corrective action", "remedies", "Office of Special Counsel"]', '[{"name": "Office of Special Counsel - Corrective Actions", "url": "https://osc.gov/Services/Pages/CorrectiveAction.aspx"}, {"name": "OSC Case Results Database", "url": "https://osc.gov/Resources/Pages/Case-Results.aspx"}, {"name": "Federal Register - OSC Corrective Action Authority", "url": "https://www.federalregister.gov/agencies/special-counsel-office"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 10, 'multiple_choice', 'Government', 'How long do federal employees have to file a whistleblower retaliation complaint with OSC?', '30 days after the retaliation', '60 days after the retaliation', '90 days after learning of retaliation', '1 year after the retaliation', '60 days after the retaliation', 'Think about typical federal administrative deadlines.', 'Federal employees must file whistleblower retaliation complaints with the Office of Special Counsel within 60 days of learning about the alleged retaliation. This relatively short deadline makes it important for employees to act quickly when they suspect retaliation.', '["filing deadlines", "OSC complaints", "procedural requirements"]', '[{"name": "Office of Special Counsel - Filing Requirements", "url": "https://osc.gov/Services/Pages/Filing-Complaint.aspx"}, {"name": "Code of Federal Regulations - OSC Procedures", "url": "https://www.ecfr.gov/current/title-5/chapter-VIII"}, {"name": "Federal Employee Legal Resources", "url": "https://www.fedsmith.com/whistleblower-protection/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 11, 'multiple_choice', 'Constitutional Law', 'What is the "public concern" requirement for protected speech?', 'Speech must involve criminal activity', 'Speech must relate to matters of public interest', 'Speech must be reported to media outlets', 'Speech must involve national security', 'Speech must relate to matters of public interest', 'Protected speech must matter to the broader public, not just personal grievances.', 'Under the Pickering-Connick framework, employee speech is protected only when it addresses matters of "public concern" - issues that the community has a legitimate interest in, rather than purely personal employment grievances. This balances employee rights with government efficiency.', '["public concern", "Pickering test", "protected speech"]', '[{"name": "Supreme Court - Pickering v. Board of Education", "url": "https://supreme.justia.com/cases/federal/us/391/563/"}, {"name": "Legal Information Institute - Public Concern Test", "url": "https://www.law.cornell.edu/wex/pickering_balancing_test"}, {"name": "Federal Circuit Analysis - Public Concern Doctrine", "url": "https://www.cafc.uscourts.gov/opinions-orders"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 12, 'multiple_choice', 'Government', 'Which agency investigates retaliation complaints in the intelligence community?', 'Office of Special Counsel', 'Intelligence Community Inspector General', 'CIA Inspector General', 'Department of Justice', 'Intelligence Community Inspector General', 'Intelligence agencies have their own specialized IG system.', 'The Intelligence Community Inspector General (ICIG) handles whistleblower complaints and retaliation cases specific to intelligence agencies. This specialized system recognizes the unique classification and national security considerations in intelligence work.', '["Intelligence Community Inspector General", "intelligence whistleblowers", "national security"]', '[{"name": "Intelligence Community Inspector General Website", "url": "https://www.icig.gov/"}, {"name": "Intelligence Community Whistleblower Protection Act", "url": "https://www.congress.gov/bill/105th-congress/house-bill/3829"}, {"name": "ICIG Annual Reports", "url": "https://www.icig.gov/index.php/reports"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 13, 'multiple_choice', 'Civil Rights', 'What protection exists for federal contractors who report wrongdoing?', 'Same protection as federal employees', 'No legal protection exists', 'Limited protection under specific statutes', 'Protection only for defense contractors', 'Limited protection under specific statutes', 'Contractors have some protection but it\'s more limited than employee protection.', 'Federal contractors have limited whistleblower protection under specific statutes like the False Claims Act and various procurement fraud laws, but they lack the comprehensive protection provided to federal employees under the Whistleblower Protection Act.', '["contractor protection", "False Claims Act", "procurement fraud"]', '[{"name": "Government Accountability Office - Contractor Whistleblower Study", "url": "https://www.gao.gov/products/gao-09-106"}, {"name": "False Claims Act Overview", "url": "https://www.justice.gov/civil/false-claims-act"}, {"name": "Federal Acquisition Regulation - Contractor Requirements", "url": "https://www.acquisition.gov/far/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 14, 'multiple_choice', 'Government', 'What role do agency ethics officials play in whistleblower protection?', 'They prosecute retaliation cases', 'They provide confidential advice and receive reports', 'They determine financial compensation', 'They supervise the Office of Special Counsel', 'They provide confidential advice and receive reports', 'Ethics officials are the first line of internal reporting and guidance.', 'Agency ethics officials provide confidential advice to employees about potential violations and can receive initial reports of wrongdoing. They serve as an internal resource before employees need to make formal disclosures to outside agencies.', '["ethics officials", "confidential advice", "internal reporting"]', '[{"name": "Office of Government Ethics - Ethics Official Duties", "url": "https://www.oge.gov/"}, {"name": "Code of Federal Regulations - Ethics Officials", "url": "https://www.ecfr.gov/current/title-5/chapter-XVI"}, {"name": "Government Ethics Training Materials", "url": "https://www.oge.gov/web/oge.nsf/Resources/Training+Resources"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 15, 'multiple_choice', 'Constitutional Law', 'How does executive privilege interact with whistleblower disclosures?', 'Executive privilege always overrides disclosure rights', 'Disclosure rights always override executive privilege', 'Courts balance competing interests case by case', 'Congress determines which takes precedence', 'Courts balance competing interests case by case', 'This involves complex constitutional balancing by the judicial branch.', 'Courts must balance executive privilege claims against the public interest in disclosure and congressional oversight needs. Neither privilege is absolute, and courts examine the specific circumstances to determine which interest prevails in each case.', '["executive privilege", "disclosure rights", "constitutional balancing"]', '[{"name": "Congressional Research Service - Executive Privilege Overview", "url": "https://crsreports.congress.gov/product/pdf/R/R42670"}, {"name": "Supreme Court - United States v. Nixon", "url": "https://supreme.justia.com/cases/federal/us/418/683/"}, {"name": "Federal Courts - Executive Privilege Cases", "url": "https://www.fjc.gov/history/landmark-judicial-legislation"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 16, 'multiple_choice', 'Government', 'What is a "prohibited personnel practice" under federal law?', 'Any negative job action against an employee', 'Personnel actions that violate merit system principles', 'Transfers between different agencies', 'Required training for professional development', 'Personnel actions that violate merit system principles', 'These practices violate the merit-based system of federal employment.', 'Prohibited personnel practices are actions that violate the merit system principles governing federal employment, including retaliation for whistleblowing, political coercion, nepotism, and discrimination. These practices undermine the professional civil service system.', '["prohibited personnel practices", "merit system principles", "civil service"]', '[{"name": "Merit Systems Protection Board - Prohibited Practices", "url": "https://www.mspb.gov/appeals/guide_to_appeals/prohibited_personnel_practices.htm"}, {"name": "Office of Special Counsel - Prohibited Practices", "url": "https://osc.gov/Services/Pages/Prohibited-Personnel-Practices.aspx"}, {"name": "Title 5 USC - Merit System Principles", "url": "https://www.govinfo.gov/content/pkg/USCODE-2011-title5/pdf/USCODE-2011-title5-partI-chap12.pdf"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 17, 'multiple_choice', 'Civil Rights', 'Which historical case established the balancing test for public employee speech?', 'New York Times v. Sullivan', 'Pickering v. Board of Education', 'Brandenburg v. Ohio', 'Tinker v. Des Moines', 'Pickering v. Board of Education', 'This 1968 case involved a teacher\'s letter to a newspaper.', 'Pickering v. Board of Education (1968) established the framework for balancing public employees\' First Amendment rights against the government\'s interest in efficient operation. This "Pickering balancing test" remains the foundation for analyzing public employee speech rights.', '["Pickering v. Board of Education", "balancing test", "public employee speech"]', '[{"name": "Supreme Court - Pickering v. Board of Education Decision", "url": "https://supreme.justia.com/cases/federal/us/391/563/"}, {"name": "Constitutional Law Scholar Analysis", "url": "https://www.law.cornell.edu/supremecourt/text/391/563"}, {"name": "First Amendment Law Review - Pickering Doctrine", "url": "https://scholarship.law.wm.edu/wmborj/"}]'),

-- True/False Questions (5 questions)
('2025-01-25-whistleblower-retaliation-concerns', 18, 'true_false', 'Government', 'Federal employees can only make protected disclosures to designated oversight agencies like OSC or GAO.', '', '', '', '', 'False', 'Remember the 2012 enhancement expanded disclosure options significantly.', 'False. The Whistleblower Protection Enhancement Act of 2012 expanded protection to include disclosures made to supervisors, colleagues, and others in the chain of command. Employees can make protected disclosures internally within their agencies, not just to external oversight bodies.', '["disclosure channels", "2012 enhancement", "internal reporting"]', '[{"name": "Office of Special Counsel - 2012 Enhancement Explained", "url": "https://osc.gov/Services/Pages/WhistleblowerProtectionEnhancementAct.aspx"}, {"name": "Federal Employee Rights Guide", "url": "https://www.fedsmith.com/whistleblower-protection/"}, {"name": "Government Executive - Disclosure Channels", "url": "https://www.govexec.com/management/2013/whistleblower-guide/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 19, 'true_false', 'Constitutional Law', 'The First Amendment provides absolute protection for all government employee speech about workplace issues.', '', '', '', '', 'False', 'Think about the Garcetti decision and limits on official duty speech.', 'False. The Supreme Court\'s decision in Garcetti v. Ceballos (2006) significantly limited First Amendment protection for government employee speech made pursuant to official duties. Additionally, the Pickering balancing test weighs employee speech rights against government operational needs.', '["First Amendment limits", "Garcetti v. Ceballos", "official duty speech"]', '[{"name": "Supreme Court - Garcetti v. Ceballos", "url": "https://supreme.justia.com/cases/federal/us/547/410/"}, {"name": "Constitutional Law Analysis - Employee Speech", "url": "https://www.law.cornell.edu/wex/government_speech"}, {"name": "Federal Bar Association - Post-Garcetti Analysis", "url": "https://www.fedbar.org/government-relations/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 20, 'true_false', 'Government', 'Inspectors General are appointed by and report directly to agency heads.', '', '', '', '', 'False', 'Consider the independence requirements for IGs in their oversight role.', 'False. While some IGs are appointed by agency heads, many statutory IGs are appointed by the President and confirmed by the Senate to ensure independence. IGs have dual reporting relationships - to both agency heads and Congress - to maintain oversight independence.', '["Inspector General independence", "dual reporting", "oversight structure"]', '[{"name": "Inspector General Act of 1978", "url": "https://www.govinfo.gov/content/pkg/COMPS-1684/pdf/COMPS-1684.pdf"}, {"name": "Council of Inspectors General", "url": "https://www.ignet.gov/"}, {"name": "Government Accountability Office - IG Independence", "url": "https://www.gao.gov/products/gao-19-282"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 21, 'true_false', 'Civil Rights', 'Whistleblower protection laws only apply to disclosures about criminal violations.', '', '', '', '', 'False', 'Protection covers a broader range of wrongdoing than just criminal acts.', 'False. Whistleblower protection covers disclosures about any violation of law, rule, or regulation, as well as gross mismanagement, gross waste of funds, abuse of authority, or substantial and specific danger to public health or safety. Criminal violations are just one category of protected disclosures.', '["scope of protection", "types of wrongdoing", "disclosure categories"]', '[{"name": "Whistleblower Protection Act Text", "url": "https://www.govinfo.gov/content/pkg/COMPS-1684/pdf/COMPS-1684.pdf"}, {"name": "Office of Special Counsel - Protected Disclosures", "url": "https://osc.gov/Services/Pages/WPA-Whistleblowers.aspx"}, {"name": "Congressional Research Service - Disclosure Categories", "url": "https://crsreports.congress.gov/product/pdf/R/R44944"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 22, 'true_false', 'Government', 'The Merit Systems Protection Board has exclusive jurisdiction over all federal employee appeals.', '', '', '', '', 'False', 'Consider that different agencies and issues may have different appeal routes.', 'False. While MSPB has jurisdiction over many federal employee appeals, other agencies like the Equal Employment Opportunity Commission handle discrimination claims, and the Office of Special Counsel handles whistleblower retaliation cases. Some employees may have multiple appeal options.', '["MSPB jurisdiction", "appeal routes", "agency authority"]', '[{"name": "Merit Systems Protection Board - Jurisdiction Guide", "url": "https://www.mspb.gov/appeals/guide_to_appeals/"}, {"name": "Federal Employee Appeals Process", "url": "https://www.fedsmith.com/appeals-process/"}, {"name": "Office of Personnel Management - Employee Rights", "url": "https://www.opm.gov/policy-data-oversight/employee-relations/"}]'),

-- Short Answer Questions (3 questions)
('2025-01-25-whistleblower-retaliation-concerns', 23, 'short_answer', 'Constitutional Law', 'Name the Supreme Court case that significantly limited federal employee speech rights when they speak pursuant to their official duties.', '', '', '', '', 'Garcetti v. Ceballos', 'This 2006 Supreme Court decision involved a prosecutor and official duties.', 'Garcetti v. Ceballos (2006) held that when public employees make statements pursuant to their official duties, they are not speaking as citizens for First Amendment purposes and therefore lack constitutional protection. This decision significantly narrowed the scope of protected employee speech.', '["Supreme Court", "Garcetti v. Ceballos", "official duties speech"]', '[{"name": "Supreme Court - Garcetti v. Ceballos Decision", "url": "https://supreme.justia.com/cases/federal/us/547/410/"}, {"name": "Justia Case Summary", "url": "https://supreme.justia.com/cases/federal/us/547/410/case.html"}, {"name": "Congressional Research Service - Impact Analysis", "url": "https://crsreports.congress.gov/product/pdf/R/R45043"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 24, 'short_answer', 'Government', 'What is the time limit for filing a whistleblower retaliation complaint with the Office of Special Counsel?', '', '', '', '', '60 days', 'Federal administrative deadlines are often measured in days, not months.', 'Federal employees must file whistleblower retaliation complaints with OSC within 60 days of learning about the alleged retaliatory personnel action. This deadline is strictly enforced and makes prompt action essential for maintaining legal protection.', '["filing deadline", "60 days", "OSC procedures"]', '[{"name": "Office of Special Counsel - Filing Requirements", "url": "https://osc.gov/Services/Pages/Filing-Complaint.aspx"}, {"name": "Code of Federal Regulations - OSC Procedures", "url": "https://www.ecfr.gov/current/title-5/chapter-VIII/part-1820"}, {"name": "Federal Employee Legal Guide", "url": "https://www.fedsmith.com/whistleblower-protection/"}]'),

('2025-01-25-whistleblower-retaliation-concerns', 25, 'short_answer', 'Civil Rights', 'What federal law was enhanced in 2012 to expand whistleblower protections to include disclosures to supervisors and colleagues?', '', '', '', '', 'Whistleblower Protection Enhancement Act', 'Think about the law that "enhanced" existing protections in 2012.', 'The Whistleblower Protection Enhancement Act of 2012 significantly expanded the original Whistleblower Protection Act by extending protection to disclosures made to supervisors, colleagues, and others within the normal chain of command, closing a major gap in coverage that had left many employees vulnerable to retaliation.', '["Whistleblower Protection Enhancement Act", "2012", "expanded coverage"]', '[{"name": "Congress.gov - S.743 Whistleblower Protection Enhancement Act", "url": "https://www.congress.gov/bill/112th-congress/senate-bill/743"}, {"name": "Office of Special Counsel - Enhancement Act Overview", "url": "https://osc.gov/Services/Pages/WhistleblowerProtectionEnhancementAct.aspx"}, {"name": "Government Executive - 2012 Changes Explained", "url": "https://www.govexec.com/management/2013/01/whistleblower-protections-enhanced/"}]');